import { DatePipe } from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';

@Component({
  selector: 'app-roles-edit',
  templateUrl: './roles-edit.component.html',
  styleUrls: ['./roles-edit.component.scss']
})
export class RolesEditComponent implements OnInit {

  FormularioData:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  dataform:any='';
  role_edit:any=[];
  rol: any = [];
  role_edit_codigo: any;
  userConn:any=[];

  public ventana="rol-edit"
  public detalle="rol-detalle";
  public tipo="transaccion-rol-PUT";

  constructor(private _formBuilder: FormBuilder,  private datePipe: DatePipe,  private spinner: NgxSpinnerService,
    private api:ApiService, public dialogRef: MatDialogRef<RolesEditComponent>, public _snackBar: MatSnackBar,
    @Inject(MAT_DIALOG_DATA) public dataRolEdit: any, public log_module:LogService){

    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

     this.role_edit_codigo = this.dataRolEdit.dataRolEdit.codigo;

  }

  ngOnInit(): void {
    this.FormularioData = this.createForm();

    this.role_edit = this.dataRolEdit.dataRolEdit;
  }

  createForm(): FormGroup {
    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      codigo: [this.role_edit_codigo],
      descripcion: [this.dataform.descripcion, Validators.compose([Validators.required])],
      contabilizar: [this.dataform.contabilizar],
      contabiliza: [true],
      dias_cambio: [this.dataform.dias_cambio],
      long_minima: [this.dataform.long_minima],
      con_letras: [this.dataform.con_letras],
      con_numeros: [this.dataform.con_numeros],

      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      horareg: [hora_actual_complete],
      usuarioreg: [this.userConn],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:--  /seg_adm/mant/serol/";
    return this.api.update("/seg_adm/mant/serol/"+this.userConn+"/"+this.role_edit.codigo, data)
      .subscribe({
        next: (datav) => {
          this.rol = datav;
          //  console.log('data', datav);

          this.onNoClick();
          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this._snackBar.open('Se ha guardado correctamente!', 'Ok', {
            duration: 3000,
            panelClass: ['coorporativo-snackbar', 'login-snackbar'],
          });

          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 2000);
          
          location.reload();
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

}
<div class="p-1">
  <h1 mat-dialog-title>Editar Rol de : <strong>{{ role_edit.codigo }}</strong></h1>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div>
</div>

<div mat-dialog-content>
  <div class="container">
    <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">
      <div class="row">
        <div class="col">
          <mat-label style="padding-right: 7px;"><u>Descripción</u> </mat-label>
          <input matInput type="text" [(ngModel)]="role_edit.descripcion" formControlName="descripcion" required>
        </div>
      </div>

      <div class="row">
        <div class="col">
          <section class="example-section" style="padding-left: 82px;">
            <input type="checkbox" [(ngModel)]="role_edit.contabilizar" formControlName="contabilizar">
            <label><u>Puede Contabilizar Documentos</u></label>
          </section>
        </div>
      </div>

      <div style="border-style: groove;padding: 0px 0px 0px 0px;">
        <div class="row">
          <div class="col">
            <mat-label style="padding-right: 17px;"><u>Cambiar cada</u> </mat-label>
            <input matInput type="number" [(ngModel)]="role_edit.dias_cambio" formControlName="dias_cambio" required>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-label style="padding-right: 1px;"><u>Longitud Minima</u></mat-label>
            <select matNativeControl formControlName="long_minima" [(ngModel)]="role_edit.long_minima">
              <option value=0>0</option>
              <option value=1>1</option>
              <option value=2>2</option>
              <option value=3>3</option>
              <option value=4>4</option>
              <option value=5>5</option>
              <option value=6>6</option>
              <option value=7>7</option>
              <option value=8>8</option>
              <option value=9>9</option>
              <option value=10>10</option>
            </select>
          </div>
        </div><br>

        <div class="row">
          <div class="col">
            <section class="example-section" style="padding-left: 110px;">
              <input type="checkbox" [(ngModel)]="role_edit.con_letras" formControlName="con_letras" value="true">
              <label><u>Debe incluir al menos una Letra</u> </label>
            </section>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <section class="example-section" style="padding-left: 110px;">
              <input type="checkbox" [(ngModel)]="role_edit.con_numeros" formControlName="con_numeros" value="true">
              <label><u>Debe incluir al menos un Numero</u> </label>
            </section>
          </div>
        </div>
      </div>

      <div class="mat-dialog-actions" mat-dialog-actions>
        <a mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</a>
        <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit">
          <i class="fa fa-save"></i> Guardar</button>
      </div>
    </form>
  </div>
</div>
