import { Component, ElementRef, ViewChild } from '@angular/core';
import { MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';
import { Moneda } from '@services/modelos/objetos';
import { MonedaCreateComponent } from './moneda-create/moneda-create.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { NgxSpinnerService } from 'ngx-spinner';
import { MatPaginator } from '@angular/material/paginator';
import { Observable, map, startWith } from 'rxjs';
import { FormControl } from '@angular/forms';
import { ToastrService } from 'ngx-toastr';
import { LogService } from '@services/log-service.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';

@Component({
  selector: 'app-moneda',
  templateUrl: './moneda.component.html',
  styleUrls: ['./moneda.component.scss']
})
export class MonedaComponent {

  nombre_ventana:string="abmadmoneda.vb";
  moneda: any = [];
  userConn: string;

  displayedColumns = ['codigo','descripcion','horareg','fechareg','usuarioreg','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild("dialogD") dialogD: ElementRef;
  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  myControl = new FormControl<string | Moneda>('');
  options: Moneda[] = [];
  filteredOptions: Observable<Moneda[]>;
  
  public ventana="Moneda"
	public detalle="moneda-detalle";
	public tipo="transaccion-moneda-DELETE";

  constructor(private api: ApiService, public dialog: MatDialog, private spinner: NgxSpinnerService, private toastr: ToastrService,
    public log_module: LogService, public nombre_ventana_service: NombreVentanaService) {
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    
    this.mandarNombre();
    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }

  ngOnInit(){
    this.getAllmoneda();

    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map(value => {
        const name = typeof value === 'string' ? value : value?.codigo;
        return name ? this._filter(name as string) : this.options.slice();
      }),
    );
  }

  private _filter(name: string): Moneda[] {
    const filterValue = name.toLowerCase();

    return this.options.filter(option => option.codigo.toLowerCase().includes(filterValue));
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
    console.log(this.dataSource.filter);
  }

  displayFn(user: Moneda): string {
    return user && user.codigo ? user.codigo : '';
  }

  getAllmoneda(){
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/admoneda/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.moneda = datav;    
          
          this.dataSource = new MatTableDataSource(this.moneda);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  openDialog(): void {
    this.dialog.open(MonedaCreateComponent, {
      width: 'auto',
      height:'auto',
    });
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion"+"Ruta:--seg_adm/mant/admoneda/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: '350px',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete("/seg_adm/mant/admoneda/"+this.userConn+"/"+element.codigo)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
            
            this.toastr.success('!SE ELIMINO EXITOSAMENTE!');
            setTimeout(() => {
              this.spinner.hide();
            }, 1500);

            location.reload();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! NO SE ELIMINO !');
      }
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<section class="content">
  <div class="container-fluid">
    <div class="overlay"></div>
    <div class="mb-3 mat-elevation-z8">
      <div class="row">
        <div class="col-lg headTittle">
          <!-- <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Buscador Moneda</mat-label>
            <input type="text" matInput [formControl]="myControl" placeholder="Codigo Moneda" (keyup)="applyFilter($event)">
          </mat-form-field> -->

          <button mat-button color="primary" (click)="openDialog()" style="float: left;">
            <i class="fa fa-plus"></i> Crear Moneda</button>
        </div>

        <div class="col-md text-center headTittle">
          <h3>Monedas</h3>
        </div>

        <div class="col-sm">
          <div class="col-md text-right headTittle">
            <mat-icon color="primary">arrow_drop_down</mat-icon>
            <mat-icon color="primary">close</mat-icon>
          </div>
        </div>
      </div>
    </div>

    <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
      <br>
      <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
    </ngx-spinner>

    <div class="row">
      <div class="col-lg">
        <div class="team p-2 bg-white scrollmenu">
          <div class="col-lg-12 pr-lg-4">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
              <ng-container matColumnDef="codigo">
                <th mat-header-cell *matHeaderCellDef> Codigo </th>
                <td mat-cell *matCellDef="let element"> {{ element.codigo }} </td>
              </ng-container>

              <ng-container matColumnDef="codmoneda_sin">
                <th mat-header-cell *matHeaderCellDef> Cod Moneda_sin</th>
                <td mat-cell *matCellDef="let element"> {{ element.codmoneda_sin }} </td>
              </ng-container>

              <ng-container matColumnDef="decimales">
                <th mat-header-cell *matHeaderCellDef> Decimales</th>
                <td mat-cell *matCellDef="let element"> {{ element.decimales }} </td>
              </ng-container>

              <ng-container matColumnDef="descripcion">
                <th mat-header-cell *matHeaderCellDef> Descripcion </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.descripcion }} </td>
              </ng-container>

              <ng-container matColumnDef="fechareg">
                <th mat-header-cell *matHeaderCellDef> Fecha Reg </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">
                  {{ element.fechareg | date:'yyyy/MM/dd'}} </td>
              </ng-container>

              <ng-container matColumnDef="horareg">
                <th mat-header-cell *matHeaderCellDef> Hora Reg </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.horareg }} </td>
              </ng-container>

              <ng-container matColumnDef="usuarioreg">
                <th mat-header-cell *matHeaderCellDef> Usuario Reg </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.usuarioreg }} </td>
              </ng-container>

              <ng-container matColumnDef="accion">
                <th mat-header-cell *matHeaderCellDef>Acción</th>
                <td mat-cell *matCellDef="let element">
                  <button mat-icon-button1 [matMenuTriggerFor]="menu" style="color: #093070;">
                    <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                  </button>
                  <mat-menu #menu="matMenu">
                    <button mat-menu-item (click)="eliminar(element);">
                      <span><i class="fa fa-trash"></i> Eliminar</span>
                    </button>
                  </mat-menu>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table><br>
            <mat-paginator #paginator [pageSizeOptions]="[5, 10, 15]" showFirstLastButtons></mat-paginator>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
