import { Component, OnInit, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { LineaproductoCreateComponent } from './lineaproducto-create/lineaproducto-create.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { FormControl } from '@angular/forms';
import { inLineaProducto } from '@services/modelos/objetos';
import { Observable } from 'rxjs';
import { LogService } from '@services/log-service.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
import { LineaProductoCatalogoComponent } from './linea-producto-catalogo/linea-producto-catalogo.component';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-lineaproducto',
  templateUrl: './lineaproducto.component.html',
  styleUrls: ['./lineaproducto.component.scss']
})
export class LineaproductoComponent implements OnInit {

  public ingrupo=[];
  public data_edit=[];
  displayedColumns = ['codigo','descripcion','codgrupomer','codgrupomer','codgrupo','codsubgrupo_vta','descdetallada',
                      'porcentaje_comis','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();
  userConn:any;

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  myControl = new FormControl<string | inLineaProducto>('');
  options: inLineaProducto[] = [];
  filteredOptions: Observable<inLineaProducto[]>;

  nombre_ventana:string="abminlinea.vb";
  public ventana="Linea Producto"
	public detalle="lineaProd-detalle";
  public tipo = "transaccion-lineaProd-DELETE";
  
  constructor(private api:ApiService, public dialog: MatDialog, private spinner: NgxSpinnerService, 
    public _snackBar: MatSnackBar, public log_module: LogService,private toastr: ToastrService, 
    public nombre_ventana_service: NombreVentanaService) {
      this.mandarNombre();
      let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
      this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

      this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }

  ngOnInit() {
    this.getAlllineaProducto();
  }

  private _filter(name: string): inLineaProducto[] {
    const filterValue = name.toLowerCase();

    return this.options.filter(option => option.codigo.toLowerCase().includes(filterValue));
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
    console.log(this.dataSource.filter);
  }
  
  displayFn(user: inLineaProducto): string {
    return user && user.codigo ? user.codigo : '';
  }

  getAlllineaProducto(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/inventario/mant/inlinea/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.ingrupo = datav;
          console.log('inlinea', datav);

          this.dataSource = new MatTableDataSource(this.ingrupo);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;

          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  openDialog(enterAnimationDuration: string, exitAnimationDuration: string): void {
    this.dialog.open(LineaproductoCreateComponent, {
      width: '950px',
      enterAnimationDuration,
      exitAnimationDuration,
    });
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:-- /inventario/mant/inlinea Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: '350px',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/inventario/mant/inlinea/'+this.userConn+"/"+element.codigo)
        .subscribe({
          next: ()=>{
            this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
            this._snackBar.open('Se elimino correctamente!', 'Ok', {
              duration: 3000,
            });
            this.toastr.success('Eliminado con Exito! ðŸŽ‰'); 
            this.spinner.show();
            setTimeout(() => {
              this.spinner.hide();
            }, 1500);

            location.reload();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        alert("Â¡No se elimino!");
      }
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }

  catalogoLineas() { 
    this.dialog.open(LineaProductoCatalogoComponent, {
      width: 'auto',
      height: 'auto',
    });
  }
}
<div class="p-2">
  <div class="overlay"></div>
  <div class="p-2 mb-1 mat-elevation-z8">
    <div class="row">
      <div class="col-lg">
        <div class="container">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Buscador Producto</mat-label>
            <input type="text" matInput [formControl]="myControl" placeholder="Codigo" (keyup)="applyFilter($event)"
              #input>
          </mat-form-field>
        </div>
      </div>

      <div class="col-md text-center">
        <h3>Linea de Productos </h3>
      </div>

      <div class="col-sm">
        <button mat-button color="primary" (click)="catalogoLineas()" style="float: right;">
          <i class="fa fa-book" aria-hidden="true"></i> Catalogo de Lineas</button>

        <button mat-button color="primary" (click)="openDialog('900ms', '900ms')" style="float: right;">
          <i class="fa fa-plus"></i> Crear Linea de Producto</button>
      </div>
    </div>
  </div>

  <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
    <br>
    <p style="font-size: 25px; color: rgb(9,48,112,)">ðŸ”© Cargando... ðŸ”©</p>
  </ngx-spinner>

  <div class="row">
    <div class="col-lg">
      <div class="team p-2 bg-white scrollmenu">
        <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
          <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
            <ng-container matColumnDef="codgrupo">
              <th mat-header-cell *matHeaderCellDef> Codigo Grupo </th>
              <td mat-cell *matCellDef="let element"> {{ element.codgrupo }} </td>
            </ng-container>

            <ng-container matColumnDef="descripcion">
              <th mat-header-cell *matHeaderCellDef> DescripciÃ³n</th>
              <td mat-cell *matCellDef="let element"> {{ element.descripcion }} </td>
            </ng-container>

            <ng-container matColumnDef="codigo">
              <th mat-header-cell *matHeaderCellDef> CÃ³digo </th>
              <td mat-cell *matCellDef="let element"> {{ element.codigo }} </td>
            </ng-container>

            <ng-container matColumnDef="codgrupomer">
              <th mat-header-cell *matHeaderCellDef> Codigo Grupo Mercaderia </th>
              <td mat-cell *matCellDef="let element">Grupo {{ element.codgrupomer }} </td>
            </ng-container>

            <ng-container matColumnDef="codsubgrupo_vta">
              <th mat-header-cell *matHeaderCellDef> Cod. Sub-Grupo Venta </th>
              <td mat-cell *matCellDef="let element"> {{ element.codsubgrupo_vta }} </td>
            </ng-container>

            <ng-container matColumnDef="descdetallada">
              <th mat-header-cell *matHeaderCellDef> Descuento Detallado</th>
              <td mat-cell *matCellDef="let element"> {{ element.descdetallada }} </td>
            </ng-container>

            <ng-container matColumnDef="porcentaje_comis">
              <th mat-header-cell *matHeaderCellDef> Porcentaje Comisiones </th>
              <td mat-cell *matCellDef="let element"> {{ element.porcentaje_comis }} </td>
            </ng-container>

            <ng-container matColumnDef="horareg">
              <th mat-header-cell *matHeaderCellDef> Registro Hora </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;"> {{ element.horareg }} </td>
            </ng-container>

            <!-- <ng-container matColumnDef="fechareg">
              <th mat-header-cell *matHeaderCellDef> Registro Fecha </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">
                {{ element.fechareg | date:'yyyy/MM/dd' }} </td>
            </ng-container> -->

            <!-- <ng-container matColumnDef="usuarioreg">
              <th mat-header-cell *matHeaderCellDef> Registro Usuario </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;"> {{ element.usuarioreg }} </td>
            </ng-container> -->
            <ng-container matColumnDef="accion">
              <th mat-header-cell *matHeaderCellDef>AcciÃ³n</th>
              <td mat-cell *matCellDef="let element">
                <button mat-icon-button1 [matMenuTriggerFor]="menu" style="color: #093070;">
                  <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                </button>
                <mat-menu #menu="matMenu">
                  <!-- <button mat-menu-item (click)="editar(element);">
                    <span><i class="fa fa-pen"></i> Editar</span>
                  </button> -->
                  <button mat-menu-item (click)="eliminar(element);">
                    <span><i class="fa fa-trash"></i> Eliminar</span>
                  </button>
                </mat-menu>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table>

          <mat-paginator #paginator [pageSizeOptions]="[10, 15, 25]" showFirstLastButtons></mat-paginator>
        </div>
      </div>
    </div>
  </div>
</div>
