import { Component, OnInit, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ItemCreateComponent } from './item-create/item-create.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { FormControl } from '@angular/forms';
import { Item } from '@services/modelos/objetos';
import { Observable, map, startWith } from 'rxjs';
import { ModalSaldoCubrirComponent } from './modal-saldoCubrir/modal-saldoCubrir.component';
import { ModalComponenteskitComponent } from './modal-componenteskit/modal-componenteskit.component';
import { ModalMaximoVentasComponent } from './modal-maximoVentas/modal-maximoVentas.component';
import { ItemEditComponent } from './item-edit/item-edit.component';
import { ModalPrecioControlComponent } from './modal-precioControl/modal-precioControl.component';
import { LogService } from '@services/log-service.service';
import { ToastrService } from 'ngx-toastr';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';

@Component({
  selector: 'app-item',
  templateUrl: './item.component.html',
  styleUrls: ['./item.component.scss']
})
export class ItemComponent implements OnInit {

  nombre_ventana:string="abminitem.vb"; 

  public item=[]; 
  public data=[];
  displayedColumns = ['codigo','descripcion','descripcorta','descripabr','medida','unidad','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  
  myControl = new FormControl<string | Item>('');
  options: Item[] = [];
  filteredOptions: Observable<Item[]>;
  userConn:any;

  public ventana="Item"
	public detalle="initem-detalle";
	public tipo="transaccion-initem-DELETE";

  constructor(private api:ApiService,public dialog: MatDialog, private spinner: NgxSpinnerService, private toastr: ToastrService,
    public log_module: LogService, public nombre_ventana_service: NombreVentanaService) {
    this.mandarNombre();
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }

  ngOnInit(){
    this.getAllitem();

    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map(value => {
        const name = typeof value === 'string' ? value : value?.codigo;
        return name ? this._filter(name as string) : this.options.slice();
      }),
    );
  }
  
  private _filter(name: string): Item[] {
    const filterValue = name.toLowerCase(); 
    let a = this.options.filter(option => option.codigo.toLowerCase().includes(filterValue))
    
    return a;
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
    console.log(this.dataSource.filter);
  }

  displayFn(user: Item): string {
    return user && user.codigo ? user.codigo : '';
  }

  getAllitem(){
    let user_conn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/inventario/mant/initem/'+user_conn)
      .subscribe({
        next: (datav) => {
          this.item = datav;

          this.dataSource = new MatTableDataSource(this.item);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;

          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  seg_adm/mant/adarea/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: '350px',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/inventario/mant/initem/'+this.userConn+"/"+element.codigo)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
            this.toastr.success('! SE ELIMINO EXITOSAMENTE !');
            this.spinner.show();
            setTimeout(() => {
              this.spinner.hide();
            }, 1500);

            location.reload();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! NO SE ELIMINO !');
      }
    });
  }

  openDialog(enterAnimationDuration: string, exitAnimationDuration: string): void {
    this.dialog.open(ItemCreateComponent, {
      width: '1100px',
      height:'auto',
      enterAnimationDuration,
      exitAnimationDuration,
    });
  }

  editar(item): void {
    this.data = item;
      const dialogRef = this.dialog.open(ItemEditComponent, {
        data: {dataItem:item},
        width: '750px',
    });
  }

  saldosAcubrir(item){
    this.data = item;
      const dialogRef = this.dialog.open(ModalSaldoCubrirComponent, {
        data: {dataItem:item},
        width: '750px',
    });
  }

  componentesArmarKit(item){
    this.data = item;
      const dialogRef = this.dialog.open(ModalComponenteskitComponent, {
        data: {dataItem:item},
        width: '750px',
    });
  }

  maximoVentas(item){
    this.data = item;
      const dialogRef = this.dialog.open(ModalMaximoVentasComponent, {
        data: {dataItem:item},
        width: '1050px',
    });
  }
  
  controPrecio(item){
    this.data = item;
      const dialogRef = this.dialog.open(ModalPrecioControlComponent, {
        data: {dataItem:item},
        width: '750px',
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<div class="p-2">
  <div class="overlay"></div>
  <div class="p-4 mb-3 mat-elevation-z8">
    <div class="row">
      <div class="col">
        <mat-form-field class="example-full-width" appearance="outline">
          <mat-label>Buscar Item por Codigo </mat-label>
          <input type="text" matInput [formControl]="myControl" (keyup)="applyFilter($event)" placeholder="Codigo"
            #input style="text-transform: uppercase;">
        </mat-form-field>
      </div>

      <div class="col-md text-center">
        <h3>Item</h3>
      </div>
      <div class="col-sm">
        <button mat-button color="primary" (click)="openDialog('500ms', '700ms')" style="float: right;">
          <i class="fa fa-plus"></i> Crear Item</button>
      </div>
    </div>
  </div>

  <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
    <br>
    <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
  </ngx-spinner>

  <div class="row">
    <div class="col-lg">
      <div class="team p-2 bg-white scrollmenu">
        <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
          <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>

            <ng-container matColumnDef="codigo">
              <th mat-header-cell *matHeaderCellDef> Codigo </th>
              <td mat-cell *matCellDef="let element"> {{ element.codigo }} </td>
            </ng-container>

            <ng-container matColumnDef="descripcion">
              <th mat-header-cell *matHeaderCellDef> Descripcion</th>
              <td mat-cell *matCellDef="let element"> {{ element.descripcion }} </td>
            </ng-container>

            <ng-container matColumnDef="descripcorta">
              <th mat-header-cell *matHeaderCellDef>Descripcion Corta</th>
              <td mat-cell *matCellDef="let element"> {{ element.descripcorta }} </td>
            </ng-container>

            <ng-container matColumnDef="descripabr">
              <th mat-header-cell *matHeaderCellDef> Descripcion Abreviada </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.descripabr }} </td>
            </ng-container>

            <ng-container matColumnDef="medida">
              <th mat-header-cell *matHeaderCellDef> Medida </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.medida }} </td>
            </ng-container>

            <ng-container matColumnDef="unidad">
              <th mat-header-cell *matHeaderCellDef> Unidad </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.unidad }} </td>
            </ng-container>

            <ng-container matColumnDef="accion">
              <th mat-header-cell *matHeaderCellDef>Acción</th>
              <td mat-cell *matCellDef="let element">
                <button mat-icon-button1 [matMenuTriggerFor]="menu" style="color: #093070;">
                  <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                </button>
                <mat-menu #menu="matMenu">
                  <button mat-menu-item (click)="editar(element);">
                    <span><i class="fa fa-pen"></i> Editar</span>
                  </button>

                  <button mat-menu-item (click)="eliminar(element);">
                    <span><i class="fa fa-trash"></i> Eliminar</span>
                  </button>

                  <button mat-menu-item (click)="saldosAcubrir(element);">
                    <span><i class="fa fa-cogs"></i> Saldos A Cubrir</span>
                  </button>

                  <button mat-menu-item (click)="componentesArmarKit(element);">
                    <span><i class="fa fa-cogs"></i> Componentes / Armar Kit</span>
                  </button>

                  <button mat-menu-item (click)="maximoVentas(element);">
                    <span><i class="fa fa-cogs"></i> Maximo de Venta</span>
                  </button>

                  <button mat-menu-item (click)="controPrecio(element);">
                    <span><i class="fa fa-cogs"></i> Control de Precios</span>
                  </button>
                </mat-menu>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table><br>

          <mat-paginator #paginator [pageSizeOptions]="[20, 35, 50]" showFirstLastButtons></mat-paginator>
        </div>
      </div>
    </div>
  </div>
</div>
