import { Component, ViewChild } from '@angular/core';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { FormControl } from '@angular/forms';
import { Observable } from 'rxjs';
import { ApiService } from '@services/api.service';
import { adUsuario } from '@services/modelos/objetos';
import { MatDialog } from '@angular/material/dialog';
import { UsuarioCreateComponent } from './usuario-create/usuario-create.component';
import { NgxSpinnerService } from 'ngx-spinner';
import { UsuarioEditComponent } from './usuario-edit/usuario-edit.component';
import { UsuarioDeleteComponent } from './usuario-delete/usuario-delete.component';
import { map, startWith } from 'rxjs/operators';
import { ToastrService } from 'ngx-toastr';
import { LogService } from '@services/log-service.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';

@Component({
  selector: 'app-usuario',
  templateUrl: './usuario.component.html',
  styleUrls: ['./usuario.component.scss']
})
export class UsuarioComponent{

  usuarios:any=[];
  data:any='';
  userConn:any;
  displayedColumns = ['login', 'persona', 'vencimiento','activo','codrol','horareg','fechareg','usuarioreg','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;
  
  myControl = new FormControl<string | adUsuario>('');
  options: adUsuario[] = [];
  filteredOptions: Observable<adUsuario[]>;

  nombre_ventana:string="abmadusuario.vb";
  public ventana="Usuarios"
  public detalle="usuario-delete";
  public tipo="usuario-DELETE";

  constructor(private api:ApiService,public dialog: MatDialog,private spinner: NgxSpinnerService,public log_module:LogService,
    private toastr: ToastrService, public nombre_ventana_service: NombreVentanaService) {
    this.mandarNombre();
    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }

  ngOnInit(){
    this.getAllUsers();

    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map(value => {
        const name = typeof value === 'string' ? value : value?.login;
        return name ? this._filter(name as string) : this.options.slice();
      }),
    );
  }

  private _filter(name: string): adUsuario[] {
    const filterValue = name.toLowerCase();

    return this.options.filter(option => option.login.toLowerCase().includes(filterValue));
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
    console.log(this.dataSource.filter);
  }
  
  displayFn(user: adUsuario): string {
    return user && user.login ? user.login : '';
  }

  getAllUsers(){
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/adusuario/'+this.userConn)    
      .subscribe({
        next: (datav) => {
          this.usuarios = datav;
          console.log(this.usuarios);
                
          this.spinner.show();

          this.dataSource = new MatTableDataSource(this.usuarios);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;

          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  openDialog(): void {
    this.dialog.open(UsuarioCreateComponent, {
      width: 'auto',
      height:'auto',
    });
  }

  editar(dataUsuarioEdit:any){
    this.data = dataUsuarioEdit;
      const dialogRef = this.dialog.open(UsuarioEditComponent, {
        data: {dataUsuarioEdit:dataUsuarioEdit},
        width: 'auto',
        height:'auto',
    });
  }

  eliminar(dataUsuarioEliminar:any){
    this.data = dataUsuarioEliminar;
      const dialogRef = this.dialog.open(UsuarioDeleteComponent, {
        data: {dataUsuarioEliminar:dataUsuarioEliminar},
        width: 'auto',
        height:'auto',
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/seg_adm/mant/adarea/'+this.userConn+"/"+ dataUsuarioEliminar.codigo)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
            
            this.toastr.success('!ELIMINADO EXITOSAMENTE!');
            location.reload();
          },
          error: (err: any) => { 
            console.log(err);
            this.toastr.error('! NO ELIMINADO !');
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! CANCELADO !');
      }
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<div class="p-2">
  <div class="overlay"></div>
  <div class="p-4 mat-elevation-z8">
    <div class="row">
      <div class="col-md">
        <mat-form-field class="example-full-width" appearance="outline">
          <mat-label>Buscador Usuario </mat-label>
          <input type="text" matInput [formControl]="myControl" [matAutocomplete]="auto" (keyup)="applyFilter($event)"
            placeholder="Usuario" #input>
          <mat-autocomplete #auto="matAutocomplete" [displayWith]="displayFn">
            <mat-option *ngFor="let option of filteredOptions | async" [value]="option">
              {{option.login}}
            </mat-option>
          </mat-autocomplete>
        </mat-form-field>
      </div>

      <div class="col-md text-center">
        <h3>Usuarios</h3>
      </div>

      <div class="col-sm">
        <button mat-button color="primary" (click)="openDialog()" style="float: right;">
          <i class="fa fa-plus"></i> Crear Usuario</button>
      </div>
    </div>
  </div>

  <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
    <br>
    <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
  </ngx-spinner>

  <div class="row">
    <div class="col-lg">
      <div class="team p-2 bg-white scrollmenu">
        <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">

          <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
            <ng-container matColumnDef="login">
              <th mat-header-cell *matHeaderCellDef> Login </th>
              <td mat-cell *matCellDef="let element"> {{ element.login }} </td>
            </ng-container>

            <ng-container matColumnDef="persona">
              <th mat-header-cell *matHeaderCellDef> Persona</th>
              <td mat-cell *matCellDef="let element"> {{ element.descPersona }} </td>
            </ng-container>

            <ng-container matColumnDef="vencimiento">
              <th mat-header-cell *matHeaderCellDef> Vencimiento </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">
                {{ element.vencimiento | date:'yyyy/MM/dd' }} </td>
            </ng-container>

            <ng-container matColumnDef="activo">
              <th mat-header-cell *matHeaderCellDef> Activo </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">
                <ng-container *ngIf="element.activo == true">
                  Activo
                </ng-container>
                <ng-container *ngIf="element.activo == false">
                  No Activo
                </ng-container>
              </td>
            </ng-container>

            <ng-container matColumnDef="codrol">
              <th mat-header-cell *matHeaderCellDef> Codigo Rol </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;"> {{ element.codrol }} </td>
            </ng-container>

            <ng-container matColumnDef="horareg">
              <th mat-header-cell *matHeaderCellDef> Registro Hora </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;"> {{ element.horareg }} </td>
            </ng-container>

            <ng-container matColumnDef="fechareg">
              <th mat-header-cell *matHeaderCellDef> Registro Fecha </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">
                {{ element.fechareg | date:'yyyy/MM/dd' }} </td>
            </ng-container>

            <ng-container matColumnDef="usuarioreg">
              <th mat-header-cell *matHeaderCellDef> Registro Usuario </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;"> {{ element.usuarioreg }} </td>
            </ng-container>
            <ng-container matColumnDef="accion">
              <th mat-header-cell *matHeaderCellDef>Acción</th>
              <td mat-cell *matCellDef="let element">
                <button mat-icon-button1 [matMenuTriggerFor]="menu" style="color: #093070;">
                  <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                </button>
                <mat-menu #menu="matMenu">
                  <button mat-menu-item (click)="editar(element);">
                    <span><i class="fa fa-pen"></i> Editar</span>
                  </button>
                  <button mat-menu-item (click)="eliminar(element);">
                    <span><i class="fa fa-times"></i> Desactivar</span>
                  </button>
                  <button mat-menu-item (click)="eliminar(element);">
                    <span><i class="fa fa-trash"></i> Eliminar</span>
                  </button>
                </mat-menu>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table>

          <mat-paginator #paginator [pageSizeOptions]="[10, 15, 25]" showFirstLastButtons></mat-paginator>
        </div>
      </div>
    </div>
  </div>
