            <i class="fa fa-paper-plane" aria-hidden="true"></i> Transferir Proforma
          </button>
        </ng-container>

        <ng-container *ngIf="isCheckedCotizaciones">
          <button mat-raised-button color="primary" (click)="transferirCotizaciones();" [disabled]="!id_cotizaciones">
            <i class="fa fa-paper-plane" aria-hidden="true"></i> Transferir Cotizaci√≥n
          </button>
        </ng-container>
      </div>
    </div>
  </div>
