import { Component, OnInit } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { UnidadnegocioCreateComponent } from './unidadnegocio-create/unidadnegocio-create.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { ToastrService } from 'ngx-toastr';
import { LogService } from '@services/log-service.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
@Component({
  selector: 'app-unidadnegocio',
  templateUrl: './unidadnegocio.component.html',
  styleUrls: ['./unidadnegocio.component.scss']
})
export class UnidadnegocioComponent implements OnInit {
  
  uni_negocio: any = [];
  userConn: string;
  usuarioLogueado:string;

  displayedColumns = ['codigo','descripcion','horareg','accion'];
  dataSource = new MatTableDataSource();

  nombre_ventana:string="abmadunidad.vb";
  public ventana="Unidad de Negocio"
	public detalle="unidadnegocio-delete";
	public tipo="transaccion-unidadnegocio-DELETE";

  constructor(private api: ApiService, public dialog: MatDialog, private spinner: NgxSpinnerService, private toastr: ToastrService,
    public log_module: LogService, public nombre_ventana_service: NombreVentanaService) {
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    this.api.getRolUserParaVentana(this.usuarioLogueado, this.nombre_ventana);
    this.mandarNombre();
  }

  ngOnInit() {
    this.getAllUnidadNegocio();
  }

  getAllUnidadNegocio(){

    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/adunidad/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.uni_negocio = datav;       
          // console.log(this.uni_negocio);
          
          this.spinner.show();
          this.dataSource = new MatTableDataSource(this.uni_negocio);

          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  openDialog(): void {
    this.dialog.open(UnidadnegocioCreateComponent, {
      width: 'auto',
      height:'auto',
    });
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  /moneda Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: '350px',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result){
        return this.api.delete('/seg_adm/mant/adunidad/'+this.userConn+'/'+element.codigo)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana,this.detalle,this.tipo);
            this.toastr.success('! SE ELIMINO EXITOSAMENTE !');
            this.spinner.show();
            setTimeout(() => {
              this.spinner.hide();
            }, 1500);
            location.reload();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        alert("¡No se elimino!");
        this.toastr.error('! NO SE ELIMINO !');
      }
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<section class="content">
  <div class="container-fluid">
    <div class="overlay"></div>
    <div class="mb-3 mat-elevation-z8">
      <div class="row">
        <div class="col-md headTittle">
          <button mat-button color="primary" (click)="openDialog()" style="float: right;">
            <i class="fa fa-plus"></i> Crear Unidad de Negocio</button>
        </div>

        <div class="col-md text-center headTittle">
          <h3>Unidad de Negocio</h3>
        </div>

        <div class="col-md text-right headTittle">
          <mat-icon color="primary">arrow_drop_down</mat-icon>
          <mat-icon color="primary">close</mat-icon>
        </div>
      </div>
    </div>

    <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
      <br>
      <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
    </ngx-spinner>

    <div class="row">
      <div class="col-lg">
        <div class="team p-2 bg-white scrollmenu">
          <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
              <ng-container matColumnDef="codigo">
                <th mat-header-cell *matHeaderCellDef> Codigo </th>
                <td mat-cell *matCellDef="let element"> {{ element.codigo }} </td>
              </ng-container>

              <ng-container matColumnDef="descripcion">
                <th mat-header-cell *matHeaderCellDef> Descripcion</th>
                <td mat-cell *matCellDef="let element"> {{ element.descripcion }} </td>
              </ng-container>

              <ng-container matColumnDef="horareg">
                <th mat-header-cell *matHeaderCellDef> Hora Reg </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.horareg }} </td>
              </ng-container>

              <ng-container matColumnDef="accion">
                <th mat-header-cell *matHeaderCellDef>Acción</th>
                <td mat-cell *matCellDef="let element">
                  <button mat-icon-button1 [matMenuTriggerFor]="menu" style="color: #093070;">
                    <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                  </button>
                  <mat-menu #menu="matMenu">
                    <button mat-menu-item (click)="eliminar(element);">
                      <span><i class="fa fa-trash"></i> Eliminar</span>
                    </button>
                  </mat-menu>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table><br>
            <!-- <mat-paginator #paginator [pageSizeOptions]="[5, 10, 15]" showFirstLastButtons></mat-paginator><br> -->
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
