import { DatePipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ModalVendedorComponent } from '@components/mantenimiento/ventas/modal-vendedor/modal-vendedor.component';
import { VendedorService } from '@components/mantenimiento/ventas/serviciovendedor/vendedor.service';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-numeracion-cobranza-create',
  templateUrl: './numeracion-cobranza-create.component.html',
  styleUrls: ['./numeracion-cobranza-create.component.scss']
})
export class NumeracionCobranzaCreateComponent implements OnInit {

  FormularioData:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  dataform:any='';
  numCobran:any=[];
  negocio:any=[];
  userConn:any;
  userLogueado:any=[];
  inputValue: number | null = null;

  vendedor_get: any = [];
  cod_vendedor:any;

  public ventana="numCobranza-create"
  public detalle="numCobranza-detalle";
  public tipo="transaccion-numCobranza-POST";


  constructor(private _formBuilder: FormBuilder, public dialog: MatDialog, private datePipe: DatePipe, private spinner: NgxSpinnerService,
    private api:ApiService, public dialogRef: MatDialogRef<NumeracionCobranzaCreateComponent>, public _snackBar: MatSnackBar,
    public log_module: LogService, private toastr: ToastrService, public serviciovendedor: VendedorService) {
    
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.userLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.FormularioData = this.createForm();
    this.getAllUnidadesNegocio();
  } 



  ngOnInit() {
    this.serviciovendedor.disparadorDeVendedores.subscribe(data => {
      console.log("Recibiendo Vendedor: ", data);
      this.cod_vendedor = data.vendedor;
    });
  }

  ngAfterViewInit(){
    this.getVendedorCatalogo();
  }

  
  getVendedorCatalogo() {
    let errorMessage: string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --/seg_adm/mant/vevendedor/catalogo/";
    return this.api.getAll('/seg_adm/mant/vevendedor/catalogo/' + this.userConn)
      .subscribe({
        next: (datav) => {
          this.vendedor_get = datav;
        },
                
        error: (err: any) => {
          console.log(err, errorMessage);
        },
        complete: () => {}
      })
  }

  modalVendedor(): void {
    this.dialog.open(ModalVendedorComponent, {
      width: 'auto',
      height: 'auto',
    });
  }

  onLeaveVendedor(event: any) {
    const inputValue = event.target.value;
    let entero = Number(inputValue);

    // Verificar si el valor ingresado est√° presente en los objetos del array
    const encontrado = this.vendedor_get.some(objeto => objeto.codigo === entero);

    if (!encontrado) {
      // Si el valor no est√° en el array, dejar el campo vac√≠o
      event.target.value = '';
      console.log("NO ENCONTRADO VALOR DE INPUT");
    } else {
      event.target.value = entero;
    }
  }

  
  createForm(): FormGroup{
    let usuario_logueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
  

    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      id: [this.dataform.id,Validators.compose([Validators.required])],
      descripcion: [this.dataform.descripcion,Validators.compose([Validators.required])],
      nroactual: [this.dataform.nroactual,Validators.pattern(/^-?\d+$/)],
      codvendedor: [this.dataform.codvendedor,Validators.compose([Validators.required])],
      codunidad: [this.dataform.codunidad],

      horareg: [hora_actual_complete],
      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      usuarioreg: [usuario_logueado],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:-- /ctasXcobrar/mant/cotipo/";
    
    return this.api.create("/ctasXcobrar/mant/cotipo/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.numCobran = datav;

          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this.onNoClick();
          this.spinner.show();
          this.toastr.success('Guardado con Exito! üéâ');

          location.reload();
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
          this.toastr.error('! NO SE GUARDO !');
        },
        complete: () => { }
      })
  }

  getAllUnidadesNegocio(){
    let errorMessage = "La Ruta presenta fallos al hacer peticion GET /seg_adm/mant/adunidad/catalogo/";
    return this.api.getAll('/seg_adm/mant/adunidad/catalogo/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.negocio = datav;
          console.log('data', datav);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }
  
  onNoClick(): void {
    this.dialogRef.close();
  }

  onInputChange(value: string) {
    // Validar y formatear el valor ingresado
    const parsedValue = parseFloat(value);

    if (!isNaN(parsedValue) && Number.isInteger(parsedValue)) {
      this.inputValue = parsedValue;
    } else {
      this.inputValue = null;
    }
  }

}
<div style="background-color: #f0f0f0;">
  <div class="p-2">
    <h1 mat-dialog-title>Crear Numeraci√≥n de Cobranza</h1>
    <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div>
  </div>

  <div mat-dialog-content>
    <div class="container">
      <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">

        <div class="row mb-2">
          <div class="col-md">
            <mat-form-field appearance="outline" class="example-full-small">
              <mat-label>C√≥digo </mat-label>
              <input matInput type="text" formControlName="id" maxlength="5" required>
            </mat-form-field>
          </div>
          <div class="col-md">
            <div class="input-group">
              <ng-container>
                <input style="background-color: transparent; color: black; border: 1px solid #959595;"
                 type="text" class="form-control form-control-sm" formControlName="codvendedor"
                placeholder="Vendedor" [(ngModel)]="cod_vendedor" (blur)="onLeaveVendedor($event)">
              </ng-container>
          
              <div class="input-group-append">
                <button class="btn btn-outline-primary form-control-sm" type="button" (click)="modalVendedor();"
                  style="border-color: #093070;"><i class="fa fa-search"></i>
                </button>
              </div>
            </div>
          </div>
        </div>

        <div class="row mb-2">
          <div class="col-md">
            <mat-form-field appearance="outline" class="example-full-small">
              <mat-label>Descripci√≥n </mat-label>
              <input matInput type="text" formControlName="descripcion" maxlength="30" required>
            </mat-form-field>
          </div>
        </div>


        <div class="row mb-2">
          <div class="col-md">
            <mat-form-field appearance="outline" class="example-full-small">
              <mat-label>N√∫mero Actual </mat-label>
              <input type="number" (input)="onInputChange($event.target.value)" min="0" step="1" matInput
                formControlName="nroactual" [(ngModel)]="inputValue" required>
            </mat-form-field>
          </div>


          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Unidad de Negocio</mat-label>
              <mat-select formControlName="codunidad" required>
