import { DatePipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-tipos-credito-create',
  templateUrl: './tipos-credito-create.component.html',
  styleUrls: ['./tipos-credito-create.component.scss']
})
export class TiposCreditoCreateComponent implements OnInit {

  FormularioData:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  dataform:any='';
  rubro:any=[];
  moneda:any=[];
  empresa:any=[];
  userConn:any;
  userLogueado: any = [];
  inputValue: number | null = null;

  public ventana="tipos-credito-create"
  public detalle="tipos-credito-detalle";
  public tipo="transaccion-tipos-credito-POST";

  constructor(private _formBuilder: FormBuilder, private datePipe: DatePipe, private spinner: NgxSpinnerService,
    private api:ApiService, public dialogRef: MatDialogRef<TiposCreditoCreateComponent>, public _snackBar: MatSnackBar,
    public log_module:LogService, private toastr: ToastrService){

      this.FormularioData = this.createForm();
  } 

  ngOnInit(): void {
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.userLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
  }

  createForm(): FormGroup{
    let usuario_logueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    console.log(this.userLogueado);

    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      codigo: [this.dataform.codigo, Validators.compose([Validators.required])],
      descripcion: [this.dataform.descripcion, Validators.compose([Validators.required])],
      duracion: [this.dataform.duracion, Validators.compose([Validators.required])],
      es_fijo: [this.dataform.es_fijo],

      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      horareg: [hora_actual_complete],
      usuarioreg: [usuario_logueado],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:- /venta/mant/verubro/";

    return this.api.create("/venta/mant/vetipocredito/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.rubro = datav;
          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this.onNoClick();
          this.spinner.show();
          this.toastr.success('Guardado con Exito! ðŸŽ‰');
          location.reload();      
        },

        error: (err) => { 
          console.log(err, errorMessage);
          this.toastr.error('! NO SE GUARDO !');
        },
        complete: () => { }
      })
  }

  onInputChange(value: string) {
    // Validar y formatear el valor ingresado
    const parsedValue = parseFloat(value);

    if (!isNaN(parsedValue) && Number.isInteger(parsedValue)) {
      this.inputValue = parsedValue;
    } else {
      this.inputValue = null;
    }
  }

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<div style="background-color: #f0f0f0;">
  <div class="p-2">
    <h1 mat-dialog-title>Crear Tipos de Credito</h1>
    <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div>
  </div>

  <div mat-dialog-content>
    <div class="container">
      <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">
        <div class="row mb-2">
          <div class="col-md">
            <mat-form-field appearance="outline" class="example-full-small">
              <mat-label>CÃ³digo </mat-label>
              <input matInput type="text" formControlName="codigo" maxlength="6" required>
            </mat-form-field>
          </div>
          <div class="col-md">
            <mat-form-field appearance="outline" class="example-full-width">
              <mat-label>DescripciÃ³n </mat-label>
              <input type="text" matInput formControlName="descripcion" maxlength="50" required>
            </mat-form-field>
          </div>
          <div class="col-md">
            <mat-form-field appearance="outline" class="example-full-width">
              <mat-label>DuraciÃ³n Dias</mat-label>
              <input type="number" matInput formControlName="duracion" min="0" step="1"
                (input)="onInputChange($event.target.value)" [(ngModel)]="inputValue" required>
            </mat-form-field>
          </div>
        </div>
        <div class="row mb-2">
          <div class="col-md text-center">
            <input type="checkbox" formControlName="es_fijo">&nbsp;&nbsp;
            <mat-label>Es credito Fijo </mat-label>
          </div>
        </div>

        <div class="mat-dialog-actions" mat-dialog-actions>
          <a mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</a>
          <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit"><i
              class="fa fa-save"></i> Guardar
          </button>
        </div>
      </form>
    </div>
  </div>
</div>
