import { Component, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';

import { fnTipoTransferencia } from '@services/modelos/objetos';
import { NumeraciontransferenciasCreateComponent } from './numeraciontransferencias-create/numeraciontransferencias-create.component';
import { NumeraciontransferenciasEditComponent } from './numeraciontransferencias-edit/numeraciontransferencias-edit.component';

import { NgxSpinnerService } from 'ngx-spinner';
import { MatPaginator } from '@angular/material/paginator';
import { FormControl } from '@angular/forms';
import { Observable, map, startWith } from 'rxjs';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { ToastrService } from 'ngx-toastr';
import { LogService } from '@services/log-service.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';

@Component({
  selector: 'app-numeraciontransferencias',
  templateUrl: './numeraciontransferencias.component.html',
  styleUrls: ['./numeraciontransferencias.component.scss']
})
export class NumeraciontransferenciasComponent{


  numTransf:any=[]; 
  data:[];
  dataNumTransfEdit_copied: any = [];
  
  displayedColumns = ['id','descripcion' ,'nroactual','horareg','fechareg','usuarioreg','codunidad','descUnidad','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;  

  myControl = new FormControl<string | fnTipoTransferencia>('');
  options: fnTipoTransferencia[] = [];
  filteredOptions: Observable<fnTipoTransferencia[]>;
  userConn:any;

  nombre_ventana:string="abmfntipotransferencia.vb";
  public ventana="TipoTransferencia"
  public detalle="TipoTransferencia-delete";
  public tipo="TipoTransferencia-DELETE";





  constructor(private api:ApiService,public dialog: MatDialog, private spinner: NgxSpinnerService,
    public log_module:LogService, private toastr: ToastrService, public nombre_ventana_service:NombreVentanaService){
    this.mandarNombre();
    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }





  ngOnInit(): void {
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.getAllNumTransferencias(this.userConn);
    
    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map(value => {
        const name = typeof value === 'string' ? value : value?.id;
        return name ? this._filter(name as string) : this.options.slice();
      }),
    );
  }



  getAllNumTransferencias(userConn){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/fondos/mant/fntipotransferencia/'+userConn)
      .subscribe({
        next: (datav) => {
          this.numTransf = datav;

          this.dataSource = new MatTableDataSource(this.numTransf);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;

          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }
  
  openDialog(): void {
    this.dialog.open(NumeraciontransferenciasCreateComponent, {
      width: 'auto',
      height:'auto',
    });
  }

  private _filter(name: string): fnTipoTransferencia[] {
    const filterValue = name.toLowerCase();

    return this.options.filter(option => option.id.toLowerCase().includes(filterValue));
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }

  displayFn(user: fnTipoTransferencia): string {
    return user && user.id ? user.id : '';
  }

  editar(dataNumTransfEdit){
    this.dataNumTransfEdit_copied ={...dataNumTransfEdit};
    console.log(this.dataNumTransfEdit_copied);
    
    this.data = dataNumTransfEdit;
    this.dialog.open(NumeraciontransferenciasEditComponent, {
      data: {dataNumTransfEdit:this.dataNumTransfEdit_copied},
      width: 'auto',
      height:'auto',
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  fondos/mant/fntipotransferencia/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: 'auto',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/fondos/mant/fntipotransferencia/'+this.userConn+"/"+ element.id)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
            
            this.toastr.success('!ELIMINADO EXITOSAMENTE!');
            location.reload();
          },
          error: (err: any) => { 
            console.log(err, errorMessage);
            this.toastr.error('! NO ELIMINADO !');
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! CANCELADO !');
      }
    });
  }

}
<div class="p-2">
  <div class="overlay"></div>
  <div class="mb-3 mat-elevation-z8">
    <div class="row headTittle">
      <div class="col-lg">
        <mat-form-field class="example-full-width" appearance="outline">
          <mat-label>Buscador Numeración </mat-label>
          <input type="text" matInput [formControl]="myControl" (keyup)="applyFilter($event)" placeholder="Codigo"
            #input>
        </mat-form-field>
      </div>
      <div class="col-md text-center">
        <h3>Numeración de Transferencias</h3>
      </div>
      <div class="col-sm">
        <button mat-button color="primary" (click)="openDialog()" style="float: right;">
          <i class="fa fa-plus"></i> Crear Numeración</button>
      </div>

      <div class="col-md-1 text-right">
        <mat-icon color="primary">arrow_drop_down</mat-icon>
        <mat-icon color="primary">close</mat-icon>
      </div>
    </div>
  </div>
  <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
    <br>
    <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
  </ngx-spinner>
  <div class="row">
    <div class="col-lg">
      <div class="team p-2 bg-white scrollmenu">
        <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
          <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8">

            <ng-container matColumnDef="id">
              <th mat-header-cell *matHeaderCellDef> Código</th>
              <td mat-cell *matCellDef="let element"> {{ element.id }} </td>
            </ng-container>

            <ng-container matColumnDef="descripcion">
              <th mat-header-cell *matHeaderCellDef style="text-align: left;"> Descripción</th>
              <td mat-cell *matCellDef="let element" style="text-align: left;"> {{ element.descripcion }} </td>
            </ng-container>

            <ng-container matColumnDef="nroactual">
              <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Nro. Actual </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.nroactual }} </td>
            </ng-container>

            <ng-container matColumnDef="horareg">
              <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Hora Reg </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.horareg }} </td>
            </ng-container>

            <ng-container matColumnDef="fechareg">
              <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Fecha Reg </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">
                {{ element.fechareg | date:'yyyy/MM/dd' }} </td>
            </ng-container>

            <ng-container matColumnDef="usuarioreg">
              <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Usuario Reg </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.usuarioreg }} </td>
            </ng-container>

            <ng-container matColumnDef="codunidad">
              <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Cod. Unidad </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.codunidad }} </td>
            </ng-container>

            <ng-container matColumnDef="descUnidad">
              <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Desc. Unidad </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.descUnidad }} </td>
            </ng-container>


            <ng-container matColumnDef="accion">
              <th mat-header-cell *matHeaderCellDef>Acción</th>
              <td mat-cell *matCellDef="let element">
                <button mat-icon-button1 [matMenuTriggerFor]="menu">
                  <i class="fa fa-ellipsis-v" aria-hidden="true" style="color: #093070;"></i>
                </button>
                <mat-menu #menu="matMenu">
                  <button mat-menu-item (click)="editar(element);">
                    <span><i class="fa fa-pen"></i> Editar</span>
                  </button>
                  <button mat-menu-item (click)="eliminar(element);">
                    <span><i class="fa fa-trash"></i> Eliminar</span>
                  </button>
                </mat-menu>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table>
          <mat-paginator #paginator [pageSizeOptions]="[5, 10, 15]" showFirstLastButtons></mat-paginator>
        </div>
      </div>
    </div>
  </div>
</div>
