import { DatePipe } from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-numeracion-comprobantes-edit',
  templateUrl: './numeracion-comprobantes-edit.component.html',
  styleUrls: ['./numeracion-comprobantes-edit.component.scss']
})
export class NumeracionComprobantesEditComponent implements OnInit {

  FormularioDataEdit:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  numcomprob_edit:any=[];
  dataform:any='';
  numcomprob:any=[];
  usuario_logueado:any;
  user_conn:any;
  errorMessage;
  inputValue: number | null = null;

  public ventana="numComprobates"
  public detalle="numComprobates-edit";
  public tipo="numComprobates-edit-PUT";

  constructor(private _formBuilder: FormBuilder, public log_module:LogService, public dialogRef: MatDialogRef<NumeracionComprobantesEditComponent>, 
    @Inject(MAT_DIALOG_DATA) public datanumcomprobEdit: any, private api:ApiService, private datePipe: DatePipe,private toastr: ToastrService,
    public _snackBar: MatSnackBar){
    this.FormularioDataEdit = this.createForm();
  }

  ngOnInit(){
    this.usuario_logueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    this.user_conn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    this.numcomprob_edit = this.datanumcomprobEdit.datanumcomprobEdit;
  }


  createForm(): FormGroup {
    const usuario_logueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      id: [this.datanumcomprobEdit.datanumcomprobEdit.id],
      descripcion: [this.dataform.descripcion,Validators.compose([Validators.required])],
      nroactual: [this.dataform.nroactual,Validators.pattern(/^-?\d+$/)],
      ajuste: [false],
      desde: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      hasta: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      horareg: [hora_actual_complete],
      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      usuarioreg: [usuario_logueado],
    });
  } 

  submitData(){
    let data = this.FormularioDataEdit.value;

    this.errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:--  /contab/mant/cnnumeracion/ Update";
    return this.api.update('/contab/mant/cnnumeracion/'+this.user_conn+"/"+this.numcomprob_edit.id, data)
      .subscribe({
        next: (datav) => {
          this.numcomprob = datav;
          this.onNoClick();
          this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
          this.toastr.success('! SE EDITO EXITOSAMENTE !');
          location.reload();
        },
    
        error: (err: any) => { 
          this.toastr.error('! NO SE EDITO !');
          console.log(err, this.errorMessage);
        },
        complete: () => { }
      })
  }

  onInputChange(value: string){
    const parsedValue = parseFloat(value);

    if (!isNaN(parsedValue) && Number.isInteger(parsedValue)) {
      this.inputValue = parsedValue;
    }else{
      this.numcomprob_edit.nroactual = null;
    }
  }

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<div class="p-2">
  <h1 mat-dialog-title>Editar Numeración de Comprobante {{ numcomprob_edit.id }}</h1>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div><br>
</div>

<div mat-dialog-content>
  <div class="container">
    <form class="example-form" [formGroup]="FormularioDataEdit" (ngSubmit)="submitData()">
      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Descripción </mat-label>
            <input type="text" matInput formControlName="descripcion" maxlength="30"
              [(ngModel)]="numcomprob_edit.descripcion" required>
          </mat-form-field>
        </div>
      </div>
      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Número Actual </mat-label>
            <input type="number" min="0" step="1" (input)="onInputChange($event.target.value)" matInput
              formControlName="nroactual" [(ngModel)]="numcomprob_edit.nroactual" required>
          </mat-form-field>
        </div>
      </div>

      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field color="accent">
            <mat-label>Valido Desde</mat-label>
            <input matInput formControlName="desde" [(ngModel)]="numcomprob_edit.desde" [matDatepicker]="picker21">
            <mat-datepicker-toggle matIconSuffix [for]="picker21"></mat-datepicker-toggle>
            <mat-datepicker #picker21 color="primary"></mat-datepicker>
          </mat-form-field>
        </div>
      </div>

      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field color="accent">
            <mat-label>Valido Hasta</mat-label>
            <input matInput formControlName="hasta" [(ngModel)]="numcomprob_edit.hasta" [matDatepicker]="picker2">
            <mat-datepicker-toggle matIconSuffix [for]="picker2"></mat-datepicker-toggle>
            <mat-datepicker #picker2 color="primary"></mat-datepicker>
          </mat-form-field>

        </div>
      </div>

      <div class="mat-dialog-actions" mat-dialog-actions>
        <a mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</a>

        <button mat-raised-button color="primary" [disabled]="!FormularioDataEdit.valid" type="submit"><i
            class="fa fa-save"></i> Guardar</button>
      </div>
    </form>
  </div>
</div>
