import { DatePipe } from '@angular/common';
import { Component } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-area-create',
  templateUrl: './area-create.component.html',
  styleUrls: ['./area-create.component.scss']
})
export class AreaCreateComponent {

  FormularioData:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  dataform:any='';
  area:any=[];
  moneda:any=[];
  empresa:any=[];
  userConn:any;
  userLogueado:any=[];

  public ventana="area-create"
  public detalle="area-detalle";
  public tipo="transaccion-area-POST";
  
  constructor(private _formBuilder: FormBuilder, private datePipe: DatePipe, private spinner: NgxSpinnerService,
    private api:ApiService, public dialogRef: MatDialogRef<AreaCreateComponent>, public _snackBar: MatSnackBar,
    public log_module:LogService, private toastr: ToastrService){

    this.FormularioData = this.createForm();
  } 

  ngOnInit() {
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.userLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    
    this.getAllEmpresa(this.userConn);
  }

  createForm(): FormGroup{
    let usuario_logueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    console.log(this.userLogueado);

    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      codigo: [this.dataform.codigo,Validators.compose([Validators.required])],
      descripcion: [this.dataform.descripcion,Validators.compose([Validators.required])],
      ciudad: [this.dataform.ciudad, Validators.compose([Validators.required])],
      empresa: [this.dataform.empresa],
      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      horareg: [hora_actual_complete],
      usuarioreg: [usuario_logueado],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:-- /seg_adm/mant/adarea/";
    
    return this.api.create("/seg_adm/mant/adarea/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.area = datav;

          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this.onNoClick();
          this.spinner.show();
          this.toastr.success('Guardado con Exito! ðŸŽ‰');

          location.reload();
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
          this.toastr.error('! NO SE GUARDO !');
        },
        complete: () => { }
      })
  }

  getAllEmpresa(user_conn){
    let errorMessage = "La Ruta presenta fallos al hacer peticion GET /seg_adm/mant/adempresa/";
    return this.api.getAll('/seg_adm/mant/adempresa/'+user_conn)
      .subscribe({
        next: (datav) => {
          this.empresa = datav;
          //console.log('data', datav);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }
  
  onNoClick(): void {
    this.dialogRef.close();
  }
   
}
<div class="p-2">
  <h1 mat-dialog-title>Crear Area</h1>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div>
</div>

<div mat-dialog-content>
  <div class="container">
    <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">

      <div class="col-md">
        <mat-label style="padding-right: 34px;"><u>CÃ³digo</u></mat-label>
        <input matInput type="text" formControlName="codigo" maxlength="3" required>
      </div>
      <div class="col-md">
        <mat-label style="padding-right: 5px;"><u>DescripciÃ³n</u></mat-label>
        <input type="text" matInput formControlName="descripcion" maxlength="50" required>
      </div>

      <div class="col">
        <mat-label style="padding-right: 34px;"><u>Ciudad</u></mat-label>
        <select formControlName="ciudad" required>
          <option value="La Paz">La Paz</option>
          <option value="Oruro">Oruro</option>
          <option value="Potosi">Potosi</option>
          <option value="Cochabamba">Cochabamba</option>
          <option value="Sucre">Sucre</option>
          <option value="Tarija">Tarija</option>
          <option value="Santa Cruz">Santa Cruz</option>
          <option value="Beni">Beni</option>
          <option value="Pando">Pando</option>
        </select>
      </div>

      <div class="col">
        <mat-label style="padding-right: 23px;"><u>Empresa</u></mat-label>
        <select formControlName="empresa">
          <option *ngFor="let empresas of empresa" [value]="empresas.codigo">
            {{ empresas.codigo }}
          </option>
        </select>
      </div><br>

      <div class="mat-dialog-actions" mat-dialog-actions>
        <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit"><i
            class="fa fa-save"></i> Guardar</button>
      </div>
    </form>
    <button mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</button>
  </div>
</div>
