import { Component, Inject, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { Router } from '@angular/router';
import { ModalPrecioVentaComponent } from '@components/mantenimiento/ventas/modal-precio-venta/modal-precio-venta.component';
import { ServicioprecioventaService } from '@components/mantenimiento/ventas/servicioprecioventa/servicioprecioventa.service';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';
@Component({
  selector: 'app-urgentes-almacenes',
  templateUrl: './urgentes-almacenes.component.html',
  styleUrls: ['./urgentes-almacenes.component.scss']
})
export class UrgentesAlmacenesComponent implements OnInit {

  FormularioData: FormGroup;  
  fecha_actual = new Date();
  hora_actual = new Date();
  userConn: string; 
  usuario: string; 
  usuario_logueado: string;
  cod_precio_venta_modal_codigo: string;

  dataform: any = '';
  almacen_codigo: any;
  almacen: any = [];
  pedidos_urgentes: any = [];
  moneda: any = [];
  cod_precio_venta_modal: any = [];
  tarifa_get: any = [];
  urgente_save: any = [];

  montoInputView: boolean = false;
  pesoInputView: boolean = false;
  trueBooleano: boolean = true;
  falseBooleano: boolean = false;

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  displayedColumns = ['codtarifa', 'suarea', 'monto', 'codmoneda', 'peso', 'accion'];

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  nombre_ventana:string="abminalmacen.vb";
  public ventana="Almacen"
  public detalle="almacen-detalle";
  public tipo = "transaccion-almacen-DELETE";

  constructor(public dialog: MatDialog, private api: ApiService, private spinner: NgxSpinnerService, private toastr: ToastrService,
    public nombre_ventana_service: NombreVentanaService, public log_module: LogService, public router: Router,
    @Inject(MAT_DIALOG_DATA) public cod_almacen_solurgente: any, private _formBuilder: FormBuilder,
    public servicioPrecioVenta: ServicioprecioventaService, public dialogRef: MatDialogRef<UrgentesAlmacenesComponent>) {
    
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.usuario = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.getMoneda();
    this.getTarifa();
  
    this.FormularioData = this.createForm();
    this.almacen = cod_almacen_solurgente.cod_almacen_solurgente;
    this.almacen_codigo = cod_almacen_solurgente.cod_almacen_solurgente.codigo;

    this.cargarTablaPedidosUrgentes();
  }

  ngOnInit() {
    // precio_venta
    this.servicioPrecioVenta.disparadorDePrecioVenta.subscribe(data => {
      console.log("Recibiendo Precio Venta: ", data);
      this.cod_precio_venta_modal = data.precio_venta;
      this.cod_precio_venta_modal_codigo = data.precio_venta.codigo;
    });
    // fin_precio_venta
  }

  cargarTablaPedidosUrgentes() {
    let errorMessage = "La Ruta presenta fallos al hacer peticion GET --/inventario/mant/inalmacen/catalogo/"
    return this.api.getAll('/inventario/mant/insolurgente_parametros/'+this.userConn+"/"+ this.almacen_codigo)
      .subscribe({
        next: (datav) => {
          this.pedidos_urgentes = datav;
          console.log(this.pedidos_urgentes);

          this.dataSource = new MatTableDataSource(this.pedidos_urgentes);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getMoneda(){
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET -/seg_adm/mant/admoneda/";
    return this.api.getAll('/seg_adm/mant/admoneda/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.moneda = datav;
          //console.log( this.moneda);
        },
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getTarifa(){ 
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    
    return this.api.getAll('/inventario/mant/intarifa/catalogo/'+this.userConn+"/"+this.usuario)
      .subscribe({
        next: (datav) => {
          this.tarifa_get = datav;
          console.log(this.tarifa_get);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  createForm(): FormGroup {   
    return this._formBuilder.group({
      codigo:0,
      codalmacen: 311,
      codtarifa:0,
      suarea: [this.dataform.suarea, Validators.compose([Validators.required])],
      monto: [this.dataform.monto],
      codmoneda: [this.dataform.codmoneda],
      peso: [this.dataform.peso,],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:-- /inventario/mant/insolurgente_parametros/";
    
    return this.api.create("/inventario/mant/insolurgente_parametros/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.urgente_save = datav;

          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this.cargarTablaPedidosUrgentes();
          this.spinner.show();

          this.toastr.success('Guardado con Exito! 🎉');
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
          this.toastr.error('! NO SE GUARDO !');
        },
        complete: () => { }
      })
  }
  
  onLeavePrecio(event: any){
    const inputValue = event.target.value;
    let numero = Number(inputValue);
      
    // Verificar si el valor ingresado está presente en los objetos del array
    const encontrado = this.tarifa_get.some(objeto => objeto.codigo === numero);

    if(!encontrado){
      // Si el valor no está en el array, dejar el campo vacío
      event.target.value = '';
      console.log("NO ENCONTRADO INPUT");
    } else {
      event.target.value = numero;
    }

    // Puedes realizar otras acciones según tus necesidades
    console.log('Input perdió el foco', numero);
  }

  eliminar(element) {
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:-- /venta/mant/insolurgente_parametros/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: 'auto',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/inventario/mant/insolurgente_parametros/'+this.userConn+"/"+element.codigo)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
            
            this.toastr.success('!ELIMINADO EXITOSAMENTE!');
             this.cargarTablaPedidosUrgentes();
          },
          error: (err: any) => { 
            console.log(err, errorMessage);
            this.toastr.error('! NO ELIMINADO !');
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! CANCELADO !');
      }
    });
  }

  modalPrecioVenta(): void {
    this.dialog.open(ModalPrecioVentaComponent, {
      width: 'auto',
      height: 'auto',
    });
  }
  
  verMonto(value) {
    console.log(value);
    
    if(this.montoInputView == true) {
      this.montoInputView = false;
    } else {
      this.montoInputView = true;
    }
  }

  verPeso(value) {
    console.log(value);
    
    if(this.pesoInputView == true) {
      this.pesoInputView = false;
    } else {
      this.pesoInputView = true;
    }
  }

  close(){
    this.dialogRef.close();
  }
}
<div class="container-fluid p-2">
  <mat-card style="background-color: #f9f9f9;">
    <div class="row">
      <div class="col-md text-left headTittle">
        <h3><strong>Solicitudes Urgentes</strong></h3>
      </div>
      <div class="col-md text-right headTittle">
        <mat-icon color="primary" (click)="close();">close</mat-icon>
      </div>
    </div>

    <div class="pl-2">
      <span><strong><u>Almacen: </u></strong>&nbsp; {{ almacen.codigo }}</span> &nbsp;
    </div>

    <div class="row">
      <div class="col-lg">
        <div class="team bg-white scrollmenu">
          <div class="col-lg-12">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8">
              <ng-container matColumnDef="codtarifa">
                <th mat-header-cell *matHeaderCellDef class="th2"> Precio </th>
                <td mat-cell *matCellDef="let element">{{ element.codtarifa }}</td>
              </ng-container>

              <ng-container matColumnDef="suarea">
                <th mat-header-cell *matHeaderCellDef class="th2"> Su Area </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">
                  <input type="checkbox" [checked]="element.suarea" disabled></td>
              </ng-container>

              <ng-container matColumnDef="monto">
                <th mat-header-cell *matHeaderCellDef class="th2"> Monto </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.monto | number: '1.2-2'}}
                </td>
              </ng-container>

              <ng-container matColumnDef="codmoneda">
                <th mat-header-cell *matHeaderCellDef class="th1"> Moneda </th>
                <td mat-cell *matCellDef="let element" style="text-align: right;">{{ element.codmoneda }}</td>
              </ng-container>

              <ng-container matColumnDef="peso">
                <th mat-header-cell *matHeaderCellDef class="th1"> Peso </th>
                <td mat-cell *matCellDef="let element" style="text-align: right;">{{ element.peso }}</td>
              </ng-container>

              <ng-container matColumnDef="accion">
                <th mat-header-cell *matHeaderCellDef style="color: white !important;">Acción</th>
                <td mat-cell *matCellDef="let element">
                  <button mat-icon-button1 [matMenuTriggerFor]="menu">
                    <i class="fa fa-ellipsis-v" aria-hidden="true" style="color: #093070;"></i>
                  </button>
                  <mat-menu #menu="matMenu">
                    <button mat-menu-item class="danger" (click)="eliminar(element);">
                      <span><i class="fa fa-trash"></i> Eliminar</span>
                    </button>
                  </mat-menu>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>
            <mat-paginator #paginator class="mat-paginator-sticky" [pageSizeOptions]="[5, 10]" showFirstLastButtons>
            </mat-paginator>
          </div>
        </div>
      </div>
    </div>
  </mat-card>

  <mat-card class="p-2" style="background-color: #f9f9f9;">
    <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">
      <div class="row">
        <mat-label style="padding-right: 5px;padding-top: 7px;"><u>Solicitudes en</u></mat-label>
        <select formControlName="suarea" style="width: 60%;" required>
          <option [ngValue]="trueBooleano">SU AREA</option>
          <option [ngValue]="falseBooleano">A OTRAS AREAS</option>
        </select>
      </div>

      <div class="row">
        <div class="input-group">
          <mat-label style="padding-right: 44px;padding-top: 7px;"><u>Precio</u>
          </mat-label>
          <input type="number" style="width: 56% !important;" formControlName="codtarifa" min="0"
            [(ngModel)]="cod_precio_venta_modal_codigo" (blur)="onLeavePrecio($event)">
          <div class="input-group-append">
            <button class="btn btn-outline-primary" type="button" (click)="modalPrecioVenta();"
              style="border-color: #093070;padding: 4px 5px 1px 5px;line-height: 0 !important;">
              <i class="fa fa-search"></i>
            </button>
          </div>
          <span></span>
        </div>
      </div>


      <div class="row">
        <!-- <mat-label style="padding-right: 41.5px;padding-top: 7px;"><u>Monto: </u></mat-label> -->
        <mat-slide-toggle (click)="verMonto(true);"> Monto </mat-slide-toggle>
        &nbsp;&nbsp;&nbsp;
        <mat-slide-toggle (click)="verPeso(true);"> Peso </mat-slide-toggle>


        <!-- <mat-label style="padding-right: 7.5px;padding-top: 7px;"><u>Peso: </u></mat-label>
        <input type="checkbox" value="false"> -->
      </div>

      <div class="row">
        <ng-container *ngIf="montoInputView">
          <div class="input-group">
            <mat-label style="padding-right: 41.5px;padding-top: 7px;"><u>Monto: </u></mat-label>
            <input type="number" style="width: 30% !important;" formControlName="monto" min="0">

            <select formControlName="codmoneda" style="width: 30% !important;">
              <option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                {{ monedas.codigo }}
              </option>
            </select>
          </div>
        </ng-container>
      </div>

      <div class="row">
        <ng-container *ngIf="pesoInputView">
          <div class="input-group">
            <mat-label style="padding-right: 47px;padding-top: 7px;"><u>Peso: </u></mat-label>
            <input type="number" style="width: 60% !important;" formControlName="peso" min="0">
          </div>
        </ng-container>
      </div>

      <mat-card-actions>
        <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit"
          (click)="submitData()"><i class="fa fa-plus"></i> Adicionar</button>
      </mat-card-actions>
    </form>
  </mat-card>
</div>
