import { Component, Inject, OnInit } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { ToastrService } from 'ngx-toastr';
@Component({
  selector: 'app-modal-etiqueta',
  templateUrl: './modal-etiqueta.component.html',
  styleUrls: ['./modal-etiqueta.component.scss']
})
export class ModalEtiquetaComponent implements OnInit {

  cod_cliente_proforma: any;
  id_proforma_get: any;
  numero_id_proforma: any;
  tipo_de_pago_proforma: any;
  nombre_cliente_get: any;
  desc_linea_proforma: any;
  id_sol_desct_proforma: any;
  nro_id_sol_desct_proforma: any;

  URL_maps: string;

  input: string;
  cliente_real_proforma: string;

  data: any = [];
  userConn: string;

  constructor(public dialogRef: MatDialogRef<ModalEtiquetaComponent>, private toastr: ToastrService,
    private api: ApiService, public _snackBar: MatSnackBar,
    @Inject(MAT_DIALOG_DATA) public cod_cliente: any, @Inject(MAT_DIALOG_DATA) public id_proforma: any,
    @Inject(MAT_DIALOG_DATA) public numero_id: any, @Inject(MAT_DIALOG_DATA) public nom_cliente: any,
    @Inject(MAT_DIALOG_DATA) public desc_linea: any, @Inject(MAT_DIALOG_DATA) public id_sol_desct: any,
    @Inject(MAT_DIALOG_DATA) public nro_id_sol_desct: any, @Inject(MAT_DIALOG_DATA) public cliente_real: any) {

    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    this.cod_cliente_proforma = cod_cliente.cod_cliente;
    this.id_proforma_get = id_proforma.id_proforma;
    this.numero_id_proforma = numero_id.numero_id;
    this.nombre_cliente_get = nom_cliente.nom_cliente;
    this.desc_linea_proforma = desc_linea.desc_linea;
    this.id_sol_desct_proforma = id_sol_desct.id_sol_desct;
    this.nro_id_sol_desct_proforma = nro_id_sol_desct.nro_id_sol_desct;
    this.cliente_real_proforma = cliente_real.cliente_real;

    console.log(this.cod_cliente_proforma, this.id_proforma_get, this.numero_id_proforma, this.nombre_cliente_get,
      this.desc_linea_proforma, this.id_sol_desct_proforma, this.nro_id_sol_desct_proforma, this.cliente_real_proforma);

    console.log('/venta/transac/veproforma/getDataEtiqueta/' + this.userConn + "/" + this.cliente_real_proforma + "/" + this.id_proforma_get + "/" + this.numero_id_proforma + "/" + this.cod_cliente_proforma + "/" + this.nombre_cliente_get + "/" + "false" + "/0" + "/0");
  }

  ngOnInit() {
    //EL DESCUENTO DE LINEA SEGUN SOLICITUD ESTA DESHABILITADO A LA FECHA QUE SE CREO ESTE COMPONENTE 8-3-2024
    // POR ENDE EL CAMPO  this.desc_linea_proforma, this.id_sol_desct_proforma, this.nro_id_sol_desct_proforma SE LOS
    // ENVIA VALOR FALSE 0, 0 RESPECTIVAMENTE

    this.getDataEtiquetaClientesinDescuento();
  }

  getDataEtiquetaClientesinDescuento() {
    let errorMessage: string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --/venta/transac/veproforma/getAlmacenUser/";
    return this.api.getAll('/venta/transac/veproforma/getDataEtiqueta/' + this.userConn + "/" + this.cliente_real_proforma + "/" + this.id_proforma_get + "/" + this.numero_id_proforma + "/" + this.cod_cliente_proforma + "/" + this.nombre_cliente_get + "/" + "false" + "/0" + "/0")
      .subscribe({
        next: (datav) => {
          this.data = datav;
          console.log('data', this.data);
        },

        error: (err: any) => {
          console.log(err, errorMessage);
        },
        complete: () => {
          this.URL_maps = "https://www.google.com/maps/search/?api=1&query=" + this.data.latitud_entrega + "%2C" + this.data.longitud_entrega;
        }
      })
  }

  submitData() {

  }

  close() {
    this.dialogRef.close();
  }
}
<section style="background-color: #f9f9f9;">
  <div class="container-fluid">
    <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="timer" [fullScreen]="true">
      <br>
      <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
    </ngx-spinner>

    <ng-container>
      <mat-card-header class="mb-1">
        <div class="col-md-9">
          <mat-card-title style="font-size: 0.75rem;"><strong>Etiqueta Proforma</strong></mat-card-title>
        </div>
        <div class="col-md-3 float-right">
          <div class="text-right">
            <mat-icon color="primary" (click)="close();">close</mat-icon>
          </div>
        </div>
      </mat-card-header>

      <div class="container row">
        <div class="col-md-4">
          <span><strong>Proforma:</strong> {{ id_proforma_get }}-{{ numero_id_proforma }}</span>
        </div>
        <div class="col-md-8" style="text-align: end !important;">
          <span>
            <strong>Cliente:</strong> {{ cod_cliente_proforma }}
          </span>&nbsp;|&nbsp;
          <span>
            <strong>Real:</strong> {{ cliente_real_proforma }}
          </span>
        </div>
      </div>

      <mat-tab-group style="background-color: #f9f9f9;">
        <mat-tab label="Etiqueta">
          <mat-card style="background-color: #f9f9f9; border: 1px #093070 solid;">
            <mat-card-content style="padding: 0px 0px 0px 0px !important;">
              <div class="nombre">
                <strong>{{ nombre_cliente_get }}</strong>
              </div>
              <input type="text" [(ngModel)]="input" style="width: 100% !important;">
              <div class="text-center">
                <label>{{ data.representante }}</label>
              </div>
              <div>
                <label>TEL.</label>
                <input type="text" value="{{ data.telefono }}" readonly>
              </div>
              <div>
                <label>CEL.</label>
                <input type="text" value="{{ data.celular }}" readonly>
              </div>

              <div class="text-center">
                <label>{{ data.ciudad }}</label>
              </div>
            </mat-card-content>
          </mat-card>
        </mat-tab>

        <mat-tab label="Teléfonos de Contacto"><br>
          <table>
            <thead>
              <tr>
                <!-- <th>Tipo</th> -->
                <th>Nro. Telefono</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td></td>
                <td>{{ data.telefono }}</td>
              </tr>
            </tbody>
          </table>
        </mat-tab>

        <mat-tab label="Georeferencia"><br>
          <div class="text-center">
            <label>LATITUD: </label>
            <label>{{ data.latitud_entrega }}</label>
          </div>
          <div class="text-center">
            <label>LONGITUD: </label>
            <label>{{ data.longitud_entrega }}</label>
          </div>

          <div class="text-center">
            <a href="{{URL_maps}}" target="_blank"><i class="fa fa-map" aria-hidden="true"></i> VER EN MAPS</a>
          </div>
        </mat-tab>
      </mat-tab-group><br>

      <div class="row float-right" style="margin-bottom: 7px;padding-block-start: 5px !important;">
        <button mat-raised-button class="btn btn-outline-secondary form-control-sm mr-1" (click)="close();">
          <span><i class="fa fa-times" aria-hidden="true" style="color: #ff0707;"></i> Cancelar</span>
        </button>

        <button mat-raised-button class="btn btn-outline-secondary form-control-sm mr-1" (click)="submitData();">
          <span><i class="fa fa-save" aria-hidden="true" style="color: #ffc107;"></i> Grabar</span>
        </button><br>
      </div>
