import { Component, OnInit, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { GruposlineasCreateComponent } from './gruposlineas-create/gruposlineas-create.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { LogService } from '@services/log-service.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';

@Component({
  selector: 'app-gruposlineas',
  templateUrl: './gruposlineas.component.html',
  styleUrls: ['./gruposlineas.component.scss']
})
export class GruposlineasComponent implements OnInit {

  public grupo_linea:any=[];
  public data:any=[];
  userConn:any;

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  displayedColumns = ['codigo','descripcion','fechareg','horareg','usuarioreg','accion'];

  nombre_ventana:string="abmingrupomer.vb";
  public ventana="Grupos de Lineas"
	public detalle="lineasgrupos-detalle";
	public tipo="transaccion-lineasgrupos-DELETE";

  constructor(private api:ApiService, public dialog: MatDialog, private spinner: NgxSpinnerService,
    public _snackBar: MatSnackBar, public log_module: LogService, public nombre_ventana_service: NombreVentanaService) {
    this.mandarNombre();
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }

  ngOnInit() {
    this.getAllGrupoLineas();
  }

  getAllGrupoLineas(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/inventario/mant/ingrupomer/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.grupo_linea = datav;
          // console.log('gruposLineas', datav);
          
          this.dataSource = new MatTableDataSource(this.grupo_linea);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;

          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  openDialog(enterAnimationDuration: string, exitAnimationDuration: string): void {
    this.dialog.open(GruposlineasCreateComponent, {
      width: 'auto',
      height:'auto',
      enterAnimationDuration,
      exitAnimationDuration,
    });
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  seg_adm/mant/adarea/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: 'auto',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean) => {
      if(result) {
        return this.api.delete('/inventario/mant/ingrupomer/'+this.userConn+"/"+element.codigo)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
            this._snackBar.open('Se ha eliminado correctamente!', 'Ok', {
              duration: 3000,
              panelClass: ['coorporativo-snackbar', 'login-snackbar'],
            });

            this.spinner.show();
            setTimeout(() => {
              this.spinner.hide();
            }, 1500);

            location.reload();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        alert("¡No se elimino!");
      }
    });
  }
  
  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<div class="p-2">
  <div class="overlay"></div>
  <div class="p-4 mb-3 mat-elevation-z8">
    <div class="row">
      <div class="col-md text-center">
        <h3>Grupo de Lineas de Items </h3>
      </div>
      <div class="col-sm">
        <button mat-button color="primary" (click)="openDialog('500ms', '700ms')" style="float: right;">
          <i class="fa fa-plus"></i> Grupo de Lineas de Items</button>
      </div>
    </div>
  </div>

  <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
    <br>
    <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
  </ngx-spinner>


  <div class="row">
    <div class="col-lg">
      <div class="team p-2 bg-white scrollmenu">
        <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
          <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>

            <ng-container matColumnDef="codigo">
              <th mat-header-cell *matHeaderCellDef> Codigo </th>
              <td mat-cell *matCellDef="let element"> {{ element.codigo }} </td>
            </ng-container>

            <ng-container matColumnDef="descripcion">
              <th mat-header-cell *matHeaderCellDef> Descripcion</th>
              <td mat-cell *matCellDef="let element"> {{ element.descripcion }} </td>
            </ng-container>

            <ng-container matColumnDef="horareg">
              <th mat-header-cell *matHeaderCellDef> Hora Reg </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.horareg }} </td>
            </ng-container>

            <ng-container matColumnDef="fechareg">
              <th mat-header-cell *matHeaderCellDef> Fecha Reg </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">
                {{ element.fechareg | date:'yyyy/MM/dd' }} </td>
            </ng-container>

            <ng-container matColumnDef="usuarioreg">
              <th mat-header-cell *matHeaderCellDef> Usuario Reg </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.usuarioreg }} </td>
            </ng-container>

            <ng-container matColumnDef="accion">
              <th mat-header-cell *matHeaderCellDef>Acción</th>
              <td mat-cell *matCellDef="let element">
                <button mat-icon-button1 [matMenuTriggerFor]="menu" style="color: #093070;">
                  <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                </button>

                <mat-menu #menu="matMenu">
                  <button mat-menu-item (click)="eliminar(element);">
                    <span><i class="fa fa-trash"></i> Eliminar</span>
                  </button>
                </mat-menu>
              </td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table><br><br>

          <mat-paginator #paginator [pageSizeOptions]="[10, 15, 25]" showFirstLastButtons></mat-paginator><br>
        </div>
      </div>
    </div>
  </div>
</div>
