<div class="p-2">
  <div class="overlay"></div>
  <div class="p-4 mb-3 mat-elevation-z8">
    <div class="row">
      <div class="col-md text-center">
        <h3>Parametros Empresa</h3>
      </div>
      <div class="col-md" *ngIf="empresa">
        <p><strong>Empresa: &nbsp;</strong>{{ empresa.codigo }}</p>
        <p><strong>Descripción: &nbsp;</strong>{{ empresa.descripcion }}</p>
      </div>
    </div>
  </div>

  <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
    <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
  </ngx-spinner>

  <div class="row">
    <div class="col-lg">
      <div class="team p-2 scrollmenu">
        <mat-tab-group dynamicHeight [color]="'primary'">
          <!-- inicio Generales -->
          <mat-tab label="Generales" style="color: white;"><br>
            <div class="content">
              <form class="example-form" [formGroup]="FormularioDataParamEmpresa"
                (ngSubmit)="submitDataUpdateParamEmpresa()">
                <!-- seccionHIDE -->
                <input type="text" formControlName="codempresa" [ngModel]="parametros.codempresa" hidden>
                <input type="text" formControlName="diascomplementarias" [ngModel]="parametros.diascomplementarias"
                  hidden>
                <input type="text" formControlName="maxcomplementarias" [ngModel]="parametros.maxcomplementarias"
                  hidden>
                <!-- seccionEndHIDE -->

                <div class="row">
                  <div class="col-md">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>I.V.A %</mat-label>
                      <input type="number" matInput placeholder="0.00 %" formControlName="iva"
                        [ngModel]="parametros.iva " required>
                    </mat-form-field>
                  </div>
                  <div class="col-md">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>RC-I.V.A %</mat-label>
                      <input type="number" matInput placeholder="0.00 %" formControlName="rciva"
                        [ngModel]="parametros.rciva" ngModel required>
                    </mat-form-field>
                  </div>
                  <div class="col-md">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>I.T %</mat-label>
                      <input type="number" matInput placeholder="0.00 %" formControlName="it" [ngModel]="parametros.it"
                        ngModel required>
                    </mat-form-field>
                  </div>
                </div>
                <div class="row">
                  <div class="col-md">
                    <mat-form-field appearance="fill" appearance="outline">
                      <mat-label>Moneda Base</mat-label>
                      <mat-select matNativeControl [ngModel]="parametros.acmonedaajuste"
                        formControlName="acmonedaajuste">
                        <!-- <mat-option><strong>Valor Actual: </strong>{{ parametros.monedae }}</mat-option> -->
                        <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                          {{ monedas.descripcion }}
                        </mat-option>
                      </mat-select>
                    </mat-form-field>
                  </div>
                  <div class="col-md">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>Frase</mat-label>
                      <input matInput type="text" placeholder="Frase" [ngModel]="parametros.frase"
                        formControlName="frase">
                    </mat-form-field>
                  </div>
                  <div class="col-md">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>Dias Complementarios</mat-label>
                      <input matInput type="number" placeholder="Dias" [ngModel]="parametros.diascomplementarias"
                        formControlName="diascomplementarias">
                    </mat-form-field>
                  </div>
                </div>
                <div class="row">
                  <div class="col-lg">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>Dias de Proceso</mat-label>
                      <input matInput type="number" placeholder="+/- Dias" formControlName="diasproceso"
                        [ngModel]="parametros.diasproceso">
                    </mat-form-field>
                  </div>
                  <div class="col-lg text-center">
                    <!-- <section class="example-section">
                      <mat-checkbox class="example-margin">Activar Cierres Diarios</mat-checkbox>
                    </section> -->
                    <ng-container *ngIf="parametros.cierres_diarios == true">
                      <section class="example-section">
                        <mat-checkbox class="example-margin" formControlName="cierres_diarios" value="true" checked>
                          Activar Cierres Diarios</mat-checkbox>
                      </section>
                    </ng-container>
                    <ng-container *ngIf="parametros.cierres_diarios == false">
                      <section class="example-section">
                        <mat-checkbox class="example-margin" formControlName="cierres_diarios" value="false">Activar
                          Cierres Diarios</mat-checkbox>
                      </section>
                    </ng-container>
                  </div>
                </div><br>

                <!-- segundaParte -->
                <div class="content p-3" style="border-style: groove;">
                  <div class="row">
                    <div class="col-md">
                      <mat-form-field appearance="outline">
                        <mat-label>Impresion Facturas Lineas Superiores</mat-label>
                        <mat-select matNativeControl [ngModel]="parametros.fact_lineas_arriba"
                          formControlName="fact_lineas_arriba">
                          <mat-option><strong>Valor Actual: </strong> {{ parametros.fact_lineas_arriba }}</mat-option>
                          <mat-option value="0">0</mat-option>
                          <mat-option value="1">1</mat-option>
                          <mat-option value="2">2</mat-option>
                          <mat-option value="3">3</mat-option>
                          <mat-option value="4">4</mat-option>
                          <mat-option value="5">5</mat-option>
                          <mat-option value="6">6</mat-option>
                        </mat-select>
                      </mat-form-field>
                    </div>
                    <div class="col-md">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Servidor Opcional</mat-label>
                        <input matInput formControlName="servidor_opcional" [ngModel]="parametros.servidor_opcional">
                      </mat-form-field>
                    </div>
                  </div>

                  <div class="row">
                    <div class="col-md">
                      <mat-form-field appearance="fill" appearance="outline">
                        <mat-label>Inicio de Rutas </mat-label>
                        <input matInput [matDatepicker]="picker" formControlName="fecha_inicio_rutas"
                          [ngModel]="parametros.fecha_inicio_rutas">
                        <mat-datepicker-toggle matIconSuffix [for]="picker"></mat-datepicker-toggle>
                        <mat-datepicker #picker></mat-datepicker>
                      </mat-form-field>
                    </div>
                    <div class="col-md">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>BD Opcional</mat-label>
                        <input matInput type="text" formControlName="bd_opcional" [ngModel]="parametros.bd_opcional">
                      </mat-form-field>
                    </div>
                  </div>

