          <!-- Inicio Planillas I -->
          <mat-tab label="Planillas I"><br>
            <div class="content">
              <form class="example-form" [formGroup]="FormularioDataParamEmpresa"
                (ngSubmit)="submitDataUpdateParamEmpresa()">
                <div class="row">
                  <div class="col-md content">
                    <p><strong>Para Planilla de Haberes:</strong></p>
                    <div>
                      <mat-form-field class="example-full-width" style="width: 75% !important;" appearance="outline">
                        <mat-label>Sueldo Minimo Nacional</mat-label>
                        <input matInput type="number" placeholder="0.00" formControlName="minnacional"
                          [ngModel]="parametros.minnacional">
                      </mat-form-field>
                      <mat-form-field appearance="outline" style="width: 25% !important;padding-left: 10px;">
                        <mat-label>Moneda</mat-label>
                        <mat-select [ngModel]="parametros.monminnal" formControlName="monminnal">
                          <mat-option><strong>Valor Actual: </strong>{{ parametros.monminnal }}</mat-option>
                          <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                            {{ monedas.descripcion }}
                          </mat-option>
                        </mat-select>
                      </mat-form-field>
                    </div>

                    <div>
                      <mat-form-field class="example-full-width" style="width: 75% !important;" appearance="outline">
                        <mat-label>Minimo No Imponible</mat-label>
                        <input matInput type="number" formControlName="minnoimponible"
                          [ngModel]="parametros.minnoimponible" placeholder="0.00">
                      </mat-form-field>
                      <mat-form-field appearance="outline" style="width: 25% !important;padding-left: 10px;">
                        <mat-label>Moneda</mat-label>
                        <mat-select [ngModel]="parametros.monminnoimpo" formControlName="monminnoimpo">
                          <mat-option><strong>Valor Actual: </strong>{{ parametros.monminnal }}</mat-option>
                          <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                            {{ monedas.descripcion }}
                          </mat-option>
                        </mat-select>
                      </mat-form-field>
                    </div>

                    <div>
                      <mat-form-field class="example-full-width" style="width: 75% !important;" appearance="outline">
                        <mat-label>Regimen Complementario</mat-label>
                        <input matInput type="number" formControlName="regcomple" [ngModel]="parametros.regcomple"
                          placeholder="0.00">
                      </mat-form-field>
                      <mat-form-field appearance="outline" style="width: 25% !important;padding-left: 10px;">
                        <mat-label>Moneda</mat-label>
                        <mat-select [ngModel]="parametros.monregcomple" formControlName="monregcomple">
                          <mat-option><strong>Valor Actual: </strong>{{ parametros.monregcomple }}</mat-option>
                          <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                            {{ monedas.descripcion }}
                          </mat-option>
                        </mat-select>
                      </mat-form-field>
                    </div>

                    <div class="col">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>AFP</mat-label>
                        <input matInput type="text" formControlName="afp" [ngModel]="parametros.afp" placeholder="%">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Prima Riesgo Comun</mat-label>
                        <input matInput type="text" formControlName="riesgocomun" [ngModel]="parametros.riesgocomun"
                          placeholder="%">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Aporte Voluntario</mat-label>
                        <input matInput type="text" formControlName="aportevoluntario"
                          [ngModel]="parametros.aportevoluntario" placeholder="%">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Comisión AFP</mat-label>
                        <input matInput type="text" formControlName="comisionafp" [ngModel]="parametros.comisionafp"
                          placeholder="%">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Riesgo Profesional</mat-label>
                        <input matInput type="text" formControlName="riesgoprof" [ngModel]="parametros.riesgoprof"
                          placeholder="%">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>PNSV</mat-label>
                        <input matInput type="text" formControlName="pnsv" [ngModel]="parametros.pnsv" placeholder="%">
                      </mat-form-field>
                    </div>
                  </div>

                  <div class="col-md content">
                    <p><strong>Para Planilla de Haberes:</strong></p>
                    <div class="col">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Factor 4 Sábados</mat-label>
                        <input matInput type="number" formControlName="cuatrosabados"
                          [ngModel]="parametros.cuatrosabados" placeholder="0.00">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Factor 5 Sábados</mat-label>
                        <input matInput type="number" formControlName="cincosabados" [ngModel]="parametros.cincosabados"
                          placeholder="0.00">
                      </mat-form-field>
                    </div>

                    <p><strong>Para Aguinaldos:</strong></p>
                    <div class="col">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Promedio Sueldos</mat-label>
                        <input matInput type="number" formControlName="prommesessueldo"
                          [ngModel]="parametros.prommesessueldo" placeholder="Meses">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Promedio Comisiones</mat-label>
                        <input matInput type="number" formControlName="prommesescomision"
                          [ngModel]="parametros.prommesescomision" placeholder="Meses">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Dias Comerciales</mat-label>
                        <input matInput type="number" formControlName="diascomerciales"
                          [ngModel]="parametros.diascomerciales" placeholder="Dias">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Horas Mes</mat-label>
                        <input matInput type="number" formControlName="horasmes" [ngModel]="parametros.horasmes"
                          placeholder="Hrs.">
                      </mat-form-field>

                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Horas Dias</mat-label>
                        <input matInput type="number" formControlName="horasdia" [ngModel]="parametros.horasdia"
                          placeholder="Hrs.">
                      </mat-form-field>

                      <section class="example-section">
                        <ng-container *ngIf="parametros.valida_salariomin == true">
                          <section class="example-section">
                            <mat-checkbox class="example-margin" formControlName="valida_salariomin" value="true"
                              checked>Valida Salario Minimo Nacional</mat-checkbox>
                          </section>
                        </ng-container>
                        <ng-container *ngIf="parametros.valida_salariomin == false">
                          <section class="example-section">
                            <mat-checkbox class="example-margin" formControlName="valida_salariomin" value="false">
                              Valida Salario Minimo Nacional</mat-checkbox>
                          </section>
                        </ng-container>
                      </section>

                      <section class="example-section">
                        <ng-container *ngIf="parametros.bono_rendimiento_pymes_segun_evaluacion == true">
                          <section class="example-section">
                            <mat-checkbox class="example-margin"
                              formControlName="bono_rendimiento_pymes_segun_evaluacion" value="true" checked>Bono
                              Rendimiento Pymes Segun Evaluación</mat-checkbox>
                          </section>
                        </ng-container>
                        <ng-container *ngIf="parametros.bono_rendimiento_pymes_segun_evaluacion == false">
                          <section class="example-section">
                            <mat-checkbox class="example-margin"
                              formControlName="bono_rendimiento_pymes_segun_evaluacion" value="false">Bono Rendimiento
                              Pymes Segun Evaluación</mat-checkbox>
                          </section>
                        </ng-container>
                      </section>

                      <div class="mat-dialog-actions" mat-dialog-actions>
                        <button mat-raised-button color="primary" type="submit"><i class="fa fa-save"></i>
                          Guardar</button>
                      </div>
                    </div>
                  </div>
                </div>
              </form>
            </div>
          </mat-tab>
          <!-- Fin Planillas I -->

          <!-- Inicio Planillas II -->
          <mat-tab label="Planillas II"><br>
            <form class="example-form" [formGroup]="FormularioDataParamEmpresa"
              (ngSubmit)="submitDataUpdateParamEmpresa()">
              <div class="content">
                <p><strong>Aporte Solidario Nacional: </strong></p>
                <div class="row p-2">
                  <p>a: 1er Rango Total Ganado Mayor a: &nbsp;</p>
                  <input class="input-planillas2" type="number" formControlName="topemonto_asn1"
                    [ngModel]="parametros.topemonto_asn1" placeholder="0.00">
                  <p>Aporte:</p>&nbsp;
                  <input class="input-planillas2" type="number" formControlName="porcentaje_asn1"
                    [ngModel]="parametros.porcentaje_asn1" placeholder="%">%
                </div><br>

                <div class="row p-2">
                  <p>a: 2do Rango Total Ganado Mayor a: &nbsp;</p>
                  <input class="input-planillas2" type="number" formControlName="topemonto_asn2"
                    [ngModel]="parametros.topemonto_asn2" placeholder="0.00">
                  <p>Aporte:</p>&nbsp;
                  <input class="input-planillas2" type="number" formControlName="porcentaje_asn2"
                    [ngModel]="parametros.porcentaje_asn2" placeholder="%"> %
                </div><br>

                <div class="row p-2">
                  <p>a: 3er Rango Total Ganado Mayor a: &nbsp;</p>
                  <input class="input-planillas2" type="number" formControlName="topemonto_asn3"
                    [ngModel]="parametros.topemonto_asn3" placeholder="0.00">
                  <p>Aporte:</p>&nbsp;
                  <input class="input-planillas2" type="number" formControlName="porcentaje_asn3"
                    [ngModel]="parametros.porcentaje_asn3" placeholder="%">%
                </div><br>
              </div>

              <div class="mat-dialog-actions" mat-dialog-actions>
                <button mat-raised-button color="primary" type="submit"><i class="fa fa-save"></i> Guardar</button>
              </div>
            </form>
          </mat-tab>
          <!-- Fin Planillas I -->

          <!-- Inicio Inventarios -->
          <mat-tab label="Inventarios"><br>
            <div class="content">
              <form class="example-form" [formGroup]="FormularioDataParamEmpresa"
                (ngSubmit)="submitDataUpdateParamEmpresa()">
                <div class="row">
                  <div class="col">
                    <mat-form-field appearance="fill" appearance="outline">
                      <mat-label>Precio Impresión Ajustes</mat-label>
                      <mat-select [ngModel]="parametros.codtarifaajuste" formControlName="codtarifaajuste">
                        <mat-option><strong>Valor Actual: </strong>{{ parametros.codtarifaajuste }}</mat-option>
                        <mat-option *ngFor="let intarifas of intarifa" [value]="intarifas.codigo">
                          {{ intarifas.codigo }} - {{ intarifas.descripcion }}
                        </mat-option>
                      </mat-select>
                    </mat-form-field>
                  </div>
                  <div class="col">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>Nombre Caracteristica Principal</mat-label>
                      <input matInput type="text" [ngModel]="parametros.ctsc1_descripcion"
                        formControlName="ctsc1_descripcion">
                    </mat-form-field>
                  </div>
                </div>
                <div class="row">
                  <div class="col">
                    <mat-form-field class="example-full-width" appearance="outline">
                      <mat-label>Nombre Caracteristica Secundaria</mat-label>
                      <input matInput type="text" [ngModel]="parametros.ctsc2_descripcion"
                        formControlName="ctsc2_descripcion">
                    </mat-form-field>
                  </div>
                  <div class="col">
                    <mat-form-field appearance="fill" appearance="outline">
                      <mat-label>Moneda de Costeos</mat-label>
                      <mat-select [ngModel]="parametros.codmoneda_costeo" formControlName="codmoneda_costeo">
                        <mat-option><strong>Valor Actual: </strong>{{ parametros.codmoneda_costeo }}</mat-option>
                        <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                          {{ monedas.descripcion }}
                        </mat-option>
                      </mat-select>
                    </mat-form-field>
                  </div>
                </div>

                <div class="row">
                  <div class="col">
                    <ng-container *ngIf="parametros.negativos == true">
                      <section class="example-section">
                        <mat-checkbox class="example-margin" formControlName="negativos" value="true" checked>Permitir
                          Saldos Negativos</mat-checkbox>
                      </section>
                    </ng-container>
                    <ng-container *ngIf="parametros.negativos == false">
                      <section class="example-section">
                        <mat-checkbox class="example-margin" formControlName="negativos" value="false">Permitir Saldos
                          Negativos</mat-checkbox>
                      </section>
                    </ng-container>
                  </div>
                  <div class="col">
                    <section class="example-section">
                      <ng-container *ngIf="parametros.stock_seguridad == true">
                        <section class="example-section">
                          <mat-checkbox class="example-margin" formControlName="stock_seguridad" value="true" checked>
                            Controlar Stock de Reserva</mat-checkbox>
                        </section>
                      </ng-container>
                      <ng-container *ngIf="parametros.stock_seguridad == false">
                        <section class="example-section">
                          <mat-checkbox class="example-margin" formControlName="stock_seguridad" value="false">Controlar
                            Stock de Reserva</mat-checkbox>
                        </section>
                      </ng-container>
                    </section>
                  </div>
                </div>

                <div class="mat-dialog-actions" mat-dialog-actions>
                  <button mat-raised-button color="primary" type="submit"><i class="fa fa-save"></i> Guardar</button>
                </div>
              </form>
            </div>
          </mat-tab>
          <!-- Fin Inventarios -->

          <!-- Inicio Contabilidad -->
          <mat-tab label="Contabilidad"><br>
            <div class="col-md content">
