                      <mat-label>Cuenta Debito Fiscal</mat-label>
                      <mat-select matNativeControl [ngModel]="parametros.ctadebfiscal" formControlName="ctadebfiscal">
                        <mat-option *ngFor="let cncuentas of cncuenta" [value]="cncuentas.codigo">
                          {{ cncuentas.codigo }} - {{ cncuentas.descripcion }}
                        </mat-option>
                      </mat-select>
                    </mat-form-field>

