                        <mat-option *ngFor="let cncuentas of cncuenta" [value]="cncuentas.codigo">
                          {{ cncuentas.codigo }} - {{ cncuentas.descripcion }}
                        </mat-option>
                      </mat-select>
                    </mat-form-field>

                    <mat-form-field appearance="outline">
                      <mat-label>Cuenta de Ajuste MN</mat-label>
                      <mat-select matNativeControl [ngModel]="parametros.codcuentaajustesmn"
                        formControlName="codcuentaajustesmn">
                        <mat-option *ngFor="let cncuentas of cncuenta" [value]="cncuentas.codigo">
                          {{ cncuentas.codigo }} - {{ cncuentas.descripcion }}
                        </mat-option>
                      </mat-select>
                    </mat-form-field>

                    <mat-form-field appearance="outline">
                      <mat-label>Cuenta de Utilidad</mat-label>
                      <mat-select matNativeControl [ngModel]="parametros.codctautilidad"
