                    <mat-form-field appearance="outline">
                      <mat-label>Moneda Extranjera</mat-label>
                      <mat-select [ngModel]="parametros.monedae" formControlName="monedae" required>
                        <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                          {{ monedas.descripcion }}
                        </mat-option>
                      </mat-select>
                    </mat-form-field>
                    <div class="row">
                      <div class="col-md">
                        <mat-form-field class="example-full-width" appearance="outline">
                          <mat-label>Resp.Libro de Compras C.I</mat-label>
                          <input matInput type="text" placeholder="Resp.Libro de Compras C.I"
                            [ngModel]="parametros.resp_lc_ci" formControlName="resp_lc_ci">
                        </mat-form-field>
                      </div>
                      <div class="col-md">
                        <mat-form-field class="example-full-width" appearance="outline">
                          <mat-label>Nombre</mat-label>
                          <input matInput type="text" placeholder="Nombre" [ngModel]="parametros.resp_lc_nombre"
                            formControlName="resp_lc_nombre">
                        </mat-form-field>
                      </div>
                    </div>
                    <div class="row">
                      <div class="col-md">
                        <mat-form-field class="example-full-width" appearance="outline">
                          <mat-label>Resp.Libro de Ventas C.I</mat-label>
                          <input matInput type="text" placeholder="Resp.Libro de Ventas C.I"
                            [ngModel]="parametros.resp_lv_ci" formControlName="resp_lv_ci">
                        </mat-form-field>
                      </div>
                      <div class="col-md">
                        <mat-form-field class="example-full-width" appearance="outline">
                          <mat-label>Nombre</mat-label>
                          <input matInput type="text" placeholder="Nombre" [ngModel]="parametros.resp_lv_nombre"
                            formControlName="resp_lv_nombre">
                        </mat-form-field>
                      </div>
                    </div>
                    <div class="col-md">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Ciudad para Reportes</mat-label>
                        <input matInput type="text" placeholder="Ciudad" [ngModel]="parametros.ciudad_reportes_conta"
                          formControlName="ciudad_reportes_conta">
                      </mat-form-field>
                    </div>
                    <div class="col-md">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Monto R.N.D 10.001011</mat-label>
                        <input matInput type="text" placeholder="en MN" [ngModel]="parametros.monto_rnd100011"
                          formControlName="monto_rnd100011">
                      </mat-form-field>
                    </div>
                    <div class="col-md">
                      <mat-form-field class="example-full-width" appearance="outline">
                        <mat-label>Monto en MN Limite Ventas Facturas Sin Nombre y Sin NIT</mat-label>
