import { Component } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { ApiService } from '@services/api.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { EmpresaCreateComponent } from './empresa-create/empresa-create.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { LogService } from '@services/log-service.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
@Component({
  selector: 'app-empresa',
  templateUrl: './empresa.component.html',
  styleUrls: ['./empresa.component.scss']
})
export class EmpresaComponent {

  empresa:any=[];
  panelOpenState = false;
  userConn;
  
  nombre_ventana: string = "abmadempresa.vb";
  public ventana="Empresa"
  public detalle="empresa-detalle";
  public tipo="transaccion-empresa-DELETE";

  constructor(private api: ApiService, public dialog: MatDialog, private spinner: NgxSpinnerService, public log_module: LogService,
    public nombre_ventana_service:NombreVentanaService) {
    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }

  ngOnInit(): void {
    this.mandarNombre();
    this.getAllEmpresa();
  }

  openDialog(): void {
    this.dialog.open(EmpresaCreateComponent, {
      width: '1020px',
      height:'auto',
    });
  }

  getAllEmpresa(){
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET -/seg_adm/mant/adempresa/";
    return this.api.getAll('/seg_adm/mant/adempresa/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.empresa = datav;
          console.log(this.empresa);
          
          
          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  eliminar(element):void{
    let user_conn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  /moneda Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: '350px',
      data:{dataUsuarioEdit:element},
    });
    
    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/seg_adm/mant/adempresa/'+user_conn+'/'+element)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
            this.spinner.show();
            setTimeout(() => {
              this.spinner.hide();
            }, 1500);

            location.reload();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        alert("¡No se elimino!");
      }
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<div class="p-2">
  <div class="overlay"></div>
  <div class="p-4 mb-3 mat-elevation-z8">
    <div class="row">
      <div class="col-md-8 text-center">
        <h3>Empresas</h3>
      </div>
      <div class="col-md-4">
        <ng-container *ngIf="!empresa">
          <button mat-button color="primary" (click)="openDialog()" style="float: right;">
            <i class="fa fa-plus"></i> Crear Empresa
          </button>
        </ng-container>
      </div>
    </div>
  </div>

  <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
    <br>
    <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
  </ngx-spinner>

  <div class="row">
    <div class="col-lg">
      <div class="team p-2 bg-white scrollmenu">
        <div class="col-lg-12">
          <mat-accordion *ngFor="let empresas of empresa">
            <mat-expansion-panel (opened)="panelOpenState = true" (closed)="panelOpenState = false">
              <mat-expansion-panel-header>
                <mat-panel-title>
                  <strong>Empresa:</strong>&nbsp; {{ empresas.codigo }} - {{ empresas.codalmacen }}&nbsp;
                  {{ empresas.descripcion }} <br>
                  <strong>Dirección:</strong>&nbsp; {{ empresas.direccion }}
                </mat-panel-title>
              </mat-expansion-panel-header>
              <div class="row">
                <div class="col-md-4">
                  <p><strong>Actividad: </strong>&nbsp; {{ empresas.actividad }}</p>
                </div>
                <div class="col-md-4">
                  <p><strong>Moneda: </strong>&nbsp; {{ empresas.moneda }}</p>
                </div>
                <div class="col-md-4">
                  <p><strong>Municipio:</strong>&nbsp; {{ empresas.municipio }}</p>
                </div>
              </div>
              <div class="row">
                <div class="col-md-4">
                  <p><strong>NIT: </strong>&nbsp; {{ empresas.nit }}</p>
                </div>
                <div class="col-md-4">
                  <p><strong>Plan Cuenta: </strong>&nbsp; {{ empresas.plancuenta }}</p>
                </div>
                <div class="col-md-4">
                  <p><strong>Fecha Registrado: </strong>&nbsp;{{ empresas.fechareg | date:'yyyy/MM/dd' }}</p>
                </div>
              </div>

              <div class="row">
                <div class="col-sm-6">
                  <p><strong>Acciones</strong></p>
                  <mat-button-toggle-group name="favoriteColor" aria-label="Favorite Color">
                    <button mat-raised-button class="btn btn-danger" color="warn" (click)="eliminar(empresas.codigo);">
                      <i class="fa fa-trash"></i> Eliminar</button>
                  </mat-button-toggle-group>
                </div>
              </div>
            </mat-expansion-panel>
          </mat-accordion>
        </div>
      </div>
    </div>
  </div>
</div>
