                  <input matInput type="number" class="pedido" placeholder="{{ element.codtarifa }}" min="0">
                </td>
              </ng-container>

              <!-- DE Column -->
              <ng-container matColumnDef="de">
                <th mat-header-cell *matHeaderCellDef style="width: 5%;text-align: center;"> DE </th>
