import { DatePipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-rosca-create',
  templateUrl: './rosca-create.component.html',
  styleUrls: ['./rosca-create.component.scss']
})
export class RoscaCreateComponent implements OnInit {
 
  FormularioData:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  dataform:any='';
  rosca:any=[];
  userConn:any;
  usuarioLogueado:any;

  public ventana="rosca-create"
  public detalle="rosca-detalle";
  public tipo="transaccion-rosca-POST";

  constructor(private _formBuilder: FormBuilder,  private datePipe: DatePipe,  private spinner: NgxSpinnerService,
    private api:ApiService, public dialogRef: MatDialogRef<RoscaCreateComponent>, public _snackBar: MatSnackBar,
    public log_module:LogService,private toastr: ToastrService,){
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.FormularioData = this.createForm();
  }

  ngOnInit(): void {
  }

  createForm(): FormGroup {
    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      codigo: [this.dataform.codigo, Validators.compose([Validators.required])],
      descripcion: [this.dataform.descripcion, Validators.compose([Validators.required])],

      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      horareg: [hora_actual_complete],
      usuarioreg: [this.usuarioLogueado],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  /inventario/mant/inrosca POST";
    return this.api.create("/inventario/mant/inrosca/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.rosca = datav;

          console.log('data', datav);
          this.onNoClick();
          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this.toastr.success('! SE GUARDO EXITOSAMENTE !');
          this._snackBar.open('Se Guardo Correctamente!', 'Ok', {
            duration: 3000,
          });

          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 2000);
          
          location.reload();
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
          this.toastr.error('! NO SE GUARDO !');

        },
        complete: () => { }
      })
  }

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<div class="p-2">
  <h1 mat-dialog-title>Crear Rosca</h1>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div>
</div>

<div mat-dialog-content>
  <div class="container">
    <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">
      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field appearance="outline" class="example-full-width">
            <mat-label>Codigo </mat-label>
            <input matInput type="text" formControlName="codigo" maxlength="6" required>
          </mat-form-field>
        </div>
        <div class="col-md">
          <mat-form-field appearance="outline" class="example-full-width">
            <mat-label>Descripcion </mat-label>
            <input type="text" matInput formControlName="descripcion" maxlength="50" required>
          </mat-form-field>
        </div>
      </div>

      <div class="mat-dialog-actions" mat-dialog-actions>
        <a mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</a>

        <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit"><i
            class="fa fa-save"></i> Guardar</button>
      </div>
    </form>

  </div>
</div>
