import { Component, OnInit, ViewChild } from '@angular/core';
import { FormControl } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { fncuenta } from '@services/modelos/objetos';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';
import { Observable, map, startWith } from 'rxjs';
import { CuentasEfectivoCreateComponent } from './cuentas-efectivo-create/cuentas-efectivo-create.component';
import { CuentasEfectivosEditComponent } from './cuentas-efectivos-edit/cuentas-efectivos-edit.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { CuentaEfectivoCuentasContablesComponent } from './cuenta-efectivo-cuentas-contables/cuenta-efectivo-cuentas-contables.component';

@Component({
  selector: 'app-cuentas-efectivo',
  templateUrl: './cuentas-efectivo.component.html',
  styleUrls: ['./cuentas-efectivo.component.scss']
})
export class CuentasEfectivoComponent implements OnInit {

  cuentas_efectivo:any=[]; 
  data:[];
  datacuentasefectivoEdit: any = [];

  displayedColumns = ['id', 'descripcion', 'balance', 'fecha', 'tipo_movimiento', 'accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;  

  myControl = new FormControl<string | fncuenta>('');
  options: fncuenta[] = [];
  filteredOptions: Observable<fncuenta[]>;
  userConn:any;

  nombre_ventana:string="abmfncuenta.vb";
  public ventana="Cuentas Efectivo"
  public detalle="uentas Efectivo-delete";
  public tipo="uentas Efectivo-DELETE";


  constructor(private api:ApiService,public dialog: MatDialog, private spinner: NgxSpinnerService,
    public log_module:LogService, private toastr: ToastrService, public nombre_ventana_service:NombreVentanaService){
    this.mandarNombre();
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;    
    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
    this.getAllCuentas();
  }

  ngOnInit(): void {
    this.filteredOptions = this.myControl.valueChanges.pipe(
      startWith(''),
      map(value => {
        const name = typeof value === 'string' ? value : value?.id;
        return name ? this._filter(name as string) : this.options.slice();
      }),
    );
  }

  getAllCuentas(){
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET -/seg_adm/mant/admoneda/";
    return this.api.getAll('/fondos/mant/fncuenta/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.cuentas_efectivo = datav;
          console.log(this.cuentas_efectivo);

          this.dataSource = new MatTableDataSource(this.cuentas_efectivo);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  openDialog(): void {
    this.dialog.open(CuentasEfectivoCreateComponent, {
      width: 'auto',
      height:'auto',
    });
  }

  private _filter(name: string): fncuenta[] {
    const filterValue = name.toLowerCase();

    return this.options.filter(option => option.id.toLowerCase().includes(filterValue));
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
  }

  displayFn(user: fncuenta): string {
    return user && user.id ? user.id : '';
  }

  editar(datanumChecCliEdit){
    this.datacuentasefectivoEdit ={...datanumChecCliEdit};
    console.log(this.datacuentasefectivoEdit);

    this.data = datanumChecCliEdit;
    this.dialog.open(CuentasEfectivosEditComponent, {
      data: {datacuentasefectivoEdit:this.datacuentasefectivoEdit},
      width: 'auto',
      height:'auto',
    });
  }

  cuentasContablesModal(datanumChecCliEdit) {
    this.datacuentasefectivoEdit ={...datanumChecCliEdit};
    console.log(this.datacuentasefectivoEdit);

    this.data = datanumChecCliEdit;
    this.dialog.open(CuentaEfectivoCuentasContablesComponent, {
      data: {datacuentasefectivoEdit:this.datacuentasefectivoEdit},
      width: 'auto',
      height:'auto',
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  fondos/mant/fnnumeracioncheque_cliente/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: 'auto',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/fondos/mant/fncuenta/'+this.userConn+"/"+ element.id)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);

            this.toastr.success('!ELIMINADO EXITOSAMENTE!');
            location.reload();
          },
          error: (err: any) => { 
            console.log(err, errorMessage);
            this.toastr.error('! NO ELIMINADO !');
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! CANCELADO !');
      }
    });
  }

}
<section class="content">
  <div class="container-fluid">
    <div class="overlay"></div>
    <div class="mb-3 mat-elevation-z8">
      <div class="row headTittle">
        <div class="col-lg">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label> Buscador Cuentas Efectivo </mat-label>
            <input type="text" matInput [formControl]="myControl" (keyup)="applyFilter($event)" placeholder="Codigo"
              #input>
          </mat-form-field>
        </div>
        <div class="col-md text-center">
          <h3>Cuentas Efectivo</h3>
        </div>
        <div class="col-sm">
          <button mat-button color="primary" (click)="openDialog()" style="float: right;">
            <i class="fa fa-plus"></i> Crear Cuentas Efectivo</button>
        </div>

        <div class="col-md-1 text-right">
          <mat-icon color="primary">arrow_drop_down</mat-icon>
          <mat-icon color="primary">close</mat-icon>
        </div>
      </div>
    </div>

    <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
      <br>
      <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
    </ngx-spinner>

    <div class="row">
      <div class="col-lg">
        <div class="team p-2 bg-white scrollmenu">
          <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8">
              <ng-container matColumnDef="id">
                <th mat-header-cell *matHeaderCellDef> Código</th>
                <td mat-cell *matCellDef="let element"> {{ element.id }} </td>
              </ng-container>

              <ng-container matColumnDef="descripcion">
                <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Descripción</th>
                <td mat-cell *matCellDef="let element" style="text-align: center;"> {{ element.descripcion }} </td>
              </ng-container>

              <ng-container matColumnDef="balance">
                <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Balance </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">
                  {{ element.balance | number: '1.2-2'}} </td>
              </ng-container>

              <ng-container matColumnDef="fecha">
                <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Fecha Balance</th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">
                  {{ element.fecha | date:'yyyy/MM/dd'}}
                </td>
              </ng-container>

              <ng-container matColumnDef="tipo_movimiento">
                <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Tipo Movimiento</th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.tipo_movimiento }}
                </td>
              </ng-container>

              <ng-container matColumnDef="accion">
                <th mat-header-cell *matHeaderCellDef>Acción</th>
                <td mat-cell *matCellDef="let element">
                  <button mat-icon-button1 [matMenuTriggerFor]="menu">
                    <i class="fa fa-ellipsis-v" aria-hidden="true" style="color: #093070;"></i>
                  </button>
                  <mat-menu #menu="matMenu">
                    <button mat-menu-item (click)="cuentasContablesModal(element);">
                      <span><i class="fa fa-cogs"></i> Cuentas Contables</span>
                    </button>
                    <button mat-menu-item (click)="editar(element);">
                      <span><i class="fa fa-pen"></i> Editar</span>
                    </button>
                    <button mat-menu-item (click)="eliminar(element);">
                      <span><i class="fa fa-trash"></i> Eliminar</span>
                    </button>
                  </mat-menu>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>
            <mat-paginator #paginator [pageSizeOptions]="[15, 20, 30]" showFirstLastButtons></mat-paginator>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
