import { Component, OnInit, ViewChild } from '@angular/core';
import { ConceptosmovimientosmercaderiaCreateComponent } from './conceptosmovimientosmercaderia-create/conceptosmovimientosmercaderia-create.component';
import { ApiService } from '@services/api.service';
import { MatDialog } from '@angular/material/dialog';
import { NgxSpinnerService } from 'ngx-spinner';
import { MatSnackBar } from '@angular/material/snack-bar';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
import { MatTableDataSource } from '@angular/material/table';
import { MatPaginator } from '@angular/material/paginator';
import { FormControl } from '@angular/forms';
import { Observable } from 'rxjs';
import { inConcepto } from '@services/modelos/objetos';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { LogService } from '@services/log-service.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-conceptosmovimientosmercaderia',
  templateUrl: './conceptosmovimientosmercaderia.component.html',
  styleUrls: ['./conceptosmovimientosmercaderia.component.scss']
})
export class ConceptosmovimientosmercaderiaComponent implements OnInit {

  concepto:any=[];
  data:any=[];
  userConn:any;

  displayedColumns = ['codigo','descripcion','factor','traspaso','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;
  myControl = new FormControl<string | inConcepto>('');
  filteredOptions: Observable<inConcepto[]>;

  nombre_ventana: string = "abminconcepto.vb";
  public ventana="Numeración de Concepto de Notas de Movimiento de Mercaderia"
  public detalle="numConceptoNotasMovimiento-create";
  public tipo="Numeración de Concepto de Notas de Movimiento de Mercaderia-CREATE";

  constructor(private api:ApiService, public dialog: MatDialog, private spinner: NgxSpinnerService, public log_module: LogService,
    public _snackBar: MatSnackBar, public nombre_ventana_service: NombreVentanaService, private toastr: ToastrService,) {
    
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
    this.getAllConcepto();
    this.mandarNombre();
  }


  ngOnInit(){
  }

  getAllConcepto(){
    let errorMessage = "La Ruta presenta fallos al hacer peticion GET --/inventario/mant/inconcepto/";
    return this.api.getAll('/inventario/mant/inconcepto/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.concepto = datav;
          console.log('data', datav);

          this.dataSource = new MatTableDataSource(this.concepto);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;

          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 1500);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  openDialog(){
    this.dialog.open(ConceptosmovimientosmercaderiaCreateComponent, {
      width: 'auto',
      height:'auto'
    });
  }

  editar(item){
    this.dialog.open(ConceptosmovimientosmercaderiaCreateComponent, {
      width: 'auto',
      height:'auto'
    });
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  fondos/mant/fntiporetiro/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: 'auto',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/inventario/mant/inconcepto/'+this.userConn+"/"+ element.id)
        .subscribe({
          next: () => {
            this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
            
            this.toastr.success('!ELIMINADO EXITOSAMENTE!');
            location.reload();
          },
          error: (err: any) => { 
            console.log(err, errorMessage);
            this.toastr.error('! NO ELIMINADO !');
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! CANCELADO !');
      }
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<section class="content">
  <div class="container-fluid">
    <div class="overlay"></div>
    <div class=" mb-3 mat-elevation-z8">
      <div class="row he">
        <div class="col-md headTittle">
          <h3>Numeración de Concepto de Notas de Movimiento de Mercaderia</h3>
        </div>
        <div class="col-md headTittle">
          <button mat-button color="primary" (click)="openDialog()" style="float: right;">
            <i class="fa fa-plus"></i> Crear Numeración</button>
        </div>
      </div>
    </div>

    <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
      <br>
      <p style="font-size: 25px; color: rgb(9,48,112,)">🔩 Cargando... 🔩</p>
    </ngx-spinner>

    <div class="row">
      <div class="col-lg">
        <div class="team p-2 bg-white scrollmenu">
          <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
              <ng-container matColumnDef="codigo">
                <th mat-header-cell *matHeaderCellDef mat-sort-header> Codigo </th>
                <td mat-cell *matCellDef="let element"> {{ element.codigo }} </td>
              </ng-container>

              <ng-container matColumnDef="descripcion">
                <th mat-header-cell *matHeaderCellDef> Descripcion</th>
                <td mat-cell *matCellDef="let element"> {{ element.descripcion }} </td>
              </ng-container>

              <ng-container matColumnDef="factor">
                <th mat-header-cell *matHeaderCellDef> Factor</th>
                <td mat-cell *matCellDef="let element">{{ element.factor }}</td>
              </ng-container>

              <ng-container matColumnDef="traspaso">
                <th mat-header-cell *matHeaderCellDef> Traspaso </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">
                  <input type="checkbox" [checked]="element.traspaso" disabled>
                </td>
              </ng-container>

              <ng-container matColumnDef="accion">
                <th mat-header-cell *matHeaderCellDef>Acción</th>
                <td mat-cell *matCellDef="let element">
                  <button mat-icon-button1 [matMenuTriggerFor]="menu">
                    <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                  </button>

                  <mat-menu #menu="matMenu">
                    <button mat-menu-item (click)="editar(element);">
                      <span><i class="fa fa-pen"></i> Editar</span>
                    </button>

                    <button mat-menu-item (click)="eliminar(element);">
                      <span><i class="fa fa-trash"></i> Eliminar</span>
                    </button>
                  </mat-menu>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>
            <mat-paginator #paginator [pageSizeOptions]="[15, 25, 35]" showFirstLastButtons></mat-paginator>
          </div>
        </div>
      </div>
    </div>
  </div>
</section>
