import { DatePipe } from '@angular/common';
import { Component, Inject, OnInit, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { Router } from '@angular/router';
import { ServicioprecioventaService } from '@components/mantenimiento/ventas/servicioprecioventa/servicioprecioventa.service';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';
import { DeudoresCatalogoComponent } from '../../deudores/deudores-catalogo/deudores-catalogo.component';
import { DeudorCatalogoService } from '../../deudores/deudor-servicio/deudor-catalogo.service';

@Component({
  selector: 'app-deudores-integrantes',
  templateUrl: './deudores-integrantes.component.html',
  styleUrls: ['./deudores-integrantes.component.scss']
})
export class DeudoresIntegrantesComponent implements OnInit {

  FormularioData: FormGroup;  
  fecha_actual = new Date();
  hora_actual = new Date();
  userConn: string; 
  usuario: string; 
  usuario_logueado: string;
  cod_precio_venta_modal_codigo: string;

  dataform: any = '';
  deudores_cat: any = [];
  deudores: any = [];
  deudor_edit: any = [];
  miembro_save: any = [];
  deudor_catalogo: any = [];
  deudor_catalogo_id: any;
  deudor_edit_codigo: any;
  inputValue: number | null = null;

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  displayedColumns = ['iddeudor_compuesto', 'porcentaje', 'accion'];

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  constructor(public dialog: MatDialog, private api: ApiService, private spinner: NgxSpinnerService, private toastr: ToastrService,
    public nombre_ventana_service: NombreVentanaService, public log_module: LogService, public router: Router,
    @Inject(MAT_DIALOG_DATA) public dataIntegrantes: any, private _formBuilder: FormBuilder, private datePipe: DatePipe,
    private servicioDeudor: DeudorCatalogoService, public dialogRef: MatDialogRef<DeudoresIntegrantesComponent>) {
    this.FormularioData = this.createForm();

    this.deudor_edit = dataIntegrantes.dataIntegrantes;
    this.deudor_edit_codigo = dataIntegrantes.dataIntegrantes.id;
    console.log(this.deudor_edit);


    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.usuario = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.getDeudorCatalogo();
    this.cargarTablaMiembrosDeudor();    
  }

  ngOnInit() {
    // precio_venta
    this.servicioDeudor.disparadorDeDeudor.subscribe(data => {
      console.log("Recibiendo Deudor: ", data);
      this.deudor_catalogo = data.deudor;
      this.deudor_catalogo_id = data.deudor.id;
    });
    // fin_precio_venta
  }

  cargarTablaMiembrosDeudor() {
    let errorMessage = "La Ruta presenta fallos al hacer peticion GET --/fondos/mant/fndeudor_compuesto1/"
    return this.api.getAll('/fondos/mant/fndeudor_compuesto1/'+this.userConn+"/"+this.deudor_edit_codigo)
      .subscribe({
        next: (datav) => {
          this.deudores = datav;
          console.log(this.deudores);

          this.dataSource = new MatTableDataSource(this.deudores);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },

        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getDeudorCatalogo(){
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET -/seg_adm/mant/admoneda/";
    return this.api.getAll('/fondos/mant/fndeudor/catalogo/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.deudores_cat = datav;
        },
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  createForm(): FormGroup {
    return this._formBuilder.group({
      iddeudor: [this.dataform.iddeudor],
      iddeudor_compuesto: [this.dataform.iddeudor_compuesto],
      porcentaje: [this.dataform.porcentaje, Validators.pattern(/^-?\d+$/)],
    });
  }

  submitData(){
    let data = this.FormularioData.value;    
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:-- /fondos/mant/fndeudor_compuesto1/";
    return this.api.create("/fondos/mant/fndeudor_compuesto1/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.miembro_save = datav;
          console.log(this.miembro_save);

          this.toastr.success('Guardado con Exito! üéâ');
          this.cargarTablaMiembrosDeudor();
        },

        error: (err) => { 
          console.log(err, errorMessage);
          // this.toastr.error('! NO SE GUARDO !');
        },
        complete: () => { }
      })
  }

  onLeaveDeudor(event: any){
    const inputValue = event.target.value;

    // Verificar si el valor ingresado est√° presente en los objetos del array
    const encontrado = this.deudores_cat.some(objeto => objeto.id === inputValue);

    if(!encontrado){
      // Si el valor no est√° en el array, dejar el campo vac√≠o
      event.target.value = '';
      console.log("NO ENCONTRADO INPUT");
    } else {
      event.target.value = inputValue;
    }

    // Puedes realizar otras acciones seg√∫n tus necesidades
    console.log('Input perdi√≥ el foco', inputValue);
  }

  modalDeudores() { 
    this.dialog.open(DeudoresCatalogoComponent, {
      width: 'auto',
      height:'auto',
    });
  }

  onInputChange(value: string) {
    // Validar y formatear el valor ingresado
    const parsedValue = parseFloat(value);

    if (!isNaN(parsedValue) && Number.isInteger(parsedValue)) {
      this.inputValue = parsedValue;
    } else {
      this.inputValue = null;
    }
  }

  eliminar(element) {
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:-- /venta/mant/insolurgente_parametros/ Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: 'auto',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/fondos/mant/fndeudor_compuesto1/'+this.userConn+"/"+this.deudor_edit_codigo+"/"+element.iddeudor)
        .subscribe({
          next: () => {
            // this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);

            this.toastr.success('!ELIMINADO EXITOSAMENTE!');
            this.cargarTablaMiembrosDeudor();
            // location.reload();
          },
          error: (err: any) => { 
            console.log(err, errorMessage);
            this.toastr.error('! NO ELIMINADO !');
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! CANCELADO !');
      }
    });
  }

  close(){
    this.dialogRef.close();
  }
}
<div class="container-fluid p-2">
  <mat-card style="background-color: #f9f9f9;">
    <div class="row">
      <div class="col-md text-left headTittle">
        <h3><strong>Miembros de Deudor Compuesto</strong></h3>
      </div>
      <div class="col-md text-right headTittle">
        <mat-icon color="primary" (click)="close();">close</mat-icon>
      </div>
    </div>

    <div class="pl-2">
      <span><strong><u>Codigo: </u></strong>&nbsp; {{ deudor_edit_codigo }}</span> &nbsp;
      <span><strong><u>Descripci√≥n: </u></strong>&nbsp; {{ deudor_edit.descripcion }}</span> &nbsp;
    </div>

    <div class="row">
      <div class="col-lg">
        <div class="team bg-white scrollmenu">
          <div class="col-lg-12">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8">

              <ng-container matColumnDef="iddeudor_compuesto">
                <th mat-header-cell *matHeaderCellDef class="th2"> Deudor </th>
                <td mat-cell *matCellDef="let element">{{ element.iddeudor }}</td>
              </ng-container>

              <ng-container matColumnDef="porcentaje">
                <th mat-header-cell *matHeaderCellDef class="th2"> % A Distribuir </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.porcentaje }}</td>
              </ng-container>

              <ng-container matColumnDef="accion">
                <th mat-header-cell *matHeaderCellDef style="color: white !important;text-align: center">Acci√≥n</th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">
                  <button mat-icon-button1 [matMenuTriggerFor]="menu">
                    <i class="fa fa-ellipsis-v" aria-hidden="true" style="color: #093070;"></i>
                  </button>
                  <mat-menu #menu="matMenu">
                    <button mat-menu-item class="danger" (click)="eliminar(element);">
                      <span><i class="fa fa-trash"></i> Eliminar</span>
                    </button>
                  </mat-menu>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>
            <mat-paginator #paginator class="mat-paginator-sticky" [pageSizeOptions]="[5, 10]" showFirstLastButtons>
            </mat-paginator>
          </div>
        </div>
      </div>
    </div>

    <div class="row" style="padding: 12px !important;">
      <div class="col-lg">
        <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">
          <input type="text" [(ngModel)]="deudor_edit_codigo" formControlName="iddeudor_compuesto" hidden>

          <div class="row">
            <div class="input-group">
              <mat-label style="padding-right: 8px;padding-top: 5px;"><u>Deudor</u></mat-label>
              <input type="text" style="width: 25.6% !important;" formControlName="iddeudor"
                (blur)="onLeaveDeudor($event)" [(ngModel)]="deudor_catalogo_id">
              <div class="input-group-append">
                <button class="btn btn-outline-primary" type="button" (click)="modalDeudores();"
                  style="border-color: #093070;padding: 4px 5px 1px 5px;line-height: 0 !important;">
                  <i class="fa fa-search"></i>
                </button>
              </div>
              <span>{{ deudor_catalogo.descripcion }}</span>
            </div>
          </div>

          <div class="row">
            <div class="input-group">
              <mat-label style="padding-right: 36px;padding-top: 5px;"><u>%</u></mat-label>
              <input type="number" (input)="onInputChange($event.target.value)" min="0" step="1" matInput
                style="width: 30% !important;" formControlName="porcentaje" min="0" required>
            </div>
          </div>

          <mat-card-actions align="end">
            <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit"
              (click)="submitData()"><i class="fa fa-plus"></i> Adicionar</button>
          </mat-card-actions>
        </form>
      </div>
    </div>
  </mat-card>
</div>
