import { DatePipe } from '@angular/common';
import { Component, ViewChild } from '@angular/core';
import { FormBuilder, FormGroup, Validators} from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { Router } from '@angular/router';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';
@Component({
  selector: 'app-almacen-create',
  templateUrl: './almacen-create.component.html',
  styleUrls: ['./almacen-create.component.scss']
})
export class AlmacenCreateComponent {
 
  FormularioData:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  isLinear = true;
  dataform:any='';
  userConn:any;
  usuarioLogueado:any;
  almacen=[];
  planporcen=[];
  adarea=[];
  moneda=[];
  fncuenta=[];

  public ventana="almacen-create"
	public detalle="almacen-detalle";
	public tipo="transaccion-almacen-CREATE";

  constructor(private _formBuilder: FormBuilder, private api:ApiService, private datePipe: DatePipe, public _snackBar: MatSnackBar,
    public dialogRef: MatDialogRef<AlmacenCreateComponent>, private spinner: NgxSpinnerService, private router: Router,
    public log_module: LogService, private toastr: ToastrService,) {
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    this.usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.FormularioData = this.createForm();
    // this.FormularioData1 = this.createForm1();
    // this.FormularioData2 = this.createForm2();
  }

  ngOnInit(): void {
    this.getAlladArea();
    this.getAllplanporcen();
    this.getAllmoneda();
    this.getAllfncuenta();
  }
  
  @ViewChild('validEmail') validEmail;

  createForm(): FormGroup {
    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
  
      codigo: [this.dataform.codigo,Validators.compose([Validators.required])],
      descripcion: [this.dataform.descripcion,Validators.compose([Validators.required])],
      direccion: [this.dataform.direccion, Validators.compose([Validators.required])],
      telefono: [this.dataform.telefono, Validators.compose([Validators.required])],
      email: [this.dataform.email, Validators.compose([Validators.required])],
      nropatronal: [this.dataform.nropatronal, Validators.compose([Validators.required])],
      codarea: [this.dataform.codarea],
      codplanporcen: [this.dataform.codplanporcen],
      tienda: [this.dataform.tienda],
      nropersonas: [this.dataform.nropersonas],
      lugar: [this.dataform.lugar],
      min_solurgente: [this.dataform.min_solurgente],
      codmoneda_min_solurgente: [this.dataform.codmoneda_min_solurgente],
      latitud: [this.dataform.latitud],
      longitud: [this.dataform.longitud],
      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      actividad: [this.dataform.actividad],
      horareg: [hora_actual_complete],
      usuarioreg: [this.usuarioLogueado],
      fax: ["0000-0000"],

      idcuenta_caja_mn: [this.dataform.idcuenta_caja_mn],
      idcuenta_caja_me: [this.dataform.idcuenta_caja_me],

      pesoest_rendi: [this.dataform.pesoest_rendi,],
      porcenmin_rendi: [this.dataform.porcenmin_rendi,],
      pesomin_rendi: [this.dataform.pesomin_rendi,],
      porcenmin: [this.dataform.porcenmin,],
      pesomin: [this.dataform.pesomin,],
      estandar: [this.dataform.estandar,],
      monestandar: [this.dataform.monestandar,],
      minimo: [this.dataform.minimo,],
      moneda: [this.dataform.moneda,],
      pesoest: [this.dataform.pesoest,],
      graficar: [this.dataform.graficar,],
      analizar_rendimiento: [this.dataform.analizar_rendimiento,],
    });
  }



  submitData(){
    const data = this.FormularioData.value;
    console.log(data);
    
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:- /inventario/mant/inalmacen/";
    return this.api.create("/inventario/mant/inalmacen/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.almacen = datav;

          console.log('data', datav);
          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this.spinner.show();
          setTimeout(() => {
            this.spinner.hide();
          }, 2000);
          this.toastr.success('! SE EDITO EXITOSAMENTE !');
          this._snackBar.open('Se ha guardado correctamente!', 'Ok', {
            duration: 3000,
          });
          location.reload();
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAlladArea(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/adarea/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.adarea = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllplanporcen(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/peplanporcen/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.planporcen = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllmoneda(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/admoneda/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.moneda = datav;
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllfncuenta(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/fondos/mant/fncuenta/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.fncuenta = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<div class="p-2">
  <h3 mat-dialog-title>Crear Almacen</h3>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div><br>
</div>

<div class="p-2 mb-1 mat-elevation-z8">
  <mat-stepper [linear]="isLinear" #stepper>
    <mat-step [stepControl]="FormularioData"><br>
      <form [formGroup]="FormularioData" (ngSubmit)="submitData()">
        <ng-template matStepLabel>Datos de Creación Almacen</ng-template>
        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Codigo</mat-label>
              <input matInput type="number" placeholder="Codigo" formControlName="codigo" maxlength="5" required>
            </mat-form-field>
          </div>

          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Dirección</mat-label>
              <input matInput placeholder="Dirección" formControlName="direccion" maxlength="45" required>
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Teléfono</mat-label>
              <input matInput type="number" placeholder="Telefono" formControlName="telefono" maxlength="10" required>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Correo Electronico</mat-label>
              <input matInput type="email" placeholder="correo Electronico" formControlName="email" maxlength="30"
                required>
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Numero Patronal</mat-label>
              <input matInput placeholder="Numero Patronal" formControlName="nropatronal" required>
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Area</mat-label>
              <mat-select formControlName="codarea">
                <mat-option *ngFor="let adareas of adarea" [value]="adareas.codigo">
                  {{ adareas.codigo }} - {{ adareas.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Plan de % de participacion en ventas precio</mat-label>
              <mat-select formControlName="codplanporcen">
                <mat-option *ngFor="let planporcens of planporcen" [value]="planporcens.codigo">
                  {{ planporcens.codigo }} - {{ planporcens.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Tipo</mat-label>
              <mat-select formControlName="tienda">
                <mat-option value="true">Tienda</mat-option>
                <mat-option value="false">Agencia</mat-option>
              </mat-select>
            </mat-form-field>
          </div>

          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Numero de Personal</mat-label>
              <input matInput placeholder="Numero de Personal" maxlength="3" formControlName="nropersonas">
            </mat-form-field>
          </div>

          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Ciudad</mat-label>
              <mat-select formControlName="lugar">
                <mat-option value="La Paz">La Paz</mat-option>
                <mat-option value="Oruro">Oruro</mat-option>
                <mat-option value="Potosi">Potosi</mat-option>
                <mat-option value="Cochabamba">Cochabamba</mat-option>
                <mat-option value="Sucre">Sucre</mat-option>
                <mat-option value="Tarija">Tarija</mat-option>
                <mat-option value="Santa Cruz">Santa Cruz</mat-option>
                <mat-option value="Beni">Beni</mat-option>
                <mat-option value="Pando">Pando</mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Suc. Libro Compras</mat-label>
              <input matInput placeholder="Libro Compras" maxlength="15">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Min. Sol. Urgente</mat-label>
              <input matInput placeholder="Solicitud Urgente" formControlName="min_solurgente" maxlength="15">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Moneda Sol. Urgente</mat-label>
              <mat-select formControlName="codmoneda_min_solurgente">
                <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                  {{ monedas.codigo }} - {{ monedas.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Latitud</mat-label>
              <input matInput placeholder="Latitud" formControlName="latitud" maxlength="45">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Longitud</mat-label>
              <input matInput placeholder="Longitud" formControlName="longitud" maxlength="45">
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Actividad</mat-label>
              <mat-select formControlName="actividad">
                <mat-option value="Venta al por mayor de materiales de construcción,
                  artículos de ferretería, equipo y materiales de fontanería y calefacción.">
                  Venta al por mayor de materiales de construcción, artículos de ferretería, equipo y materiales de
                  fontanería y calefacción.</mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Descripción</mat-label>
              <input matInput placeholder="Descripción" formControlName="descripcion" maxlength="35" required>
            </mat-form-field>
          </div>
        </div>

        <button mat-button (click)="onNoClick();" style="float: left;"> <i class="fa fa-arrow-left"></i> Atras</button>

        <div style="float: right;">
          <button mat-button color="primary" matStepperNext [disabled]="!FormularioData.valid">Siguiente <i
              class="fa fa-arrow-right"></i></button>
        </div>
      </form>
    </mat-step>


    <!-- SegundoFormulario -->
    <mat-step label="Extras"><br>
      <form [formGroup]="FormularioData">
        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Caja de MN</mat-label>
              <mat-select formControlName="idcuenta_caja_mn">
                <mat-option *ngFor="let fncuentas of fncuenta" [value]="fncuentas.id">
                  {{ fncuentas.id }} - {{ fncuentas.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>

          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Caja de ME</mat-label>
              <mat-select formControlName="idcuenta_caja_me">
                <mat-option *ngFor="let fncuentas of fncuenta" [value]="fncuentas.id">
                  {{ fncuentas.id }} - {{ fncuentas.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div>
          <button mat-button matStepperPrevious> <i class="fa fa-arrow-left"></i> Atras</button>
          <button mat-button color="primary" matStepperNext style="float: right;">Siguiente <i
              class="fa fa-arrow-right"></i></button>
        </div>
      </form>
    </mat-step>


    <!-- TercerFormulario -->
    <mat-step label="Parametros Estadistica"><br>
      <form [formGroup]="FormularioData" (ngSubmit)="submitData()">
        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Peso Estandar Rendimiento Kg.</mat-label>
              <input matInput type="number" formControlName="pesoest_rendi" placeholder="Kg">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Porcentaje Minimo Rendimiento %</mat-label>
              <input matInput type="number" formControlName="porcenmin_rendi" placeholder="%">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Peso Minimo Rendimiento KG</mat-label>
              <input matInput type="number" formControlName="pesomin_rendi" placeholder="Kg">
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Peso Estandar de Comisiones Kg.</mat-label>
              <input matInput type="number" formControlName="pesoest" placeholder="Kg">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Porcentaje Minimo %</mat-label>
              <input matInput type="number" formControlName="porcenmin" placeholder="%">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Peso Minimo de Ventas Comisiones KG</mat-label>
              <input matInput type="number" formControlName="pesomin" placeholder="Kg">
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Estandar de Venta</mat-label>
              <input matInput type="number" formControlName="estandar" placeholder="0">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Moneda</mat-label>
              <mat-select formControlName="monestandar">
                <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                  {{ monedas.codigo }} - {{ monedas.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Minimo de Venta</mat-label>
              <input matInput type="number" formControlName="minimo" placeholder="0">
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Moneda</mat-label>
              <mat-select formControlName="moneda">
                <mat-option *ngFor="let monedas of moneda" [value]="monedas.codigo">
                  {{ monedas.codigo }} - {{ monedas.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-radio-group aria-label="Seleccione una opcion" formControlName="graficar">
              <mat-label>Graficar Estadistica</mat-label><br>
              <mat-radio-button value="true">SI</mat-radio-button>
              <mat-radio-button value="false">NO</mat-radio-button>
            </mat-radio-group>
          </div>
          <div class="col">
            <mat-radio-group aria-label="Seleccione una opcion" formControlName="analizar_rendimiento">
              <mat-label>Analizar Rendimiento</mat-label><br>
              <mat-radio-button value="true">SI</mat-radio-button>
              <mat-radio-button value="false">NO</mat-radio-button>
            </mat-radio-group>
          </div>
        </div>

        <div>
          <button mat-button matStepperPrevious (click)="onNoClick();"> <i class="fa fa-arrow-left"></i> Atras</button>
          <button mat-button color="primary" matStepperNext style="float: right;"><i class="fa fa-save"></i>
            Guardar Formulario</button>
        </div>
      </form>
    </mat-step>
  </mat-stepper>
</div>
