import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { MatTableDataSource } from '@angular/material/table';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-modalPreciosFacturacion',
  templateUrl: './modalPreciosFacturacion.component.html',
  styleUrls: ['./modalPreciosFacturacion.component.scss']
})
export class ModalPreciosFacturacionComponent implements OnInit {

  FormularioData:FormGroup;
  public dataform:any=[];
  public tarifasfact=[];
  public params_tarifas=[];
  public intarifa:any=[];
  public dataEmpresa=[];
  empresa="PE";
  userConn:any;
  BD_storage:any;

  displayedColumns = ['codtarifa','descripcion','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  public ventana="prec-fac-create"
  public detalle="prec-fac-detalle";
  public tipo="prec-fac-POST";

  constructor(public dialogRef: MatDialogRef<ModalPreciosFacturacionComponent>, private api:ApiService, private spinner: NgxSpinnerService,
    @Inject(MAT_DIALOG_DATA) public dataEmpresaParametros: any, public _snackBar: MatSnackBar, public dialog: MatDialog,
    private _formBuilder: FormBuilder, public log_module:LogService, private toastr: ToastrService,){

    this.BD_storage = localStorage.getItem("bd_logueado") !== undefined ? JSON.parse(localStorage.getItem("bd_logueado")) : null;
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    this.FormularioData = this.createForm();
    this.getAllinTarifaSelect(this.userConn);
  }

  ngOnInit(){
    this.dataEmpresa = this.dataEmpresaParametros.dataEmpresaParametros;
    this.getbyIdparametrosTarifas();
  }

  createForm(): FormGroup{      
    return this._formBuilder.group({
      codempresa: [this.empresa],
      codtarifa: [this.dataform.codigo_intarifa, Validators.compose([Validators.required])],
    });
  }

  getAllinTarifaSelect(useConn){
    //select
    let  errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --/seg_adm/mant/adparametrosDiasextranc";
    return this.api.getAll('/inventario/mant/intarifa/'+useConn)  
      .subscribe({
        next: (datav) => {
          this.intarifa = datav;
          // console.log('data', datav);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getbyIdparametrosTarifas(){
    //tabla
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --/seg_adm/mant/adparametrosDiasextranc";
    return this.api.getById('/seg_adm/mant/adparametros_tarifasfact/'+this.userConn+"/"+this.BD_storage.bd)  
      .subscribe({
        next: (datav) => {
          this.params_tarifas = datav;
          // console.log('data', datav);
          this.dataSource = new MatTableDataSource(this.params_tarifas);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  submitData(){
    let data = this.FormularioData.value;
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  /seg_adm/mant/adparametros_tarifasfact";

    return this.api.create("/seg_adm/mant/adparametros_tarifasfact/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.tarifasfact = datav;
          this.toastr.success('! SE GUARDO EXITOSAMENTE !');
          this.getbyIdparametrosTarifas();
          
          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:-- seg_adm/mant/adparametrosTarifasfact Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: '350px',
      height:'auto',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result:Boolean)=>{
      // console.log(result);
      if(result) {
        return this.api.delete('/seg_adm/mant/adparametros_tarifasfact/'+this.userConn+"/"+element.codtarifa+'/'+this.BD_storage.bd)
        .subscribe({
          next: () => {
            this.spinner.show();
            setTimeout(() => {
              this.spinner.hide();
            }, 1000);
            this.getbyIdparametrosTarifas();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        this.toastr.error('! NO SE ELIMINO !');
      }
    });
  }

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<div class="p-2 container">
  <h1 mat-dialog-title><strong>Precios Permitidos Facturacion: </strong> {{ dataEmpresa }}</h1>
</div>

<div mat-dialog-content>
  <div class="container">
    <div class="content p-3" style="border-style: groove;">
      <form [formGroup]="FormularioData" (ngSubmit)="submitData()">
        <div class="row">
          <mat-form-field appearance="outline">
            <mat-label>Tarifa</mat-label>
            <mat-select formControlName="codtarifa" required>
              <mat-option *ngFor="let intarifas of intarifa" [value]="intarifas.codigo">
                {{ intarifas.descripcion }}
              </mat-option>
            </mat-select>
          </mat-form-field>

          <div class="col">
            <div class="mat-dialog-actions text-center" mat-dialog-actions>
              <button mat-raised-button color="primary" type="submit" [disabled]="!FormularioData.valid">
                <i class="fa fa-plus"></i> Añadir</button>
            </div>
          </div>
        </div>
      </form>

      <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
        <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
          <ng-container matColumnDef="codtarifa">
            <th mat-header-cell *matHeaderCellDef style="text-align: center;"> Tarifa </th>
            <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.codtarifa }}</td>
          </ng-container>

          <ng-container matColumnDef="descripcion">
            <th mat-header-cell *matHeaderCellDef style="text-align: center;">Descripcion</th>
            <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.descripcion }} </td>
          </ng-container>

          <ng-container matColumnDef="accion">
            <th mat-header-cell *matHeaderCellDef>Acción</th>
            <td mat-cell *matCellDef="let element">
              <button mat-icon-button1 [matMenuTriggerFor]="menu">
                <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
              </button>
              <mat-menu #menu="matMenu">
                <button mat-menu-item (click)="eliminar(element);">
                  <span><i class="fa fa-trash"></i> Eliminar</span>
                </button>
              </mat-menu>
            </td>
          </ng-container>
          <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
          <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
        </table>
      </div>
    </div><br>

    <button mat-raised-button color="primary" (click)="onNoClick();" style="float: right;">
      <i class="fa fa-times"></i>&nbsp; Cerrar</button>
  </div>
</div>
