              <th mat-header-cell *matHeaderCellDef> Rango Monto</th>
              <td mat-cell *matCellDef="let element">
                <input type="checkbox" [checked]="element.rango_monto" disabled>
              </td>
            </ng-container>

            <ng-container matColumnDef="rango_peso">
              <th mat-header-cell *matHeaderCellDef> Rango Peso </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;">
                <input type="checkbox" [checked]="element.rango_peso" disabled>
              </td>
