   <div class="tittle"> Direcciones Cliente</div>
   <div class="p-1 mat-elevation-z8">
     <div class="row p-2 text-center">
       <div class="col-md-6 pt-2">
         <mat-form-field class="example-full-width" appearance="outline">
           <mat-label>Teléfono</mat-label>
           <input type="text" matInput [formControl]="myControlTelefono" placeholder="Teléfono"
             (keyup)="applyFilter($event)" #input>
         </mat-form-field>
       </div>

       <div class="col-md-6 pt-2">
         <mat-form-field class="example-full-width" appearance="outline">
           <mat-label>Dirección</mat-label>
           <input type="text" matInput [formControl]="myControlDireccion" placeholder="Dirección"
             (keyup)="applyFilter($event)" #input>
         </mat-form-field>
       </div>
     </div>
     <div class="row">
       <div class="col-md-8" style="padding: 15px 20px 2px;">
         <p><strong>Direccion:</strong> &nbsp; {{ direccion_view }}</p>
       </div>
       <div class="col-md-4 text-center p-2">
         <button mat-stroked-button color="primary" (click)="mandarDireccion();">
           <i class="fa fa-check"></i></button>
         <button mat-stroked-button color="warn" (click)="close();"><i class="fa fa-times"></i></button>
       </div>
     </div>
   </div>

   <div class="row">
     <div class="col-lg">
       <div class="team bg-white scrollmenu">
         <div class="col-lg-12" style="width: 100%;overflow-x: auto;">
           <table mat-table [dataSource]="dataSourceDirecciones" multiTemplateDataRows class="mat-elevation-z8" matSort>
             <ng-container matColumnDef="telefono" class="ag-theme-alpine">
               <th mat-header-cell *matHeaderCellDef> Teléfono </th>
               <td mat-cell *matCellDef="let element" (click)="getDireccionView(element);" class="text-center">
                 {{ element.telefono }}</td>
             </ng-container>

             <ng-container matColumnDef="direccion">
               <th mat-header-cell *matHeaderCellDef> Dirección </th>
               <td mat-cell *matCellDef="let element" style="text-align: center;"
                 (click)="getDireccionView(element.direccion);">{{ element.direccion }}</td>
             </ng-container>

             <ng-container matColumnDef="central">
               <th mat-header-cell *matHeaderCellDef> Central </th>
               <td mat-cell *matCellDef="let element" (click)="getDireccionView(element);" class="text-center">
                 <ng-container *ngIf="element.central; else central">
                   SI
                 </ng-container>
                 <ng-template #central>
                   NO
                 </ng-template>
               </td>
             </ng-container>

             <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
             <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
           </table>
           <mat-paginator #paginator [pageSizeOptions]="[5, 10, 15]" showFirstLastButtons></mat-paginator>
         </div>
       </div>
     </div>
   </div>
 </div>
