  MatrizItemsComponent.prototype.unloadHandler1 = function (event) {
    var focusedElement = document.activeElement;
    var nombre_input = focusedElement.id;
    console.log("Elemento enfocado Matriz: ".concat(nombre_input));
    switch (nombre_input) {
      case 'focusPedido':
        this.addItemArray();
        this.pedido = undefined;
        break;
      case 'focusCantidad':
        // ACA SE COLOCA AL ARRAY DE ITEMS SELECCIONADOS PARA LA VENTA
        // UNA VEZ YA EN EN ARRAY, VUELVE A LA ULTIMA POSICION DE LA MATRIZ
        this.addItemArray();
        this.pedido = undefined;
        break;
      case 'idBuscadorHoja':
        this.getHoja();
        break;
    }
  };
  MatrizItemsComponent.prototype.unloadHandler2 = function (event) {
    var focusedElement = document.activeElement;
