import { Component, ElementRef, ViewChild } from '@angular/core';
import { MatDialog } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { RolesCreateComponent } from './roles-create/roles-create.component';
import { DialogDeleteComponent } from '@modules/dialog-delete/dialog-delete.component';
import { Roles } from '@services/modelos/objetos';
import { FormControl } from '@angular/forms';
import { Observable } from 'rxjs';
import { RolesEditComponent } from './roles-edit/roles-edit.component';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';

@Component({
  selector: 'app-roles-component',
  templateUrl: './roles-component.component.html',
  styleUrls: ['./roles-component.component.scss']
})
export class RolesComponent {

  roles:any=[];
  semodulo:any=[];
  data_accesos:any=[];
  seclasificacion:any=[];
  cheked: any = [];
  array_ventanas_permiso_true: any = [{
    codprograma: 0,
    codrol: "",
    codigo: 0,
  }];

  array_ventanas_permiso_false: any = [{
    codprograma: 0,
    codrol: "",
    codigo: 0,
  }];

  userConn:string;
  visible:boolean=false;
  rol_select:any;

  displayedColumns = ['codigo','descripcion','dias_cambio','fechareg','usuarioreg','accion'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild("dialogD") dialogD: ElementRef;
  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;
  
  myControl = new FormControl<string | Roles>('');
  options: Roles[] = [];
  filteredOptions: Observable<Roles[]>;

  nombre_ventana: string = "abmserol.vb";
  public ventana="Roles Usuarios"

  constructor(private api: ApiService, public dialog: MatDialog, private spinner: NgxSpinnerService, public nombre_ventana_service: NombreVentanaService) {
    this.mandarNombre();

    let usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    this.api.getRolUserParaVentana(usuarioLogueado, this.nombre_ventana);
  }

  ngOnInit() {
    this.getAllRoles();
    this.getAllModulos();
    this.getAllClasificacion();
  }

  private _filter(name: string): Roles[] {
    const filterValue = name.toLowerCase();

    return this.options.filter(option => option.codigo.toLowerCase().includes(filterValue));
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
    console.log(this.dataSource.filter);
  }
  
  displayFn(roles: Roles): string {
    return roles && roles.codigo ? roles.codigo : '';
  }



  pasarCheked(element_check, modulo) {
    let fal: boolean = false;
    let ved: boolean = true;

    console.log(element_check);
    let check = element_check.activo;
        
    if (check === true) {
      element_check.activo = fal;
      this.array_ventanas_permiso_true.push({
        codprograma: element_check.codigo,
        codrol: this.rol_select,
        codigo:modulo
      });

    } if(check === false){
      element_check.activo = ved;
      this.array_ventanas_permiso_false.push({
        codprograma: element_check.codigo,
        codrol: this.rol_select,
        codigo:modulo
      });
    }

    console.log("Array de ventanas a guardar con check TRUE" + JSON.stringify(this.array_ventanas_permiso_true));
    console.log("Array de ventanas a guardar con check FALSE"+ JSON.stringify(this.array_ventanas_permiso_false) );
    
  }

  submitData() { 

  }

  



  getAllRoles(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/serol/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.roles = datav;
          console.log(this.roles);
          
          this.spinner.show();
 
          setTimeout(() => {
            this.spinner.hide();
          }, 1500);

          this.dataSource = new MatTableDataSource(this.roles);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllModulos(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/oper/prgaccesosrol/semodulo/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.semodulo=datav;
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllClasificacion(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/oper/prgaccesosrol/seclasificacion/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.seclasificacion = datav;
          console.log(this.seclasificacion);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  verOpciones(modulo, clasificacion){
    console.log(modulo, clasificacion, this.rol_select);
    
    let errorMessage = "La Ruta presenta fallos al hacer peticion GET --/seg_adm/oper/prgaccesorol/seprograma/";
    return this.api.getById('/seg_adm/oper/prgaccesosrol/serolprogs/rolGetCheck/'+this.userConn+"/"+this.rol_select+"/"+clasificacion+'/'+modulo)
      .subscribe({
        next: (datav) => {
          this.cheked = datav;
          console.log(this.cheked);
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  verAcceso(codigo){
    this.rol_select = codigo;
    if(this.visible == false){
      this.visible = true;
    }else{
      this.visible=false;
    }
  }

  eliminar(element): void{
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  /moneda Delete";

    const dialogRef = this.dialog.open(DialogDeleteComponent, {
      width: '350px',
      data:{dataUsuarioEdit:element},
    });

    dialogRef.afterClosed().subscribe((result: Boolean)=>{
      if(result) {
        return this.api.delete('/seg_adm/mant/serol/'+this.userConn+"/"+element.codigo)
        .subscribe({
          next: () => {

            this.spinner.show();
            setTimeout(() => {
              this.spinner.hide();
            }, 1500);

            location.reload();
          },
          error: (err: any) => { 
            console.log(errorMessage);
          },
          complete: () => { }
        })
      }else{
        alert("Â¡No se elimino!");
      }
    });
  }

  openDialogCreateRol(): void {
    this.dialog.open(RolesCreateComponent, {
      width: 'auto',
      height: 'auto',
    });
  }

  openDialogEditar(dataRolEdit): void {
    this.data_accesos = dataRolEdit;
    //console.log(this.data);
      const dialogRef = this.dialog.open(RolesEditComponent, {
        data: {dataRolEdit:dataRolEdit},
        width: 'auto',
        height:'auto',
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }

}
<section class="content">
  <div class="container-fluid">
    <div class="overlay"></div>
    <div class="mat-elevation-z8">
      <div class="row headTittle">
        <div class="col-md ">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Buscador Rol </mat-label>
            <input type="text" matInput [formControl]="myControl" (keyup)="applyFilter($event)" placeholder="Roles"
              #input>
          </mat-form-field>
        </div>

        <div class="col-md text-center">
          <h3>Roles Usuarios</h3>
        </div>

        <div class="col-md">
          <button mat-button color="primary" (click)="openDialogCreateRol()" style="float: right;">
            <i class="fa fa-plus"></i> Crear Rol</button>
        </div>
      </div>
    </div>

    <ngx-spinner bdColor="rgba(251, 216, 0, 0.8)" size="large" color="#093070" type="square-loader" [fullScreen]="true">
      <br>
      <p style="font-size: 25px; color: rgb(9,48,112,)">ðŸ”© Cargando... ðŸ”©</p>
    </ngx-spinner>

    <div class="row">
      <div class="col-lg">
        <div class="team p-2 bg-white scrollmenu">
          <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
              <ng-container matColumnDef="codigo">
                <th mat-header-cell *matHeaderCellDef> CÃ³digo </th>
                <td mat-cell *matCellDef="let element"> {{ element.codigo }} </td>
              </ng-container>

              <ng-container matColumnDef="descripcion">
                <th mat-header-cell *matHeaderCellDef> DescripciÃ³n</th>
                <td mat-cell *matCellDef="let element"> {{ element.descripcion }} </td>
              </ng-container>

              <ng-container matColumnDef="dias_cambio">
                <th mat-header-cell *matHeaderCellDef> Dias Cambio</th>
                <td mat-cell *matCellDef="let element"> {{ element.dias_cambio }} </td>
              </ng-container>

              <ng-container matColumnDef="fechareg">
                <th mat-header-cell *matHeaderCellDef> Fecha Registro </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">
                  {{ element.fechareg | date:'yyyy/MM/dd'}} </td>
              </ng-container>

              <ng-container matColumnDef="usuarioreg">
                <th mat-header-cell *matHeaderCellDef> Usuario Registro </th>
                <td mat-cell *matCellDef="let element" style="text-align: center;">{{ element.usuarioreg }} </td>
              </ng-container>

              <ng-container matColumnDef="accion">
                <th mat-header-cell *matHeaderCellDef>AcciÃ³n</th>
                <td mat-cell *matCellDef="let element">
                  <button mat-icon-button1 [matMenuTriggerFor]="menu">
                    <i class="fa fa-ellipsis-v" aria-hidden="true"></i>
                  </button>
                  <mat-menu #menu="matMenu">
                    <button mat-menu-item (click)="verAcceso(element.codigo);">
                      <span><i class="fa fa-lock"></i> Accesos</span>
                    </button>

                    <button mat-menu-item (click)="openDialogEditar(element);">
                      <span><i class="fa fa-pen"></i> Editar</span>
                    </button>

                    <button mat-menu-item (click)="eliminar(element);">
                      <span><i class="fa fa-trash"></i> Eliminar</span>
                    </button>
                  </mat-menu>
                </td>
              </ng-container>

              <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
              <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
            </table>
            <mat-paginator #paginator [pageSizeOptions]="[10, 15, 20]" showFirstLastButtons></mat-paginator>
          </div>
        </div>
      </div>
    </div>


    <mat-tab-group [@.disabled]="true" *ngIf="visible">
      <mat-tab *ngFor="let semodulos of semodulo">
        <ng-template mat-tab-label>
          <mat-icon>tab</mat-icon> {{ semodulos.descripcion }} AA
        </ng-template>
        <div>
          <!-- segundoTab -->
          <mat-tab-group>
            <mat-tab *ngFor="let seclasificacions of seclasificacion">
              <ng-container>
                <ng-template mat-tab-label>
                  <button mat-raised-button color="primary"
                    (click)="verOpciones(semodulos.codigo, seclasificacions.codigo);" style="cursor: pointer;">
                    <mat-icon>open_in_browser</mat-icon>
                    {{ seclasificacions.descripcion }} BB
                  </button>
                </ng-template>
              </ng-container>

              <div class="p-3">
                <div class="row" style="float: right;">
                  <button type="button" mat-raised-button color="primary" (click)="submitData();">
                    <i class="fa fa-save" aria-hidden="true"></i> Guardar
                  </button>
                  <!-- <button type="button" mat-button color="warn">
                    <i class="fa fa-eraser" aria-hidden="true"></i>
                    <span> Limpiar Accesos</span>
                  </button> -->
                </div>
                <div class="col-md">
                  <section class="example-section" *ngFor="let chekeds of cheked">
                    <mat-checkbox class="example-margin" [checked]="chekeds.activo"
                      (click)="pasarCheked(chekeds, semodulos.codigo)">
                      <div class="row"> {{ chekeds.descripcion }}
                        <!-- <br><strong>Nombre Ventana: </strong> {{ chekeds.nombre }} -->
                      </div>
                    </mat-checkbox>
                  </section>
                </div>
              </div>
            </mat-tab>
          </mat-tab-group>
        </div>
      </mat-tab>
    </mat-tab-group><br><br>
  </div>
</section>
