          <div class="col-md">
            <mat-form-field color="accent">
              <mat-label>Valido Desde</mat-label>
              <input matInput formControlName="desde" [(ngModel)]="numChecCli_edit.desde" [matDatepicker]="picker21">
              <mat-datepicker-toggle matIconSuffix [for]="picker21"></mat-datepicker-toggle>
              <mat-datepicker #picker21 color="primary"></mat-datepicker>
            </mat-form-field>
          </div>

          <div class="col-md">
            <mat-form-field color="accent">
              <mat-label>Valido Hasta</mat-label>
              <input matInput formControlName="hasta" [(ngModel)]="numChecCli_edit.hasta" [matDatepicker]="picker2">
              <mat-datepicker-toggle matIconSuffix [for]="picker2"></mat-datepicker-toggle>
              <mat-datepicker #picker2 color="primary"></mat-datepicker>
            </mat-form-field>
          </div>
        </div>

        <div class="row">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Nro. de Cuenta</mat-label>
              <mat-select formControlName="codcuentab" [(ngModel)]="numChecCli_edit.codcuentab">
                <mat-option *ngFor="let cuentas_bancariass of cuentas_bancarias" [value]="cuentas_bancariass.codigo">
                  {{ cuentas_bancariass.codbanco }} - {{ cuentas_bancariass.descripcion }} -
                  {{ cuentas_bancariass.codigo }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="row  mb-2">
          <div class="col-md">
            <mat-form-field appearance="outline">
              <mat-label>Origen por Defecto</mat-label>
              <mat-select formControlName="origen" [(ngModel)]="numChecCli_edit.origen">
                <mat-option value="OTRO">OTRO</mat-option>
                <mat-option value="PROVINCIA">PROVINCIA</mat-option>
                <mat-option value="CIUDAD">CIUDAD</mat-option>
                <mat-option value="ALMACEN">ALMACEN</mat-option>
                <mat-option value="VENDEDOR">VENDEDOR</mat-option>
                <mat-option value="CLIENTE">CLIENTE</mat-option>
                <mat-option value="BANCO">BANCO</mat-option>
              </mat-select>
            </mat-form-field>
          </div>
        </div>

        <div class="mat-dialog-actions" mat-dialog-actions>
          <a mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</a>&nbsp;
          <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit"><i
              class="fa fa-save"></i> Guardar</button>
        </div>
      </form>
    </div>
  </div>
</div>
