import { Component, HostListener, OnInit, ViewChild } from '@angular/core';
import { FormControl } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { ApiService } from '@services/api.service';
import { veDescuento } from '@services/modelos/objetos';
import { NgxSpinnerService } from 'ngx-spinner';
import { Observable } from 'rxjs';
import { DescuentoService } from '../../serviciodescuento/descuento.service';
@Component({
  selector: 'app-modal-descuentos',
  templateUrl: './modal-descuentos.component.html',
  styleUrls: ['./modal-descuentos.component.scss']
})
export class ModalDescuentosComponent implements OnInit {

  @HostListener("document:keydown.enter", []) unloadHandler(event: KeyboardEvent) {
    this.mandarDescuento();
  };

  @HostListener('dblclick') onDoubleClicked2() {
    this.mandarDescuento();
  };

  descuentos_get: any = [];
  public descuento_view: any = [];
  userConn: any;

  displayedColumns = ['codigo', 'descripcion'];

  dataSource = new MatTableDataSource<veDescuento>();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  myControlCodigo = new FormControl<string | veDescuento>('');
  myControlDescripcion = new FormControl<string | veDescuento>('');

  options: veDescuento[] = [];
  filteredOptions: Observable<veDescuento[]>;

  constructor(public dialogRef: MatDialogRef<ModalDescuentosComponent>, private api: ApiService,
    public servicioDescuento: DescuentoService) {

    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
  }

  ngOnInit() {
    this.getDescuentos();
  }

  applyFilter(event: Event) {
    const filterValue = (event.target as HTMLInputElement).value;
    this.dataSource.filter = filterValue.trim().toLowerCase();
    console.log(this.dataSource.filter);
  }

  displayFn(user: veDescuento): any {
    return user && user.codigo ? user.codigo : '';
  }

  getDescuentos() {
    let errorMessage: string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --vedescuento/catalogo";

    return this.api.getAll('/venta/mant/vedescuento/' + this.userConn)
      .subscribe({
        next: (datav) => {
          this.descuentos_get = datav;
          console.log(this.descuentos_get);

          this.dataSource = new MatTableDataSource(this.descuentos_get);
          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },
        error: (err: any) => {
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getDescuentobyId(element) {
    this.descuento_view = element;
    console.log(element);
  }

  mandarDescuento() {
    this.servicioDescuento.disparadorDeDescuentos.emit({
      descuento: this.descuento_view,
    });
    this.close();
  }

  close() {
    this.dialogRef.close();
  }
}
<div class="p-1">
  <div class="tittle"> Catálogo Descuentos Especiales</div>
  <div class="p-1 mat-elevation-z8">
    <div class="row p-2 text-center">
      <div class="col-md pt-2">
        <mat-form-field class="example-full-width" appearance="outline">
          <mat-label>Código</mat-label>
          <input type="text" matInput [formControl]="myControlCodigo" placeholder="Codigo" (keyup)="applyFilter($event)"
            #input>
        </mat-form-field>
      </div>

      <div class="col-md pt-2">
        <mat-form-field class="example-full-width" appearance="outline">
          <mat-label>Descripción</mat-label>
          <input type="text" matInput [formControl]="myControlDescripcion" placeholder="Descripción"
            (keyup)="applyFilter($event)" #input>
        </mat-form-field>
      </div>
    </div>
    <div class="row">
      <div class="col-md-8" style="padding: 15px 20px 2px;">
        <p><strong>Dsct. Especial:</strong> &nbsp; {{ descuento_view.codigo }} - {{ descuento_view.descripcion }}</p>
      </div>
      <div class="col-md-4 text-center p-2">
        <button mat-stroked-button color="primary" (click)="mandarDescuento();">
          <i class="fa fa-check"></i>
        </button>
        <button mat-stroked-button color="warn" (click)="close();"><i class="fa fa-times"></i></button>
      </div>
    </div>
  </div>

  <div class="row">
    <div class="col-lg">
      <div class="team bg-white scrollmenu">
        <div class="col-lg-12" style="width: 100%;overflow-x: auto;">
          <table mat-table [dataSource]="dataSource" multiTemplateDataRows class="mat-elevation-z8" matSort>
            <ng-container matColumnDef="codigo" class="ag-theme-alpine">
              <th mat-header-cell *matHeaderCellDef> Código </th>
              <td mat-cell *matCellDef="let element" (click)="getDescuentobyId(element);">
                {{ element.codigo }}</td>
            </ng-container>

            <ng-container matColumnDef="descripcion">
              <th mat-header-cell *matHeaderCellDef> Descripción </th>
              <td mat-cell *matCellDef="let element" style="text-align: center;" (click)="getDescuentobyId(element);">
                {{ element.descripcion }}</td>
            </ng-container>

            <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
            <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
          </table>
          <mat-paginator #paginator [pageSizeOptions]="[10, 15, 20]" showFirstLastButtons></mat-paginator>
        </div>
      </div>
    </div>
  </div>
</div>
