import { Component, HostListener, Inject, OnInit } from '@angular/core';
import { MAT_DIALOG_DATA, MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { ToastrService } from 'ngx-toastr';
import { Clipboard } from '@angular/cdk/clipboard';

@Component({
  selector: 'app-permiso-especial-password',
  templateUrl: './permiso-especial-password.component.html',
  styleUrls: ['./permiso-especial-password.component.scss']
})
export class PermisoEspecialPasswordComponent implements OnInit {

  @HostListener("document:keydown.enter", []) unloadHandler(event: KeyboardEvent){    
    this.getaAutorizacion();
  };

  contrasenia: string;
  userConn: any;
  precio_get: any = [];  
  data_autorizacionA:any=[];
  data_autorizacionB:any=[];
  servicio_id: any = [];
  text_servicio: any = [];
  autorizacion_recibida: any = [];
  BD_storage:any=[];


  constructor(private api:ApiService,public dialog: MatDialog, public dialogRef: MatDialogRef<PermisoEspecialPasswordComponent>,
    public log_module: LogService, private toastr: ToastrService, public _snackBar: MatSnackBar, private clipboard: Clipboard,
    @Inject(MAT_DIALOG_DATA) public dataA: any,@Inject(MAT_DIALOG_DATA) public dataB: any,
    @Inject(MAT_DIALOG_DATA) public permiso_id: any,
    @Inject(MAT_DIALOG_DATA) public permiso_text: any){ 

  }

  ngOnInit() {
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.BD_storage = localStorage.getItem("bd_logueado") !== undefined ? JSON.parse(localStorage.getItem("bd_logueado")) : null;

    this.data_autorizacionA = this.dataA.dataA;
    this.data_autorizacionB = this.dataB.dataB;
    this.servicio_id = this.permiso_id.permiso_id;
    this.text_servicio = this.permiso_text.permiso_text;


    console.log( this.data_autorizacionA, this.data_autorizacionB, this.servicio_id, this.text_servicio );
  }

  getaAutorizacion(){
    let errorMessage = "La Ruta presenta fallos al hacer peticion GET --/seg_adm/oper/prgcontrasena/passwordAut/"
    return this.api.getAll('/seg_adm/oper/prgcontrasena/passwordAut/'+this.userConn+"/"+this.BD_storage.bd+"/"+this.servicio_id+"/"+this.contrasenia)
      .subscribe({
        next: (datav) => {
          this.autorizacion_recibida = datav;
          console.log(this.autorizacion_recibida);

            if(datav = 712){
              this._snackBar.open('¬° Permiso Autorizado !'+ this.autorizacion_recibida.resp, '‚òëÔ∏è', {
                duration: 12000,
                panelClass: ['coorporativo-snackbar', 'login-snackbar'],
              });
              this.toastr.success('!AUTORIZADO!');

              this.closeEventTrue();
            }},

        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  copyToClipboard(): void {
    let copia = "Codigo Servicio: "+ this.servicio_id+ "Dato A: "+ this.data_autorizacionA + "Dato B: "+ this.data_autorizacionB
    // Se copia el texto del input al portapapeles
    this.clipboard.copy(copia);

    // Se muestra un snackbar durante 2 segundos en la parte inferior
    this._snackBar.open('¬° Texto Copiado !', 'üìë', {
      duration: 2000,
      panelClass: ['coorporativo-snackbar', 'login-snackbar'],
    });
  }

  closeEventTrue(){
    this.dialogRef.close(true);
  }

  closeEventFalse(){
    this.dialogRef.close(false);
  }
}
<div class="text-center" style="background-color: #f0ffff;">
  <h1 mat-dialog-title>Seguridad - Permiso Especial</h1>

  <div mat-dialog-content>
    <div class="container">
      <div class="row">
        <div class="col-md-3" style="font-size: 5.0rem;">
          <img src="assets/img/llave_azul.png" alt="Llaves" width="70px;" height="70px;"
            style="display:block;margin: auto;">
        </div>

        <div class="col-md-9">
          <textarea rows="4" cols="43" style="color: red;" disabled>{{ servicio_id }} - {{ text_servicio }}</textarea>
        </div>
      </div>

      <div class="row">
        <div class="col-md">
          <mat-label style="margin-right: 36px;"><u>Dato A</u></mat-label>
          <input type="text" matInput style="width:73%;" [(ngModel)]="data_autorizacionA" disabled>
        </div>
      </div>

      <div class="row">
        <div class="col-md">
          <mat-label style="margin-right: 36px;"><u>Dato B</u></mat-label>
          <input type="text" matInput style="width:73%;" [(ngModel)]="data_autorizacionB" disabled>
        </div>
      </div>

      <div class="row">
        <div class="col-md">
          <mat-label style="margin-right: 4px;"><u>Contrase√±a</u></mat-label>
          <input type="password" matInput maxlength="10" style="width:73%;" [(ngModel)]="contrasenia" required>
        </div>
      </div>
    </div>

    <div class="mat-dialog-actions" mat-dialog-actions style="display: block !important;">
      <button mat-raised-button class="success" (click)="getaAutorizacion();"> Aceptar <i
          class="fa fa-check"></i></button>

      <button mat-raised-button class="blank" (click)="copyToClipboard()"><i class="fa fa-clone"></i> Copiar Dato A & B
      </button>

      <button mat-raised-button class="danger" (click)="closeEventFalse();">Cancelar <i
          class="fa fa-times"></i></button>
    </div>
  </div>
</div>
