import { DatePipe } from '@angular/common';
import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
@Component({
  selector: 'app-gruposlineas-create',
  templateUrl: './gruposlineas-create.component.html',
  styleUrls: ['./gruposlineas-create.component.scss']
})
export class GruposlineasCreateComponent implements OnInit {

  FormularioData:FormGroup;
  public gruposlineas=[];
  dataform:any='';
  datagruposlineas=[];
  userConn:any;
  usuarioLogueado:any;

  fecha_actual = new Date();
  hora_actual = new Date();

  public ventana="lineasgrupos-create"
  public detalle="lineasgrupos";
  public tipo="lineasgrupos-POST";

  constructor(private api:ApiService, public dialogRef: MatDialogRef<GruposlineasCreateComponent>,
    private _formBuilder: FormBuilder, private datePipe: DatePipe, public log_module:LogService,
    public _snackBar: MatSnackBar){
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    this.FormularioData = this.createForm();
  }

  ngOnInit(){

  }

  createForm(): FormGroup {
    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      codigo: [this.dataform.codigo, Validators.compose([Validators.required])],
      descripcion: [this.dataform.descripcion, Validators.compose([Validators.required])],
      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      horareg: [hora_actual_complete],
      usuarioreg: [this.usuarioLogueado],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    console.log(data);
    
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:--  /seg_adm/mant/adunidad POST";
    return this.api.create("/inventario/mant/ingrupomer/"+this.userConn, data)
      .subscribe({
        next: (datav) => {
          this.datagruposlineas = datav;

          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);
          this._snackBar.open('Se ha guardado correctamente!', 'Ok', {
            duration: 3000,
            panelClass: ['coorporativo-snackbar', 'login-snackbar'],
          });
          
          location.reload();
        },
    
        error: (err) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  onNoClick(): void {
    this.dialogRef.close();
  }

}
<div class="p-2">
  <h1 mat-dialog-title>Crear Grupo de Linea de Items</h1>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div>
</div>

<div mat-dialog-content>
  <div class="container">
    <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">
      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Código </mat-label>
            <input matInput type="number" formControlName="codigo" maxlength="6" min="0" step="0" required>
          </mat-form-field>
        </div>
      </div>

      <div class="row">
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Descripción </mat-label>
            <input type="text" matInput formControlName="descripcion" maxlength="50" required>
          </mat-form-field>
        </div>
      </div>

      <div class="mat-dialog-actions" mat-dialog-actions>
        <a mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</a>

        <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit">
          <i class="fa fa-save"></i> Guardar</button>
      </div>
    </form>
  </div>
</div>
