  <div class="container-fluid">
    <ng-container>
      <div class="p-2">
        <mat-card style="background-color: #ffe0c0;">
          <mat-card-header class="mb-1">
            <mat-card-title style="font-size: 0.8rem;">Registro de Inventario por Grupo</mat-card-title>
          </mat-card-header>

          <mat-card-content>
            <div>
              <button mat-raised-button color="primary" (click)="guardarInventario()"><i class="fa fa-save"></i>
                Grabar</button>&nbsp;

              <button mat-raised-button color="primary" (click)="limpiarCantidades();">
                <mat-icon>create</mat-icon> Limpiar Cantidades
              </button>&nbsp;

              <div style="float: right;">
                <a class="danger" (click)="modalMatrizProductos()">
                  <mat-icon>apps</mat-icon>
                </a>
              </div>
            </div><br>





            <tr>
              <th class="tg-0lax" style="width: 80%;">
                <div class="row">
                  <div class="input-group">
                    <mat-label style="padding-right: 5px;padding-top: 7px;"><u>ID Inventario</u></mat-label>
                    <input type="text" style="width: 41% !important;" [(ngModel)]="cod_inventario.id">
                    <div class="input-group-append">
                      <button class="btn btn-outline-primary" type="button" (click)="catalogoInventario();"
                        style="border-color: #093070;padding: 4px 5px 1px 5px;line-height: 0 !important;"
                        matTooltip="Buscar Inventario">
                        <i class="fa fa-search"></i>
                      </button>
                      <input type="text" style="width: 40% !important;" [(ngModel)]="numero_id">
                    </div>
                  </div>
                </div>

                <div class="row">
                  <mat-label style="padding-right: 15px;padding-top: 7px;"><u>Nro. Grupo</u></mat-label>
                  <input type="text" style="width: 10% !important;" [(ngModel)]="nro">
                  <input type="text" style="width: 49.2% !important;" [(ngModel)]="get_observaciones.obs" disabled>
                </div>

                <div class="row">
                  <mat-label style="padding-right: 49px;padding-top: 7px;"><u>Zona</u>
                  </mat-label>
                  <input type="text" style="width: 10% !important;" [(ngModel)]="zona" disabled>
                </div>
              </th>
            </tr>
            <br>



            <div class="row">
              <div class="col">
                <table mat-table [dataSource]="dataSource" class="mat-elevation-z8" multiTemplateDataRows matSort>

                  <!-- Item Column -->
                  <ng-container matColumnDef="item">
                    <th mat-header-cell *matHeaderCellDef class="th">Item</th>
                    <td mat-cell *matCellDef="let element" class="td"> </td>
                  </ng-container>

                  <!-- Descripcion Column -->
                  <ng-container matColumnDef="descripcion">
                    <th mat-header-cell *matHeaderCellDef style="width: 30%;"> DescripciÃ³n </th>
                    <td mat-cell *matCellDef="let element" style="width: 30%;"> </td>
                  </ng-container>

                  <!-- Medida Column -->
                  <ng-container matColumnDef="medida">
                    <th mat-header-cell *matHeaderCellDef> Medida </th>
                    <td mat-cell *matCellDef="let element" class="medida"
                      style="text-align: left !important;padding: 0 0px 0 5px;"> </td>
                  </ng-container>

                  <!-- Unidad Column -->
                  <ng-container matColumnDef="unidad">
                    <th mat-header-cell *matHeaderCellDef> Unidad </th>
                    <td mat-cell *matCellDef="let element"> </td>
                  </ng-container>

                  <!-- iva Column -->
                  <ng-container matColumnDef="zona">
                    <th mat-header-cell *matHeaderCellDef> Zona </th>
                    <td mat-cell *matCellDef="let element"> </td>
                  </ng-container>

                  <!-- cantidad Column -->
                  <ng-container matColumnDef="cantidad">
                    <th mat-header-cell *matHeaderCellDef> Cantidad </th>
                    <td mat-cell *matCellDef="let element"> </td>
                  </ng-container>

                  <mat-header-row *matHeaderRowDef="displayedColumns"></mat-header-row>
                  <mat-row *matRowDef="let row; columns: displayedColumns;"></mat-row>
                </table>
              </div>
            </div>
          </mat-card-content>
        </mat-card>
      </div>
    </ng-container>
  </div>
</section>
