import { Component, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MatDialog } from '@angular/material/dialog';
import { ModalAlmacenComponent } from '@components/mantenimiento/inventario/almacen/modal-almacen/modal-almacen.component';
import { ServicioalmacenService } from '@components/mantenimiento/inventario/almacen/servicioalmacen/servicioalmacen.service';
import { NombreVentanaService } from '@modules/main/footer/servicio-nombre-ventana/nombre-ventana.service';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  // eslint-disable-next-line @angular-eslint/component-selector
  selector: 'app-parametros-facturacion-SIAT',
  templateUrl: './parametros-facturacion-SIAT.component.html',
  styleUrls: ['./parametros-facturacion-SIAT.component.scss']
})
export class ParametrosFacturacionSIATComponent implements OnInit {

  nombre_ventana: string = "prgadsiat_parametros_facturacion.vb";
  
  FormularioDataInternet: FormGroup;
  FormularioDataSIN: FormGroup;
  
  userConn:any;
  BD_storage:any;
  nombre_almacen:any;
  cod_almacen_input:any;
  cod_sistema:any;
  para_facturacion:any=[];
  servicio_internet_activo:any;
  servicio_sin_activo: any;
  servicio_actividad: any=[];

  dataform: any = "";
  almacen_local:any=[];
  sector:any=[];
  factura:any=[];
  tipo_emision:any=[];
  empresa:any=[];
  sucursal:any=[];
  actividad:any=[];
  servicio_internet:any=[];
  servicio_sin: any = [];
  para_facturacion_copied: any = [];
  public cod_almacen_cliente:string;

  public ventana="Parametros Facturacion"
  public detalle="parametros_facturacion-update";
  public tipo="parametros_facturacion-UPDATE";

  constructor(public dialog: MatDialog, public log_module:LogService,public almacenservice:ServicioalmacenService,
    private api:ApiService,private toastr: ToastrService,private _formBuilder: FormBuilder,public nombre_ventana_service:NombreVentanaService){
    this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
    this.BD_storage = localStorage.getItem("bd_logueado") !== undefined ? JSON.parse(localStorage.getItem("bd_logueado")) : null;

    this.getSucursal();
    this.getActividad();
    this.getTipoEmision();
    this.getTipoFactura();
    this.getTipoDocSector();
    this.getAlmacenLocal(this.BD_storage);
    this.getNombreEmpresa(this.BD_storage);
    this.mandarNombre();
  }

  ngOnInit(){
    this.almacenservice.disparadorDeAlmacenes.subscribe(data =>{
      console.log("Recibiendo Almacen: ", data);
      this.cod_almacen_cliente = data.almacen;
    });
  }

  getSucursal(){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/prgadsiat_parametros_facturacion/sucursales/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.sucursal = datav;
          console.log(this.sucursal);
          
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getActividad(){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/prgadsiat_parametros_facturacion/actividades/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.actividad = datav;
          console.log(this.actividad);
          
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getNombreEmpresa(bd){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/adempresa/getNomEmpresa/'+this.userConn+"/"+bd.bd)
      .subscribe({
        next: (datav) => {
          this.empresa = datav;
          console.log(this.empresa);
          
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getTipoEmision(){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/prgadsiat_parametros_facturacion/tipoEmision/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.tipo_emision = datav;
          console.log(this.tipo_emision);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getTipoFactura(){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/prgadsiat_parametros_facturacion/tipoFactura/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.factura = datav;
          console.log(this.factura);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getTipoDocSector(){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/prgadsiat_parametros_facturacion/tipoDocSector/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.sector = datav;
          console.log(this.sector);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAlmacenLocal(bd){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/adempresa/'+this.userConn+"/"+bd.bd)
      .subscribe({
        next: (datav) => {
          this.almacen_local = datav;
          console.log(this.almacen_local);
          let cod_almacen = this.almacen_local.codalmacen;
          console.log(cod_almacen);
          
          this.getParamFacturacion(cod_almacen);
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getParamFacturacion(codAlmacenLocal){
    let errorMessage:string = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/prgadsiat_parametros_facturacion/paramFacturacion/'+this.userConn+"/"+codAlmacenLocal)
      .subscribe({
        next: (datav) => {
          this.para_facturacion = datav;
          this.para_facturacion_copied ={...datav};
          console.log(this.para_facturacion_copied);
          this.servicio_actividad = Number(this.para_facturacion.codactividad);

          console.log( this.servicio_actividad );          
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  actualizarServicioInternet(valor){
    let data=[];
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:- /seg_adm/mant/prgadsiat_parametros_facturacion/updateServInternet/ Update";
    return this.api.update('/seg_adm/mant/prgadsiat_parametros_facturacion/updateServInternet/'+this.userConn+"/"+valor, data)
      .subscribe({
        next: (datav) => {
          this.servicio_internet = datav;
          this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
          this.toastr.success('! SE EDITO EXITOSAMENTE !');
          location.reload();
        },
    
        error: (err: any) => { 
          this.toastr.error('! NO SE EDITO !');
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  actualizarServicioSIN(valor){
    let data=[];
    let errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:- /seg_adm/mant/prgadsiat_parametros_facturacion/updateServSIN/ Update";
    return this.api.update('/seg_adm/mant/prgadsiat_parametros_facturacion/updateServSIN/'+this.userConn+"/"+valor, data)
      .subscribe({
        next: (datav) => {
          this.servicio_internet = datav;
          this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
          this.toastr.success('! SE ACTUALIZO EXITOSAMENTE !');
          location.reload();
        },
    
        error: (err: any) => { 
          this.toastr.error('! NO SE EDITO !');
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  changeInternetActivo(value){
    console.log(value);
    this.actualizarServicioInternet(value);
  }

  changeSINActivo(value){
    console.log(value);
    this.actualizarServicioSIN(value);
  }

  modalAlmacen(): void {
    this.dialog.open(ModalAlmacenComponent, {
      width: 'auto',
      height: 'auto',
    });
  }

  mandarNombre(){
    this.nombre_ventana_service.disparadorDeNombreVentana.emit({
      nombre_vent:this.ventana,
    });
  }
}
<div class="p-2">
  <mat-card>
    <mat-card-header class="mb-1">
      <mat-card-title style="font-size: 0.8rem;">
        <strong><u>Parametros de Facturación</u></strong>
      </mat-card-title>
    </mat-card-header>

    <mat-card-content>
      <div class="row">
        <table class="tg" style="width: 100%;">
          <thead>
            <tr>
              <td class="tg-0lax">
                <mat-label style="padding-right: 47px;padding-top: 5px;"><u>Almacen</u></mat-label>
                <input type="text" [(ngModel)]="para_facturacion.codalmacen" style="width: 25%;" readonly>
              </td>

              <td class="tg-0lax" style="float: right;width: 55%;">
                <mat-label style="padding-right: 2px;"><u>Sucursal</u></mat-label>
                <select [(ngModel)]="para_facturacion.codsucursal" disabled="true">
                  <option *ngFor="let sucursals of sucursal" [value]="sucursals.codsucursal">
                    {{sucursals.codsucursal}}
                  </option>
                </select>

                <mat-label style="padding-right: 3px;"><u>Pto. de Venta</u></mat-label>
                <input type="text" [(ngModel)]="para_facturacion.punto_vta" style="width: 11%;" [readonly]="true">


                <mat-label style="padding-right: 2px;"><u>Cod. Sistema</u></mat-label>
                <input type="text" matInput [(ngModel)]="para_facturacion.codsistema"
                  style="width: 42%; background-color: black;color: #fbd800;" [readonly]="true">
              </td>
            </tr>
          </thead>
        </table>
      </div>

      <div class="row">
        <table class="tg" style="width: 100%;">
          <thead>
            <tr>
              <td class="tg-0lax" style="width: 19% !important;">
                <mat-label style="padding-right: 45px;"><u>Empresa</u></mat-label>
                <input type="text" matInput [(ngModel)]="para_facturacion.codempresa" [readonly]="true"
                  style="width: 50%;">
              </td>

              <td class="tg-0lax">
                <input type="text" matInput style="width: 100%;" [(ngModel)]="empresa.nombre" disabled>
              </td>
            </tr>
          </thead>
        </table>
      </div>

      <div class="row">
        <table class="tg" style="width: 100%;">
          <thead>
            <tr>
              <td class="tg-0lax">
                <mat-label style="padding-right: 14px;"><u>Ambiente Fact.</u></mat-label>
                <select style="width: 49.5%;" id="cars" name="cars" [(ngModel)]="para_facturacion.ambiente"
                  disabled="true">
                  <option [value]="1">PRODUCCION</option>
                  <option [value]="0">PRUEBAS</option>
                </select>
              </td>
              <td class="tg-0lax" style="width: 50%;"></td>
              <td class="tg-0lax">
                <mat-label style="padding-right: 5px;"><u>Modalidad de Facturación</u></mat-label>
                <select style="float: inline-end" [(ngModel)]="para_facturacion.modalidad" disabled="true">
                  <option [value]="1">ELECTRONICA EN LINEA</option>
                  <option [value]="0">COMPUTARIZADA EN LINEA</option>
                </select>
              </td>
            </tr>
          </thead>
        </table>
      </div>

      <div class="row">
        <table class="tg" style="width: 100%;">
          <thead>
            <tr>
              <td class="tg-0lax">
                <mat-label style="padding-right: 25px;"><u>Tipo Emisión</u></mat-label>
                <select style="width: 50%;" [(ngModel)]="para_facturacion.tipo_emision" disabled="true">
                  <option *ngFor="let tipo_emisions of tipo_emision" [value]="tipo_emisions.codigoclasificador">
                    {{ tipo_emisions.descripcion }}</option>
                </select>
              </td>
              <td class="tg-0lax" style="width: 50%;"></td>
              <td class="tg-0lax">
                <mat-label style="padding-right: 5px;"><u>Tipo Factura</u></mat-label>
                <select style="width: 75.5%;float: inline-end" [(ngModel)]="para_facturacion.tipo_factura"
                  disabled="true">
                  <option *ngFor="let facturas of factura" [value]="facturas.codigoclasificador">
                    {{ facturas.descripcion }}</option>
                </select>
              </td>
            </tr>
          </thead>
        </table>
      </div>

      <div class="row">
        <table class="tg" style="width: 100%;">
          <thead>
            <tr>
              <td class="tg-0lax">
                <mat-label style="padding-right: 5px;"><u>Tipo Doc. Sector</u></mat-label>
                <select [(ngModel)]="para_facturacion.tipo_doc_sector" disabled="true">
                  <option *ngFor="let sectors of sector" [value]="sectors.codigoclasificador">{{ sectors.descripcion }}
                  </option>
                </select>
              </td>
            </tr>
          </thead>
        </table>
      </div>

      <div class="row">
        <table class="tg" style="width: 100%;">
          <thead>
            <tr>
              <td class="tg-0lax">
                <mat-label style="padding-right: 44px;"><u>Actividad</u></mat-label>
                <select style="width: 89%;" [(ngModel)]="servicio_actividad" disabled="true">
                  <option *ngFor="let actividads of actividad" [value]="actividads.codigocaeb">
                    {{ actividads.codigocaeb }} - {{ actividads.descripcion }}</option>
                </select>
              </td>
              <td class="tg-0lax">
                <select style="width: 99.5%;" [(ngModel)]="servicio_actividad" disabled="true">
                  <option *ngFor="let actividads of actividad" [value]="actividads.codigocaeb">
                    {{ actividads.codigocaeb }}
                  </option>
                </select>
              </td>
            </tr>
          </thead>
        </table>
      </div>


      <div class="row">
        <table class="tg" style="margin-left: 98px;">
          <thead>
            <tr>
              <td class="tg-0lax">
                <input type="checkbox" [(ngModel)]="para_facturacion.servicio_internet_activo"
                  [checked]="para_facturacion.servicio_internet_activo" (ngModelChange)="changeInternetActivo($event)">
                <mat-label style="padding-right: 48px;"><u>&nbsp; Servicio de Internet Activo (En Linea)</u>
                </mat-label>
              </td>
            </tr>
          </thead>
        </table>
      </div>


      <div class="row">
        <table class="tg" style="margin-left: 98px;">
          <thead>
            <tr>
              <td class="tg-0lax">
                <input type="checkbox" [(ngModel)]="para_facturacion.servicio_sin_activo"
                  [checked]="para_facturacion.servicio_sin_activo" (ngModelChange)="changeSINActivo($event)">
                <mat-label style="padding-right: 48px;"><u>&nbsp; Servicio S.I.N Activo (En Linea SIN)</u></mat-label>
              </td>
            </tr>
          </thead>
        </table>
      </div>
    </mat-card-content>

    <!-- <mat-card-actions align="end">
      <button mat-button class="success" (click)="actualizarServicioInternet();">
        <i class="fa fa-save"></i>&nbsp;Actualizar Servicio de Internet Activo
      </button>

      <button mat-button class="success" (click)="actualizarServicioSIN();">
        <i class="fa fa-save"></i>&nbsp;Actualizar Servicio S.I.N Activo
      </button>
    </mat-card-actions> -->
  </mat-card>
</div>
