import { DatePipe } from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ModalVendedorComponent } from '@components/mantenimiento/ventas/modal-vendedor/modal-vendedor.component';
import { VendedorService } from '@components/mantenimiento/ventas/serviciovendedor/vendedor.service';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-numeracion-cobranza-edit',
  templateUrl: './numeracion-cobranza-edit.component.html',
  styleUrls: ['./numeracion-cobranza-edit.component.scss']
})
export class NumeracionCobranzaEditComponent implements OnInit {

  FormularioDataEdit:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  numCobran_edit:any=[];
  dataform:any='';
  unidadNegocio:[];
  numCobran:any=[];
  usuario_logueado:any;
  user_conn:any;
  errorMessage;
  inputValue: number | null = null;

  vendedor_get: any = [];
  cod_vendedor:any;

  public ventana="numCobranza"
  public detalle="numCobranza-edit";
  public tipo="numCobranza-edit-PUT";

  constructor(private _formBuilder: FormBuilder, public dialog: MatDialog, public log_module:LogService, public dialogRef: MatDialogRef<NumeracionCobranzaEditComponent>, 
    @Inject(MAT_DIALOG_DATA) public datanumCobranEdit: any, private api:ApiService, private datePipe: DatePipe,private toastr: ToastrService,
    public _snackBar: MatSnackBar, public serviciovendedor: VendedorService){
    this.FormularioDataEdit = this.createForm();
  }

  ngOnInit(){
    this.usuario_logueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
    this.user_conn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    this.numCobran_edit = this.datanumCobranEdit.datanumCobranEdit;
    this.getAllUnidadesNegocio();

    this.serviciovendedor.disparadorDeVendedores.subscribe(data => {
      console.log("Recibiendo Vendedor: ", data);
      this.numCobran_edit.codvendedor = data.vendedor;
    });
  }



  ngAfterViewInit(){
    this.getVendedorCatalogo();
  }

  
  getVendedorCatalogo() {
    let errorMessage: string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --/seg_adm/mant/vevendedor/catalogo/";
    return this.api.getAll('/seg_adm/mant/vevendedor/catalogo/' + this.user_conn)
      .subscribe({
        next: (datav) => {
          this.vendedor_get = datav;
        },
                
        error: (err: any) => {
          console.log(err, errorMessage);
        },
        complete: () => {}
      })
  }
  
  modalVendedor(): void {
    this.dialog.open(ModalVendedorComponent, {
      width: 'auto',
      height: 'auto',
    });
  }

  onLeaveVendedor(event: any) {
    const inputValue = event.target.value;
    let entero = Number(inputValue);

    // Verificar si el valor ingresado está presente en los objetos del array
    const encontrado = this.vendedor_get.some(objeto => objeto.codigo === entero);

    if (!encontrado) {
      // Si el valor no está en el array, dejar el campo vacío
      event.target.value = '';
      console.log("NO ENCONTRADO VALOR DE INPUT");
    } else {
      event.target.value = entero;
    }
  }


  getAllUnidadesNegocio(){
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/seg_adm/mant/adunidad/catalogo/'+this.user_conn)
      .subscribe({
        next: (datav) => {
          this.unidadNegocio = datav;
        },
    
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  createForm(): FormGroup {
    const usuario_logueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;

    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      id: [this.datanumCobranEdit.datanumCobranEdit.id],
      descripcion: [this.dataform.descripcion,Validators.compose([Validators.required])],
      nroactual: [this.dataform.nroactual,Validators.pattern(/^-?\d+$/)],
      codvendedor: [this.dataform.codvendedor,Validators.compose([Validators.required])],
      horareg: [hora_actual_complete],
      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      usuarioreg: [usuario_logueado],
      codunidad: [this.dataform.codunidad, Validators.compose([Validators.required])],
    });
  } 

  submitData(){
    let data = this.FormularioDataEdit.value;

    this.errorMessage = "La Ruta presenta fallos al hacer la creacion"+"Ruta:--  /ctasXcobrar/mant/cotipo/ Update";
    return this.api.update('/ctasXcobrar/mant/cotipo/'+this.user_conn+"/"+this.numCobran_edit.id, data)
      .subscribe({
        next: (datav) => {
          this.numCobran = datav;
          this.onNoClick();
          this.log_module.guardarLog(this.ventana, this.detalle, this.tipo);
          this.toastr.success('! SE EDITO EXITOSAMENTE !');
          location.reload();
        },
    
        error: (err: any) => { 
          this.toastr.error('! NO SE EDITO !');
          console.log(err, this.errorMessage);
        },
        complete: () => { }
      })
  }

  onInputChange(value: string){
    const parsedValue = parseFloat(value);

    if (!isNaN(parsedValue) && Number.isInteger(parsedValue)) {
      this.inputValue = parsedValue;
    }else{
      this.numCobran_edit.nroactual = null;
    }
  }

  onNoClick(): void {
    this.dialogRef.close();
  }


}
<div class="p-2">
  <h1 mat-dialog-title>Editar Numeración de Cobranza {{ numCobran_edit.id }}</h1>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div><br>
</div>

<div mat-dialog-content>
  <div class="container">
    <form class="example-form" [formGroup]="FormularioDataEdit" (ngSubmit)="submitData()">
      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Descripción </mat-label>
            <input type="text" matInput formControlName="descripcion" maxlength="30"
              [(ngModel)]="numCobran_edit.descripcion" required>
          </mat-form-field>
        </div>
      </div>
      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Número Actual </mat-label>
            <input type="number" min="0" step="1" (input)="onInputChange($event.target.value)" matInput
              formControlName="nroactual" [(ngModel)]="numCobran_edit.nroactual" required>
          </mat-form-field>
        </div>
      </div>

      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field appearance="outline">
            <mat-label>Unidad de Negocio</mat-label>
            <mat-select formControlName="codunidad" [(ngModel)]="numCobran_edit.codunidad">
              <mat-option *ngFor="let unidad of unidadNegocio" [value]="unidad.codigo">
                {{ unidad.codigo }} - {{ unidad.descripcion }}
              </mat-option>
            </mat-select>
          </mat-form-field>
        </div>
      </div>

      <div class="row mb-2">
        <div class="col-md">
          <div class="input-group">
            <ng-container>
              <input style="background-color: transparent; color: black; border: 1px solid #959595;"
               type="text" class="form-control form-control-sm" formControlName="codvendedor"
              placeholder="Vendedor" [(ngModel)]="numCobran_edit.codvendedor" (blur)="onLeaveVendedor($event)">
            </ng-container>
        
            <div class="input-group-append">
              <button class="btn btn-outline-primary form-control-sm" type="button" (click)="modalVendedor();"
                style="border-color: #093070;"><i class="fa fa-search"></i>
              </button>
            </div>
          </div>
        </div>
      </div>

      <div class="mat-dialog-actions" mat-dialog-actions>
        <a mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</a>

        <button mat-raised-button color="primary" [disabled]="!FormularioDataEdit.valid" type="submit"><i
            class="fa fa-save"></i> Guardar</button>
      </div>
    </form>
  </div>
</div>
