import { DatePipe } from '@angular/common';
import { Component, Inject, OnInit } from '@angular/core';
import { FormBuilder, FormGroup, Validators } from '@angular/forms';
import { MAT_DIALOG_DATA, MatDialogRef } from '@angular/material/dialog';
import { MatSnackBar } from '@angular/material/snack-bar';
import { ApiService } from '@services/api.service';
import { LogService } from '@services/log-service.service';
import { NgxSpinnerService } from 'ngx-spinner';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-item-edit',
  templateUrl: './item-edit.component.html',
  styleUrls: ['./item-edit.component.scss']
})
export class ItemEditComponent implements OnInit {

  FormularioData:FormGroup;
  fecha_actual = new Date();
  hora_actual = new Date();
  dataform:any='';
  rosca:any=[];
  inudemed:any=[];
  interminacion:any=[];
  inresistencia:any=[];
  item_edit:any=[];
  inlinea:any=[];
  initem_create:any=[];
  userConn:any;
  usuarioLogueado:any;
  conjunto:any;

  public ventana="initem-edit"
  public detalle="initem-edit";
  public tipo="transaccion-initem-EDIT";

  constructor(private _formBuilder: FormBuilder,private datePipe: DatePipe,private toastr: ToastrService,
    private api:ApiService, public dialogRef: MatDialogRef<ItemEditComponent>, @Inject(MAT_DIALOG_DATA) public dataItem: any,
    public _snackBar: MatSnackBar, public log_module:LogService,){
      
      this.usuarioLogueado = localStorage.getItem("usuario_logueado") !== undefined ? JSON.parse(localStorage.getItem("usuario_logueado")) : null;
      this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
      this.FormularioData = this.createForm();
  }

  ngOnInit(): void {
    this.getAllRosca();
    this.getAllinudemed();
    this.getAllinTerminacion();
    this.getAllinReistencia();
    this.getAllinLinea();

    this.item_edit = this.dataItem.dataItem;
    this.conjunto = this.item_edit.kit;
  }

  createForm(): FormGroup {
    let hour = this.hora_actual.getHours();
    let minuts = this.hora_actual.getMinutes();
    let hora_actual_complete = hour + ":" + minuts;  

    return this._formBuilder.group({
      codigo: [this.dataform.codigo, Validators.compose([Validators.required])],
      descripcion: [this.dataform.descripcion, Validators.compose([Validators.required])],
      descripcorta: [this.dataform.descripcorta, Validators.compose([Validators.required])],
      descripabr: [this.dataform.descripabr, Validators.compose([Validators.required])],

      medida: [this.dataform.medida],
      unidad: [this.dataform.unidad],
      rosca: [this.dataform.rosca],
      terminacion: [this.dataform.terminacion],
      codresistencia: [this.dataform.codresistencia],
      peso: [this.dataform.peso],
      kit: [this.dataform.kit],
      clasificacion: [this.dataform.clasificacion],
      codlinea: [this.dataform.codlinea],
      estadocv: [this.dataform.estadocv],
      enlinea: [this.dataform.enlinea],
      reservastock: [this.dataform.reservastock],
      usar_en_movimiento: [this.dataform.usar_en_movimiento],
      controla_negativo: [this.dataform.controla_negativo],
      codigobarra: [this.dataform.codigobarra],
      paga_comision: [this.dataform.paga_comision],

      costo: [0],
      monedacosto: [""],
      saldominimo: [0],
      iva: [0],
      codmoneda_valor_criterio: [""],
      porcen_gac: [0],
      nandina: [""],
      porcen_saldo_restringido: [0],
      codproducto_sin: [""],

      fechareg: [this.datePipe.transform(this.fecha_actual,"yyyy-MM-dd")],
      horareg: [hora_actual_complete],
      usuarioreg: [this.usuarioLogueado],
    });
  }

  submitData(){
    let data = this.FormularioData.value;
    console.log(data);
    
    let errorMessage = "La Ruta o el servidor presenta fallos al hacer la creacion"+"Ruta:-  /inventario/mant/inlinea  POST";
    return this.api.create("/inventario/mant/initem/"+this.userConn+"/"+this.item_edit.codigo, data)
      .subscribe({
        next: (datav) => {
          this.initem_create = datav;
          this.log_module.guardarLog(this.ventana,this.detalle, this.tipo);

          this.toastr.success('! SE GUARDO EXITOSAMENTE !');
          location.reload();
        },

        error: (err) => { 
          console.log(err, errorMessage);
          this.toastr.error('! NO SE GUARDO !');
        },
        complete: () => { }
      })
  }

  getAllinudemed(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --inventario/mant/inudemed";
    return this.api.getAll('/inventario/mant/inudemed/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.inudemed = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllRosca(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --/inventario/mant/inrosca";
    return this.api.getAll('/inventario/mant/inrosca/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.rosca = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllinTerminacion(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --inventario/mant/interminacion";
    return this.api.getAll('/inventario/mant/interminacion/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.interminacion = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllinReistencia(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --inventario/mant/inresistencia";
    return this.api.getAll('/inventario/mant/inresistencia/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.inresistencia = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllinLinea(){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET --inventario/mant/inresistencia";
    return this.api.getAll('/inventario/mant/inlinea/'+this.userConn)
      .subscribe({
        next: (datav) => {
          this.inlinea = datav;
        },
                
        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  onNoClick(): void {
    this.dialogRef.close();
  }
}
<div>
  <h1 mat-dialog-title>Crear Item</h1>
  <div style="font-size: 0.6rem;padding: 0 24px 0px;">Los campos (*) son obligatorios</div>
</div>

<div mat-dialog-content>
  <mat-tab-group preserveContent>
    <mat-tab label="Datos"><br>
      <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">

      <div class="row">
        <div class="col">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Codigo</mat-label>
            <input matInput type="text" [(ngModel)]="item_edit.codigo" formControlName="codigo" maxlength="12" required>
          </mat-form-field>
        </div>
      </div>

      <div class="row mb-2">
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Caracteristica Primaria </mat-label>
            <input matInput type="text" [(ngModel)]="item_edit.descripcion" formControlName="descripcion" maxlength="50" required>
          </mat-form-field>
        </div>
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Descripción Corta</mat-label>
            <input type="text" matInput [(ngModel)]="item_edit.descripcorta" formControlName="descripcorta" maxlength="50" required>
          </mat-form-field>
        </div>
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Descripción Abreviada</mat-label>
            <input type="text" matInput [(ngModel)]="item_edit.descripabr" formControlName="descripabr" maxlength="50" required>
          </mat-form-field>
        </div>
        <div class="col-md">
          <mat-form-field class="example-full-width" appearance="outline">
            <mat-label>Caracteristica Secundaria</mat-label>
            <input type="text" matInput [(ngModel)]="item_edit.medida" formControlName="medida" maxlength="50">
          </mat-form-field>
        </div>
      </div>

      <div class="row">
        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Unidad Base</mat-label>
            <mat-select formControlName="unidad">
              <mat-option *ngFor="let inudemeds of inudemed" [value]="inudemeds.Codigo">
              {{ inudemeds.Codigo }} - {{ inudemeds.Descripcion }}
              </mat-option>
            </mat-select>
          </mat-form-field>
        </div>
        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Rosca</mat-label>
            <mat-select formControlName="rosca">
              <mat-option *ngFor="let roscas of rosca" [value]="roscas.codigo">
              {{ roscas.codigo }} - {{ roscas.descripcion }}
              </mat-option>
            </mat-select>
          </mat-form-field>
        </div>
        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Terminación</mat-label>
            <mat-select formControlName="terminacion">
              <mat-option *ngFor="let interminacions of interminacion" [value]="interminacions.codigo">
              {{ interminacions.codigo }} - {{ interminacions.descripcion }}
              </mat-option>
            </mat-select>
          </mat-form-field>
        </div>
        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Resistencia</mat-label>
            <mat-select  formControlName="codresistencia" >
              <mat-option *ngFor="let inresistencias of inresistencia" [value]="inresistencias.codigo">
              {{ inresistencias.codigo }} - {{ inresistencias.descripcion }}
              </mat-option>
            </mat-select>
          </mat-form-field> 
        </div>
      </div>

      <div class="row mb-2">
        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Peso Kg.</mat-label>
              <input type="number" matInput [(ngModel)]="item_edit.peso" formControlName="peso">
          </mat-form-field>
        </div>

        <div class="col">
          <section class="example-section">
            <mat-checkbox class="example-margin" formControlName="kit" value="true" [ngModel]="conjunto">
              Conjuntodd</mat-checkbox>
          </section>
        </div>

        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Clasificación ABC</mat-label>
            <mat-select formControlName="clasificacion">
              <mat-option value="A">A</mat-option>
              <mat-option value="B">B</mat-option>
              <mat-option value="C">C</mat-option>
            </mat-select>
          </mat-form-field>
        </div>

        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Linea</mat-label>
            <mat-select formControlName="codlinea">
              <mat-option *ngFor="let inlineas of inlinea" [value]="inlineas.codigo">
              {{ inlineas.codigo }} - {{ inlineas.descripcion }}
              </mat-option>
            </mat-select>
          </mat-form-field>            
        </div>
      </div>

      <div class="row mb-2">
        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Estado</mat-label>
            <mat-select formControlName="estadocv">
              <mat-option value="1">Venta y Compra</mat-option>
              <mat-option value="2">Solo Venta</mat-option>
              <mat-option value="3">Solo Compra</mat-option>
              <mat-option value="4">Inactivo</mat-option>
            </mat-select>
          </mat-form-field>
        </div>
      </div>

      <hr><br>

      <div class="row">
        <div class="col">
          <section class="example-section" style="text-align: center !important;">
            <ng-container>
              <section class="example-section">
                <mat-checkbox class="example-margin" formControlName="enlinea" value="false" [ngModel]="item_edit.enlinea">
                  El Item Se Encuentra en Linea</mat-checkbox>
              </section>
            </ng-container>
          </section>
        </div>

        <div class="col">
          <section class="example-section" style="text-align: center !important;">
            <ng-container>
              <section class="example-section">
                <mat-checkbox class="example-margin" formControlName="reservastock" value="true" 
                  [ngModel]="item_edit.reservastock" checked>
                  El Item Reserva Stocks Para Tiendas
                </mat-checkbox>
              </section>
            </ng-container>
            
          </section>
        </div>

        <div class="col">
          <section class="example-section" style="text-align: center !important;">
            <ng-container>
              <section class="example-section">
                <mat-checkbox class="example-margin" formControlName="usar_en_movimiento" 
                  [ngModel]="item_edit.usar_en_movimiento"  value="true" checked>
                  Usar en Notas de Movimiento
                </mat-checkbox>
              </section>
            </ng-container>
          </section>
        </div>
      </div>

      <div class="row">
        <div class="col">
          <section class="example-section" style="text-align: center !important;">
            <ng-container>
              <section class="example-section">
                <mat-checkbox class="example-margin" formControlName="controla_negativo" [ngModel]="item_edit.controla_negativo" 
                value="true" checked>
                  Controlar Saldos Negativos</mat-checkbox>
              </section>
            </ng-container>
          </section>
        </div>
        <div class="col">
          <ng-container>
            <section class="example-section">
              <mat-checkbox class="example-margin" formControlName="paga_comision" [ngModel]="item_edit.paga_comision" 
              value="true" checked>
                Paga Comisión</mat-checkbox>
            </section>
          </ng-container>
        </div>
      </div><br>

      <div class="row">
        <div class="col">
          <mat-form-field appearance="outline">
            <mat-label>Código de Barras</mat-label>
              <input matInput type="text" [(ngModel)]="item_edit.codigobarra" formControlName="codigobarra">
          </mat-form-field>
        </div>
      </div>
    
      <button mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</button>
      <div class="mat-dialog-actions" mat-dialog-actions>
        <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit">
          <i class="fa fa-save"></i> Guardar</button>
      </div>
    </form>  
    </mat-tab>
  </mat-tab-group>
</div>
