<div class="p-2">
  <div class="overlay"></div>
  <div class="p-4 mb-3 mat-elevation-z8">
    <div class="row">
     
      <div class="col-md">
        <div class="row">
          <div style="text-align: center;">
            <h5>Saldos a Cubrir</h5>
          </div><hr>
          
          <p><strong>Código:</strong>&nbsp; {{ dataItem.dataItem.codigo }} <strong>&nbsp; 
            Descripción:</strong>&nbsp; {{ dataItem.dataItem.descripcion }} </p>
        </div>

        <form class="example-form" [formGroup]="FormularioData" (ngSubmit)="submitData()">
          <div class="col">
            <mat-form-field appearance="outline">
              <mat-label>Item</mat-label>
              <mat-select formControlName="coditemcontrol">
                <mat-option *ngFor="let items of item" [value]="items.codigo">
                {{ items.codigo }} - {{ items.descripcion }}
                </mat-option>
              </mat-select>
            </mat-form-field>
          </div>
          <div class="col">
            <mat-form-field class="example-full-width" appearance="outline">
              <mat-label>Porcentaje %</mat-label>
              <input matInput type="text" formControlName="porcentaje" maxlength="5" required>
            </mat-form-field>
          </div>

          <div>
            <button mat-button (click)="onNoClick()"><i class="fa fa-arrow-left"></i> Atras</button>
            <div class="mat-dialog-actions" mat-dialog-actions>
              <button mat-raised-button color="primary" [disabled]="!FormularioData.valid" type="submit">
                <i class="fa fa-save"></i> Guardar</button>
            </div>
          </div>
        </form>

        <div class="col">
          <div class="team p-2 bg-white scrollmenu">
            <div class="col-lg-12 pr-lg-4" style="width: 100%;overflow-x: auto;">
              <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>
    
                <ng-container matColumnDef="item">
                  <th mat-header-cell *matHeaderCellDef> Item </th>
                  <td mat-cell *matCellDef="let element"> {{ element.coditemcontrol }} </td>
                </ng-container>
    
                <ng-container matColumnDef="descripcion">
                  <th mat-header-cell *matHeaderCellDef> Descripción</th>
                  <td mat-cell *matCellDef="let element"> {{ element.descripcion }} </td>
                </ng-container>
    
                <ng-container matColumnDef="medida">
                  <th mat-header-cell *matHeaderCellDef> Medida</th>
                  <td mat-cell *matCellDef="let element"> {{ element.medida }} </td>
                </ng-container>

                <ng-container matColumnDef="porcentaje">
                  <th mat-header-cell *matHeaderCellDef> Porcentaje</th>
                  <td mat-cell *matCellDef="let element"> {{ element.porcentaje }} </td>
                </ng-container>

                <ng-container matColumnDef="accion">
                  <th mat-header-cell *matHeaderCellDef>Acción</th>
                  <td mat-cell *matCellDef="let element">
             
                      <button class="btn btn-danger" mat-menu-item (click)="eliminar(element);">
                        <span><i class="fa fa-trash"></i> </span>
                      </button>
                  </td>
                </ng-container>
    
                <tr mat-header-row *matHeaderRowDef="displayedColumns"></tr>
                <tr mat-row *matRowDef="let row; columns: displayedColumns;"></tr>
              </table><br>
    
              <mat-paginator #paginator [pageSizeOptions]="[5, 10, 15]" showFirstLastButtons></mat-paginator>
            </div>
          </div>
        </div>
      </div>
    </div>
  </div>
</div>
