import { Component, ElementRef, HostListener, OnInit, ViewChild } from '@angular/core';
import { FormGroup } from '@angular/forms';
import { MatDialog, MatDialogRef } from '@angular/material/dialog';
import { MatPaginator } from '@angular/material/paginator';
import { MatTableDataSource } from '@angular/material/table';
import { ItemServiceService } from '@components/mantenimiento/ventas/serviciosItem/item-service.service';
import { ApiService } from '@services/api.service';
import { ToastrService } from 'ngx-toastr';

@Component({
  selector: 'app-matriz-inventario',
  templateUrl: './matriz-inventario.component.html',
  styleUrls: ['./matriz-inventario.component.scss']
})
export class MatrizInventarioComponent implements OnInit {

  @HostListener("document:keyup.RIGHT_ARROW", []) unloadHandler0(event: Event){
    console.log("No se puede actualizar"); 

  }

  id_tipo:any=[];
  hojas:any=[];
  item_obtenido:any=[];

  FormularioBusqueda:FormGroup
  dataform:any='';
  num_hoja:number=0;
  cantidad:number;
  value:any;
  item_valido:boolean;
  BD_storage:any;
  userConn:any;

  activeCell: { row: number, column: string } = { row: -1, column: '' };

  private codigo_item_catalogo:string;
  private cantidad_item_matriz:number;

  displayedColumns = ['a','b','c','d','e','f','g','h','i','j','k','l','m'];

  dataSource = new MatTableDataSource();
  dataSourceWithPageSize = new MatTableDataSource();

  @ViewChild("dialogD") dialogD: ElementRef;
  @ViewChild('paginator') paginator: MatPaginator;
  @ViewChild('paginatorPageSize') paginatorPageSize: MatPaginator;

  constructor(private api:ApiService, public dialog: MatDialog,public dialogRef: MatDialogRef<MatrizInventarioComponent>, 
    private servicioItem: ItemServiceService, private toastr: ToastrService) {

      this.BD_storage = localStorage.getItem("bd_logueado") !== undefined ? JSON.parse(localStorage.getItem("bd_logueado")) : null;
      this.userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;
  }

  ngOnInit(){
    let userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    this.getHojaPorDefecto(userConn);
  }

  getlineaProductoID(value){
    //se le quita el espacio en blanco que tiene
    const cleanText = value.replace(/\s+/g,"").trim();
    console.log('item seleccionado:', cleanText);

    let userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    let errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/inventario/mant/initem/'+userConn+"/"+cleanText)
      .subscribe({
        next: (datav) => {
          this.item_obtenido = datav;
          // console.log('item seleccionado data: ', this.item_obtenido);
        },

        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getAllHoja(hoja){
    let userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/inventario/mant/inmatriz/'+userConn+"/"+hoja)
      .subscribe({
        next: (datav) => {
          this.hojas = datav;
          console.log(this.hojas);

          this.dataSource = new MatTableDataSource(this.hojas);

          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },

        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  getHojaPorDefecto(userConn){
    let errorMessage:string;
    errorMessage = "La Ruta o el servidor presenta fallos al hacer peticion GET";
    return this.api.getAll('/inventario/mant/inmatriz/'+userConn+"/01")
      .subscribe({
        next: (datav) => {
          this.hojas = datav;
          console.log(this.hojas);

          this.dataSource = new MatTableDataSource(this.hojas);

          this.dataSource.paginator = this.paginator;
          this.dataSourceWithPageSize.paginator = this.paginatorPageSize;
        },

        error: (err: any) => { 
          console.log(err, errorMessage);
        },
        complete: () => { }
      })
  }

  buscadorHoja(){
    let userConn = localStorage.getItem("user_conn") !== undefined ? JSON.parse(localStorage.getItem("user_conn")) : null;

    console.log(this.num_hoja);
    if(this.num_hoja == 0){
      this.getHojaPorDefecto(userConn);
    }else{
      this.getAllHoja(this.num_hoja);
    }
  }

  isActiveCell(row: number, column: string): boolean {
    return this.activeCell.row === row && this.activeCell.column === column;
  }

  getinMatrizByID(value){
    const cleanText = value.replace(/\s+/g, "").trim();
    this.value = cleanText.toUpperCase();

    console.log(this.value);

    this.getlineaProductoID(this.value);

  }

  mandarItem(value){
    console.log("Holass");

    const cleanText = value.replace(/\s+/g, " ").trim();
    console.log( cleanText );

    this.servicioItem.disparadorDeItems.emit({
      item:cleanText,
      cantidad:this.cantidad,

    });
  }

  close(){
    this.dialogRef.close();
  }

}
<section class="content">
  <mat-card style="background-color: #ffe0c0;">
    <mat-card-header class="mb-1">
      <div class="row" style="width: 100% !important;">
        <div class="col-md-3">
          <button mat-raised-button color="primary"><i class="fa fa-check"></i> OK </button>&nbsp;
          <button mat-raised-button class="danger" (click)="close()"><i class="fa fa-times"></i> Cancelar </button>
        </div>
        <div class="col-md-7">

          <input type="text" class="input_dark" matInput [(ngModel)]="item_obtenido.codigo" disabled>
          <input type="text" class="input_dark" matInput [(ngModel)]="item_obtenido.descLinea" disabled>
          <input type="text" class="input_dark" matInput [(ngModel)]="item_obtenido.medida" disabled>

        </div>

        <div class="col-md-2">
          <div style="float: right;">
            <mat-label style="padding-right: 5px;padding-top: 7px;"><u>Cantidad</u></mat-label>
            <input type="text" style="width: 50% !important;" required>
          </div>
        </div>
      </div>
    </mat-card-header>

    <div class="row p-3">

    </div>


    <!-- <div class="row">
      <div class="col-md-9 float-left">
        <span style="color: black;background-color: #80808030;margin-left: 9px;">
          <strong>Item Seleccionado:</strong> {{ value }}
          |<strong>Descripcion:</strong> {{ item_obtenido.descripcion }}
          |<strong> Medida:</strong> {{ item_obtenido.medida }}
          |<strong> Reserva:</strong> {{ item_obtenido.porcen_maximo }}
        </span>
      </div>

      <div class="col-md-3">
        <ng-container *ngIf="cantidad > 0">
          <button mat-raised-button color="primary" (click)="mandarItem(value);" style="width: 100% !important;">
            <i class="fa fa-cart-plus"></i> Confirmar
          </button>
        </ng-container>
      </div>
    </div> -->





    <div class="row">
      <div class="col">
        <mat-dialog-content class="dialog-matriz">
          <div class="col-lg-12" style="width: 100%;overflow-x: auto;">
            <table mat-table [dataSource]="dataSource" multiTemplateDataRows matSort class="mat-elevation-z8" matSort>

              <ng-container matColumnDef="a">
                <th mat-header-cell *matHeaderCellDef></th>
                <td mat-cell *matCellDef="let element" style="text-align: center">
                  {{ element.a }}
                </td>
              </ng-container>

              <ng-container matColumnDef="b">
